//particle buffer

int cid;

ivec4 get(int id)
{
    return floatBitsToInt(texel(ch0, i2xy(id)));
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float F(float d)
{
    return 0.8/(1.+0.4*d) - 4.*exp(-0.5*d);
}

vec2 Fv(vec4 p0, int pid)
{
    if(pid < 0 || pid >= tot_n || pid == cid) return vec2(0); 
   	vec4 p1 = getParticle(pid);
    vec2 flocking_force = 0.3*(p1.zw - p0.zw)/(1.+1.*distance(p1.xy,p0.xy));
    vec2 interaction = normalize(p1.xy-p0.xy)*F(distance(p1.xy,p0.xy));
    return flocking_force + interaction;
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);
        int id = xy2i(p);
        cid = id;

        //this pixel value
        if(iFrame<10)
        {
            U.xy = size*hash22(pos);
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
      		return;
        }

        //neighbors
   		ivec4 cp = get(id);

        vec2 F = Fv(U, cp.x) +
            	 Fv(U, cp.y) +
            	 Fv(U, cp.z) +
                 Fv(U, cp.w);

        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 2.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw += (0.5*F)*dt;
        U.zw = normalize(U.zw);
        U.xy += U.zw*dt;

        //border conditions
        /*if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);*/

        U.xy = mod(U.xy, size);


    }
    else discard;
}

