  	//Waving screen
  	uv.x += sin(uv.y*8.5)*0.5;
  	uv.y += sin(uv.x*8.5+iTime)*0.5;
  	//Screen split
  	uv.x = mod(uv.x, 0.2)-0.2*0.5;

  	//Shape
  	float verLine = smoothstep(0.05, 0.2,length(uv.y+circle));

