    	vec3 n = get_normal(p);
    	vec3 l = vec3(0.5,2.,-2.);
    	color = get_normal(p) + vec3(shading);
    	color = mix(vec3(0.8,0.05,0.2), vec3(0.1,0.0,0.0), vec3(diffuse_directional(n,l)));
    	hit = false;
  	}else if(hit2){
    	vec3 n = get_normal(p);
    	vec3 l = vec3(0.5,2.,-4.);
    	color = get_normal(p) + vec3(shading);
    	color = mix(vec3(0.65,0.65,0.7), vec3(0.1,0.0,0.0), mix(vec3(0.65,0.45,0.7), vec3(circle-verLine),
    	vec3(diffuse_directional(n,l))));
    	hit2 = false;
  	}else{
    	//color = vec3(circle - verLine/shading);
    	color = vec3(circle -= atan(verLine)*iTime);
  	}
  	float t = length(ro*p);
  	//is final output for now
  	vec3 fog = mix(color, vec3(0.1,0.1,0.2), 1.*exp(-0.01*t*t));

  	fragColor = vec4(fog, 1.) + vec4((color + circle / 1.5), 1.);
}

/* Code by Sixclones

My boilerplate: https://www.shadertoy.com/view/wdsyzH */

#define E 0.000001
#define QP 0.785398163397448
#define TRP 1.047197551196598
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t

#define S(a, b, t) smoothstep(a, b, t)

float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

float map01(float n) {
  return 0.5 * n + 0.5;
}

vec2 rotate2d(vec2 uv, float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a)) * uv;
}

float circleSDF(vec2 uv) {
  return 2.0 * length(uv);
}

float rectSDF(vec2 uv, vec2 s) {
  return 2.0 * max(abs(uv.x / s.x), abs(uv.y / s.y));
}

float fill(float x, float s, float p) {
  p *= 0.1;
  return 1.0 - S(s - p, s + p, x);
}

void mainImage(out vec4 FragColor, in vec2 FragCoord) {
  vec4 uv = vec4(FragCoord.xy / iResolution.xy,
    (FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y);
  vec2 m = iMouse.xy / iResolution.xy;

  float n = 8.0;
  vec2 gv = fract(n * uv.xy);
  vec2 id = floor(n * uv.xy);

  vec3 color = vec3(0.0);
  vec3 black = vec3(0.129);
  vec3 white = vec3(0.925);
  vec3 pal1 = vec3(0.95, 0.22, 0.31);
  vec3 pal2 = vec3(0.21, 0.24, 0.63);

  // ANIM
  float speed = 0.75 * t;
  // speed = 2.0 * m.x; // debug
  /** ^ `x`
      |      /\            /\            /\            /\      
      |    /    \        /    \        /    \        /    \    
      |  /        \    /        \    /        \    /        \  
      |/____________\/____________\/____________\/____________\> `time` */
  float timer = fract(mix(speed, -speed, step(1.0, mod(speed, 2.0))));

  // SHAPES
  // angle of the break
  float angle = map(S(0.2, 0.8, fract(speed)), 0.0, 1.0, 3.0 * TP, 0.0) - QP;
  // break between the two shapes
  float breaker = step(0.0, rotate2d(uv.zw, angle).x);
  // mix the shapes and the palettes
  float mixer = S(0.45, 0.55, timer);

  // BACKGROUND
  // rotated uv
  vec2 rv = rotate2d(uv.zw, -HP - QP);
  // switch the bounds to change the direction of the rotation
  vec2 bounds = mix(
    vec2(1.0, E), vec2(E, 1.0),
    step(1.0, mod(speed, 2.0))
  );
  // MIXERS - mix shapes, colors and background based on time
  vec2 mixers = vec2(
    S(0.45, 0.55, timer),
    S(
      S(0.5, 1.0, S(0.45, 0.65, timer)),
      S(0.0, 0.5, S(0.25, 0.9, timer)),
      map(atan(rv.y, rv.x), -P, P, bounds.x, bounds.y)
    )
  );

  /* firstly compute the two sdf, circle and rectangle
  then mix/interpolate them into a shape
  finaly morph two values over time
  */
  // SDF
  float circ = circleSDF(uv.zw);
  float rect = rectSDF(uv.zw, vec2(1.0));
  float shape = mix(circ, rect, mixers.x);
  float shadow = rectSDF(rotate2d(uv.zw, angle), vec2(0.02, 0.25));
  // FIILS
  vec2 p = vec2(
    0.1,
    map(
      S(0.4, 0.6, 2.0 * fract(mix(speed, -speed, step(0.5, mod(speed, 1.0))))),
      0.0, 1.0,
      1.75, 4.5
    )
  ); // precisions
  // compute the fill of the two shapes, the sharp and the blurry7
  vec2 shapefs = vec2(fill(shape, 0.5, p.x), fill(shape, 0.5, p.y));
  // combine the two shapes to get this slashed render
  float shapef = mix(shapefs.x, shapefs.y, breaker); // main effect
  // compute the shadow
  float shadowf = 0.125 * fill(shadow, 1.0, 51.0 * p.y);
  color = mix(
    mix(white, black, mixers.y),
    mix(pal1, pal2, mixers.x) * shapef,
    shapef
  );
  // add more depth with a shadow
  color -= shapef * mix(shadowf, 0.0, breaker);

  FragColor = vec4(color, 1.0);
}

#define i_time iTime
#define i_resolution iResolution

float tt;
vec3 ap, bp;
float attr;

mat2 rot2(float r) {
  return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float sd_cylinder(vec3 p, float r, float height) {
  float d = length(p.xz) - r;
  d = max(d, abs(p.y) - height);
  return d;
}

float sd_box(vec3 p, vec3 b) {
  vec3 q  = abs(p) - b;
  return max(max(q.x, q.y), q.z);
}

float spiral(vec3 p) {
  vec3 q = p - vec3(.0, 2.5, .0);
  q.y *= 0.04;
  q.xz *= rot2((sin(q.y + 3.0)-tt*.05)*20.0);
  q.yz *= rot2(radians(20.0));
  q.y *= 0.18;
  q = abs(q) - vec3(0.3, -.55, 3.0);
  q -= vec3(.5, .05, 0.);
  float d = length(q) - .7;
  return d*0.5;
}

vec2 rails(vec3 p) {
  vec3 q = p;

  q.y -= .4;
  q = abs(q) - vec3(1.2, .2, 0.);
  vec2 a = vec2(sd_box(q, vec3(.1, .3, 20.)), 1.0);

  q = p;
  q.z = abs(q.z) - 2.0;
  q.xy *= rot2(radians(90.0));
  vec2 b = vec2(sd_cylinder(q - vec3(.8, 0, 0), 0.2, 5.), 1.0);

  a = (a.x < b.x) ? a : b;

  a.x *= 0.8;
  return a;
}

vec2 map(vec3 p) {
  attr = max(0.0, (6.0 - (length(p + vec3(0., 0., 1.0-tt*2.5)) - 6.0)));

  vec3 q = p;
  vec2 a = vec2(length(q - vec3(0., 0., 10. + tt*2.5)) - 2.5, 2.0);

  q = p;
  q.z = mod(q.z - 1.0, 11.0);
  q = abs(q) - vec3(0., 6., 11.);
  for (int i=0; i<6; i++) {
    q = abs(q) - vec3(2.0, 0.0, 0.5);
    //q.xz *= rot2(radians(i*5));
    q.xy *= rot2(radians(4.+float(i)*1.5));
  }

  vec2 b = rails(q);

  a = (a.x < b.x) ? a : b;

  float s = sin(tt*.5)*2.0 +  + 1.5;
  float ss = cos(tt*.5)*2.0 +  + 1.5;
  q = abs(q) - vec3(s, ss*2.0, 0.);
  b = rails(q);

  a = (a.x < b.x) ? a : b;

  q = p;
  q.yz *= rot2(radians(90.0));
  b = vec2(spiral(q), 1.0);

  a = (a.x < b.x) ? a : b;

  return a;
}

vec2 trace(vec3 ro, vec3 rd) {
  vec2 h, t = vec2(0.1);
  for (int i=0; i<128; i++) {
    h = map(ro+rd*t.x);
    if (h.x < .0001 || t.x > 50.0) break;
    t.x += h.x;
    t.y = h.y;
  }
  if (t.x > 50.0) t.x = 0.0;
  return t;
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
  vec2 uv = vec2(frag_coord.x / i_resolution.x, frag_coord.y / i_resolution.y);
  uv -= 0.5;
  uv /= vec2(i_resolution.y / i_resolution.x, 1);

  tt = i_time*0.5;

  vec3 ro = vec3(0., 1., -20.);
  ro.z += tt*2.5;
  // ro.xz *= rot2(tt*.5);

  vec3 cam_target = vec3(0, 5, 0);
  cam_target.z += tt*2.5;

  vec3 cw = normalize(cam_target - ro);
  vec3 cu = normalize(cross(cw, vec3(0,1,0)));
  vec3 cv = normalize(cross(cu, cw));

  vec3 rd = mat3(cu, cv, cw) * normalize(vec3(uv, 0.5));

  vec3 ld = normalize(vec3(.3, .5, -.5));

  // Fibonacci Blue #112358, R: 17, G: 35 B: 88

  vec3 fog = vec3(.066, .137, .345) * (1.0 - (length(uv)-0.2));
  vec3 col = fog;

  vec2 scene = trace(ro, rd);
  float t = scene.x;

  if (t > 0.0) {
    vec3 pp = ro+rd*t;

    vec2 e = vec2(.00035, -.00035);
    vec3 nor = normalize(e.xyy*map(pp+e.xyy).x +
                         e.yyx*map(pp+e.yyx).x +
                         e.xyx*map(pp+e.xyx).x +
                         e.xxx*map(pp+e.xxx).x);

    vec3 b0 = vec3(.1, .5, .8);
    vec3 b1 = vec3(0.7, .8, .8);
    vec3 base = mix(b0, b1, attr);

    if (scene.y == 1.0) {

      float diff = max(0., dot(nor, ld));

      float aor = t/30.;
      float ao = exp2(-2.*pow(max(0.,1.-map(pp+nor*aor).x/aor),2.));

      float fr = pow(1.+dot(nor,rd),2.);

      vec3 sss = vec3(0.5)*smoothstep(0.,1.,map(pp+ld*0.4).x/0.4);

      col = base*(0.9*ao+0.2)*(diff+sss);
      col = mix(col, b0, min(fr, 0.2));

    }

    col = mix(col, fog, 1.0 - exp(-.00005*t*t*t));

    if (scene.y == 2.0) {
      float fr = pow(1.+dot(nor,rd),5.);
      col = mix(vec3(0.), base, min(fr, 0.8));
    }

  }

  frag_color = vec4(col, 1.0);
}

// challenge: recreate https://www.instagram.com/p/B3dHAHCHSrv/
// via: @DmC

// easter colors:
vec3 colorA = vec3(1., .4, .8);
vec3 colorB = vec3(.8, 1., .4);
vec3 colorC = vec3(.4, .8, 1.);
const float shade = .2;

// animation:
const float persp = .9;
const float spin = .7;
const float dolly = .04;

// constants:
const float pi = asin(1.);
const float bgDepth = 1e16;

struct Cylinder {
    vec3 centers[2];
    float radius;
};

struct Ray {
    vec3 position;
    vec3 direction;
};

void hitPlane(vec3 position, vec3 normal, Ray ray, out float depth, out bool front)
{
    float nd = dot(normal, ray.direction);
    depth = dot(position - ray.position, normal) / nd;
    front = nd < 0.;
}

void hitCylinder(Cylinder cylinder, Ray ray, out float depth, out int faceId)
{
    // quadratic ray-cylinder intersection solve:
    vec3 dir = normalize(cylinder.centers[0] - cylinder.centers[1]);
    vec3 v0 = ray.position - cylinder.centers[0];
    vec3 v1 = ray.direction - dot(ray.direction, dir) * dir;
    vec3 v2 = v0 - dir * dot(v0, dir);
    float a = dot(v1, v1);
    float b = dot(v1, v2) * 2.;
    float c = dot(v2, v2) - cylinder.radius * cylinder.radius;
    float desc = b * b - 4. * a * c;
    float sqrtDesc = sqrt(desc);
    float d = .5 / a;
    depth = desc > 0. ? (-b - sqrtDesc) * d : bgDepth;
    float backDepth = desc > 0. ? (-b + sqrtDesc) * d : bgDepth;

    // end caps:
    faceId = 0;
    for (int i = 0; i < 2; ++i) {
        float hitDepth;
        bool front;
        hitPlane(cylinder.centers[i], dir, ray, hitDepth, front);
        if (front != bool(i)) {
            if (backDepth < hitDepth) {
                depth = bgDepth;
            } else if (depth < hitDepth) {
                depth = hitDepth;
                faceId = i + 1;
            }
        } else if (depth > hitDepth) {
            depth = bgDepth;
        }
    }
}

vec4 shadeScene(vec2 uv)
{
    // spin angle:
    float angle = iTime * spin + pi * .5;
    float sinAngle = sin(angle);
    float cosAngle = cos(angle);
    bool phase = sinAngle > 0.;

    // camera dolly:
    float dollyTime = iTime * iTime * dolly + 1.;

    // this does the crazy ortho/persp blending:
    Ray ray;
    ray.position = vec3(uv * dollyTime, 0.);
    float lens = abs(sinAngle); // <- this
    ray.direction = normalize(vec3(uv * persp * lens, 1.));

    // tiling selects which cylinders are drawn:
    vec2 tile = floor((ray.position.xy - vec2(0., float(phase))) * .5 + .5);
    vec3 tileCenter = vec3(tile * 2. + vec2(0., float(phase)), 0.);

    // draw 4 cylinders around empty center hole:
    Cylinder cylinder;
    cylinder.radius = .5;
    float depth = bgDepth;
    int colorId;
    for (int dir = 0; dir < 2; ++dir) { // x or y
        float endOffsetZ = bool(dir) == phase ? -sinAngle : sinAngle;
        vec3 endOffset = vec3(0., cosAngle, endOffsetZ) * .5;
        for (int sign = 0; sign < 2; ++sign) { // - or +
            // draw a cylinder:
            vec3 cylinderOffset = vec3(0.);
            cylinderOffset[dir] = float(sign * 2 - 1);
            cylinder.centers[0] = tileCenter + cylinderOffset + endOffset;
            cylinder.centers[1] = tileCenter + cylinderOffset - endOffset;
            float hitDepth;
            int faceId;
            hitCylinder(cylinder, ray, hitDepth, faceId);
            // depth composite:
            if (hitDepth < depth) {
                depth = hitDepth;
                colorId = faceId;
            }
        }
    }

    // colors:
    vec3 modelColor = bool(colorId % 3) ? colorC : (phase ? colorB : colorA);
    vec3 bgColor = phase ? colorA : colorB;

    // depth lighting:
    float light = mix(1., .5 - depth, shade);
    float fill = 1. - shade * .5;
    modelColor *= mix(fill, light, lens);
    bgColor *= fill;

    // final comp:
    return vec4(depth < bgDepth ? modelColor : bgColor, 1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // supersampling:
    const int samplesSqrt = 2;
    float xyScale = 1. / iResolution.y;
    vec2 xy = (fragCoord.xy - iResolution.xy * .5) * xyScale;
    float xyStep = xyScale / float(samplesSqrt);
    fragColor = vec4(0.);
    for (int i = 0; i < samplesSqrt; ++i) {
        for (int j = 0; j < samplesSqrt; ++j) {
            vec2 uv = xy + vec2(i, j) * xyStep - xyScale * .5;
            fragColor += shadeScene(uv);
        }
    }
    fragColor *= 1. / fragColor.a;
}

vec4 RotationBetween(vec3 v0, vec3 v1)
{
    // Shortest arc
    float d = dot(v0, v1);
    float s = sqrt((1.0 + d) * 2.0);
    float invs = 1.0 / s;
    vec3 c = cross(v1, v0);
    return vec4(c.xyz * invs, 0.5 * s);
}

vec4 RotationFromZ(vec3 v1)
{
    // Shortest arc from <0, 0, 1>
    int x = floatBitsToInt(2.0 + v1.z * 2.0);
    //x = 0x1FBD1DF5 + (x >> 1);
    // rsqrt from Robin's new math paper, allows one less div
    x = 0x5F37624F - (x >> 1);
    float s = intBitsToFloat(x);
    //return vec4(v1.y / s, -v1.x / s, 0.0, 0.5 * s);
    return vec4(v1.y * s, -v1.x * s, 0.0, 0.5 / s);
}

vec4 nlerp(vec4 q0, vec4 q1, float t)
{
    return normalize(mix(q0, q1, t));
}

vec4 fnlerp(vec4 l, vec4 r, float t)
{
	float ca = dot(l, r);
	float k = 0.931872f + ca * (-1.25654f + ca * 0.331442f);
	float ot = t + t * (t - 0.5f) * (t - 1.0) * k;
    return normalize(mix(l, r, ot));
}

vec4 slerp(vec4 a, vec4 b, float t)
{
    // Get half-angle between quaternions
    float cos_theta = clamp(dot(a, b), -1.0, 1.0);
    float theta = acos(cos_theta);
    float sin_theta = sqrt(1.0 - cos_theta * cos_theta);

    // Slerp
    float t0 = sin((1.0 - t) * theta) / sin_theta;
    float t1 = sin(t * theta) / sin_theta;
    vec4 r;
    r.x = a.x * t0 + b.x * t1;
    r.y = a.y * t0 + b.y * t1;
    r.z = a.z * t0 + b.z * t1;
    r.w = a.w * t0 + b.w * t1;
    return r;
}

vec3 Transform(vec4 q, vec3 v)
{
    return v * (q.w * q.w - dot(q.xyz, q.xyz)) + 2.0 * q.xyz * dot(q.xyz, v) + 2.0 * q.w * cross(q.xyz, v);
    //return v + 2.0 * cross(q.xyz, q.w * v + cross(q.xyz, v));
}

vec3 TransformZ(vec4 q)
{
    // Transforming <0,0,1> by q where q.z = 0 for tangent-space normal
    vec3 r;
    r.x = -2.0 * q.w * q.y;
    r.y =  2.0 * q.w * q.x;
    r.z =  q.w * q.w - dot(q.xy, q.xy);
    return r;

    // c = cross(a, b)
    // c.x = a.y * b.z - b.y * a.z
    // c.y = a.z * b.x - b.z * a.x
    // c.z = a.x * b.y - b.x * a.y
}

vec3 NormalBlend(vec3 n1, vec3 n2, float t)
{
    n1 = n1 * 2.0 - 1.0;
    n2 = n2 * 2.0 - 1.0;

    //return n1;

    //return normalize(mix(n1, n2, 0.8));

    #if 1

    //vec4 q1 = RotationBetween(vec3(0.0, 0.0, 1.0), n1);
    vec4 q1 = RotationFromZ(n1);

    //vec4 q2 = RotationBetween(vec3(0.0, 0.0, 1.0), n2);
    vec4 q2 = RotationFromZ(n2);    

    vec4 qa = nlerp(q1, q2, t);
    vec4 qb = fnlerp(q1, q2, t);
    vec4 qc = slerp(q1, q2, t);

    //vec4 d = qc - qb;
    //return vec3(abs(dot(d, d)) * 10000.0);

    //return Transform(qa, vec3(0.0, 0.0, 1.0));
    return TransformZ(qa);

    #else

    vec4 q = RotationBetween(n1, n2);

    //vec4 qr = slerp(vec4(0.0, 0.0, 0.0, 1.0), q, 0.2);
    vec4 qr = nlerp(vec4(0.0, 0.0, 0.0, 1.0), q, 1 - t);

    if (abs(qr.w) < 0.001) return vec3(1.0);

    //qr.z = 0.0;

    vec3 p = Transform(qr, n2);

    return p;

    #endif
}


// Compute base normal (since we don't have a texture)
vec3 ComputeBaseNormal(vec2 uv) 
{
    uv = fract(uv) * 2.0 - 1.0;    

    vec3 ret;
    ret.xy = sqrt(uv * uv) * sign(uv);
    ret.z = sqrt(abs(1.0 - dot(ret.xy,ret.xy)));

    ret = ret * 0.5 + 0.5;
    return mix(vec3(0.5,0.5,1.0), ret, smoothstep(1.0,0.95,dot(uv,uv)));
}

// Compute a detail normal (since we don't have a texture)
vec3 ComputeDetailNormal(vec2 uv)
{
    const vec4 avgRGB0 = vec4(1.0/3.0, 1.0/3.0, 1.0/3.0, 0.0);
    const float scale = 0.02;
    const vec2 du = vec2(1.0/512.0, 0.0);
    const vec2 dv = vec2(0.0, 1.0/512.0);

    float h0  = dot(avgRGB0, texture(iChannel0, uv)) * scale;
    float hpx = dot(avgRGB0, texture(iChannel0, uv + du)) * scale;
    float hmx = dot(avgRGB0, texture(iChannel0, uv - du)) * scale;
    float hpy = dot(avgRGB0, texture(iChannel0, uv + dv)) * scale;
    float hmy = dot(avgRGB0, texture(iChannel0, uv - dv)) * scale;

    float dHdU = (hmx - hpx) / (2.0 * du.x);
    float dHdV = (hmy - hpy) / (2.0 * dv.y);

    return normalize(vec3(dHdU, dHdV, 1.0)) * 0.5 + 0.5;
}
