// Main
//---------------------------------------------------------------------------------------------
void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.x;

    //---------------------------------------------------------------------------------------------------------
    // [LEFT SIDE] - Combined normal, switches between technique
    //---------------------------------------------------------------------------------------------------------
    // Base Normal (Disk)
    vec2 uvN = uv * (iResolution.x / iResolution.y) * 1.2 - vec2(0.12, 0.16);

  	vec3 BN = vec3(0.5, 0.5, 1.0);
    if (uvN.x > 0.0 && uvN.x < 0.83 && uvN.y < 0.83 && uvN.y > 0.0)
    {
        BN = ComputeBaseNormal(uvN * vec2(1.2,1.2));
    }

    // Detail Normal
	vec2 uvDN = uvN;
    vec3 n1 = ComputeDetailNormal(uvDN);

    float t = 0.5;
    t = fract(iTime * 0.1);

	// Combined Normal
    vec3 N = NormalBlend(BN, n1, t);

    //---------------------------------------------------------------------------------------------------------
    // [RIGHT SIDE] - Combined normal with lighting, switches between technique
    //---------------------------------------------------------------------------------------------------------
    // Base Normal (Disk)
    uvN = (uv + vec2(0.28, 0.0)) * (iResolution.x / iResolution.y) * 1.2 - vec2(0.12, 0.16);

  	BN = vec3(0.5, 0.5, 1.0);
    if (uvN.x > 1.66 && uvN.y > 0.0 && uvN.y < 0.83 && uvN.x < 2.50)
    {
        BN = ComputeBaseNormal(uvN * vec2(1.2,1.2));
    }

    // Detail Normal
	uvDN = uvN+vec2(0.33,0);
    n1 = ComputeDetailNormal(uvDN);

	// Combined Normal
    vec3 N2 = NormalBlend(BN, n1, t);

	vec3 light = normalize(vec3(1.0, 0.0, 1.0));    
    vec3 lit = vec3(clamp(dot(light, N2), 0.0, 1.0));

    // Mix normal and lit result
	vec3 color = mix(N*0.5 + 0.5, lit, float(uv.x > 0.5));

	fragColor = vec4(color, 1.0);
}

mat2 rotate(float a){
    float c=cos(a),s=sin(a);
    return mat2(c,-s,s,c);
}

float sdRect(vec2 p, vec2 r)
{
    p=abs(p)-r;
    return max(p.x,p.y);
}

float sdf(vec2 p)
{
    float d=1e9;
    d=min(d,sdRect(p-vec2(3),vec2(7,1)));
    d=min(d,sdRect(p-vec2(3),vec2(1,7)));
    d=min(d,sdRect(p+vec2(3),vec2(1,7)));
    d=min(d,sdRect(p+vec2(6,3),vec2(4,1)));
    d=min(d,sdRect(p+vec2(9),vec2(1)));
    d=min(d,sdRect(p-vec2(9),vec2(1)));
    return d;
}

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
	vec2 uv=fragCoord.xy/iResolution.xy;
	vec4 tex=texelFetch(iChannel0,ivec2(fragCoord),0);

	// divide by sample-count
	vec3 color=tex.rgb/tex.a;

    // each grayscale light is in a separate color channel
    // so I can adjust the balance in post here
    // comment this out for pretty debug colors
    vec3 weights = vec3(1.5,.2,.2);
    weights /= dot(weights,vec3(1));
    color = vec3(dot(color,weights));

	// vignette to darken the corners
	uv-=.5;
	color *= 1.-dot(uv,uv)*.8;

    // exposure and tonemap
    color *= 2.5;
    color = 1.-exp(color*-2.);

	// gamma correction
	color = pow(color, vec3(.45));

    // raise the black level slightly
    color = color*.98+.02;

    // "final" color
    fragColor = vec4(vec3(color),1);

	// set up for logo overlay
    uv.x*=iResolution.x/iResolution.y;
    uv -= vec2(.8888,-.5); // assumes 16:9
    uv *= 720.;
    uv += vec2(20,-20);
	float threshold = abs(dFdx(uv.x)*.5);
	uv+=vec2(8,0);
    uv*=rotate(acos(-1.)*.25);

    // logo overlay
	fragColor.rgb *= smoothstep(-threshold,threshold,sdf(uv))*.2+.8;
}

