#define M_PI 3.141592
#define SND_HZ 48000.

float s(float t)
{
	return sin(t*2.*M_PI)*.25;
}

float s1(float t)
{
	if(t<.2)
    {
        t/=.2;
        return sin(t*2.*M_PI)*.25;
    }
	return 0.;
}

int l1a[16]=int[16](0,8,6,7,6,0,4,3,5,6,6,0,2,1,2,1);
int l1b[16]=int[16](0,1,2,0,4,2,1,0,1,2,0,3,4,2,0,0);
int l2a[16]=int[16](0,1,0,2,0,3,0,1,0,2,0,3,0,1,2,0);
int l2b[16]=int[16](2,4,3,2,1,4,2,3,2,4,1,2,3,3,2,6);

float sndVal(float ft)
{
//    original oneliners
//	int i1=(t*("0867604356602121"[(t>>12)&15]-'0')*("0120421012034200"[(t>>16)&15]-'0'));
//	int i2=(t*("0102030102030120"[(t>>12)&15]-'0')*("2432142624123326"[(t>>17)&15]-'0'));
    int idx1=int(ft/4096. )&15;
    int idx2=int(ft/65536.)&15;
    int idx3=int(ft/65536./2.)&15;

    float i1=ft*float(l1a[idx1]*l1b[idx2]);
	float i2=ft*float(l2a[idx1]*l2b[idx3]*4);

    return (
        s(mod(i1,1024.)/1024.)*.5
        -s(mod(i1,256.)/256.)*.25
        +s1(mod(i2,2048.)/2048.)*.25
    )

    ;
}

vec2 tune0(float time)
{
    vec2 val=vec2(0);
    float vib=time*M_PI/2.;
    val.x=sndVal((time*SND_HZ+sin(vib)*250.));
    val.y=sndVal((time*SND_HZ-sin(vib)*250.));
    return val;
}

const float delay=1024.*8./SND_HZ;


vec2 tune(float time)
{
    time*=.68;
    // synchronize echo with beats
    float echomin=0.75;
    vec2 val=tune0(time);
    //val=vec2(0.);
    //if(false)
    for(float ex=1.;ex<=8.1;ex+=1.)
    {
        val+=pow(echomin,ex)*tune0(time-ex*delay);
    }
    return val;
}

