    vec3 curLook = normalize (-curEye);
    vec3 curSide = normalize (cross (curLook, vec3 (0.0, 1.0, 0.0)));
    vec3 curUp = cross (curSide, curLook);

    vec3 sampleDirNorm = normalize (curLook + curUp * uv.y + curSide * uv.x);
    vec3 sampleDir = sampleDirNorm * 0.01;
    vec3 samplePt = curEye + sampleDir;

    for (int i = 0; i != 650; i++)
    {
        float hitAzimuth;
