            azimuthColor = azimuthColor * 3.0;
            fragColor.rgb = clamp (samplePt.y + 1.0, 0.0, 1.0) * azimuthColor;
            return;
        }
        samplePt += sampleDir;
    }

    float shiftedTime = iTime + 1000.0; // Better noise around here...
    vec3 tunnelDir = normalize (vec3 (uv.x, uv.y, 1.0));
    tunnelDir.xy = rotate(tunnelDir.xy, shiftedTime);

