vec3 lookat(vec2 uv, vec3 dir)
{
  float fov = 1.;
  uv *= fov;
  dir = normalize(dir);
  vec3 right = normalize(cross(dir, vec3(0.,1.,0.)));
  vec3 up = normalize(cross(dir, right));

  return dir + uv.x * right + uv.y*up;
}


// Raymarching SDF composition
float map(vec3 p)
{
    vec3 off = vec3(0.,-2.,0.);
	float szf = -0.02;
    vec3 sz = vec3(1.,1.,1.)*szf;
    float acc = 10.0;
    if (LeftAvailable > .5)
    	acc = min(acc, sph(off+szf*coordMix(LeftHand[0])+p));
    if (RightAvailable > .5)
    	acc = min(acc, sph(off+szf*coordMix(RightHand[0])+p));
    for (int i = 1; i < 27; ++i)
    {
        if (LeftAvailable > .5)
        	acc = min(acc, sph(off+szf*coordMix(LeftHand[i])+p));
        if (RightAvailable > .5)
        	acc = min(acc, sph(off+szf*coordMix(RightHand[i])+p));
    }
   return acc;
}

vec3 normal(vec3 p, float d)
{
  float xPos = map(p+EPS.xyy);
  float yPos = map(p+EPS.yxy);
  float zPos = map(p+EPS.yyx);
  return (vec3(xPos, yPos,zPos)-d)/EPS.x;
  return vec3(map(p+EPS.xyy)-map(p-EPS.xyy),
    map(p+EPS.yxy)-map(p-EPS.yxy),
    map(p+EPS.yyx)-map(p-EPS.yyx));
}

vec4 rdr3D(vec2 uv)
{
  vec3 orig = vec3(0.,0.,-10.);
  vec3 lookatpos = vec3(0.);
  vec3 dir = normalize(lookat(uv, lookatpos-orig));
  vec3 p = orig + dir;

  for (int i = 0; i <64;++i)
  {
    float d = map(p);

    if (d < 0.01)
    {
      vec3 norm = normal(p,d);
      return vec4(norm*.5+.5,1.);
      vec3 lPos = 3.*vec3(sin(iTime),cos(iTime),0.);
       return vec4(dot(norm,normalize(lPos-p))*vec3(1.),1.);
    }
    p+= dir*d;
  }
  return vec4(0.);
}

vec3 rdrScn(vec2 uv)
{
  vec3 land;
  vec3 light = vec3(100.,200.,197.)/255.;
  land = mix(vec3(.1,.1,.2),light,sat(1.-length(uv)));
  vec4 col = rdr3D(uv);
  land = mix(land, col.xyz, col.w);
  return land;
}

