#define RADIUS 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	// algorithm stolen from the gimp oilify filter :3
	float hist[256];
	for (int i = 0 ; i < 256 ; i++ ) hist[i] = 0.;
	float histmax = 0.;
	float rad = RADIUS;

	float mainsamp = 0.;
	for(float i = -rad; i <= rad; i+=1.){
		for(float j = -rad; j <= rad; j+=1.){
			vec2 off = vec2(i,j);
			if (length(off) > rad) continue;
			float samp = pow(clamp(texelFetch(iChannel0, ivec2(fragCoord.xy + off), 0).x,0.,1.),2.);
			if (i == 0. && j == 0.) mainsamp = samp;
			int pos = int(samp*254.);
			hist[pos] += 1.;
			histmax =max(histmax,hist[pos]);
		  }
	}
	float color = 0.;
	float acc = 0.;
	for (int i = 0; i < 255; i++ ){
		float weight = pow(hist[i]/histmax, 8.);
		color += (float(i)/255.) * weight;
		acc += weight;
	}

	fragColor = vec4(sqrt(mix(color/acc, mainsamp, 0.25)));
}

