vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

vec3 hash3(float a) {
    float h = hash(a, a+32.23);
    return vec3(hash(a,h), hash(a+h,h*a), hash(a+h,h));
}

