void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x*2.;
    bool fix = uv.x > 0.;
    uv.x = fract(uv.x)-0.5;

    vec3 cam = normalize(vec3(1.5,uv));
    vec3 init = vec3(-5,0,0);
    cam = erot(cam, vec3(0,1,0), cos(iTime));
    init = erot(init, vec3(0,1,0), cos(iTime));
    cam = erot(cam, vec3(0,0,1), iTime);
    init = erot(init, vec3(0,0,1), iTime);
    vec3 p = init;
    bool hit = false;
    for (int i = 0; i < 100; i++) {
        float dist = sphere(p);
        if (dist*dist < 0.00001) {hit = true; break;}
        p+=dist*cam;
    }

    if (hit) {
        vec2 uv = map_from_sphere(p);
        if (fix) {
    		fragColor = texture(iChannel1, uv);
        } else {
    		fragColor = texture(iChannel0, uv);
        }
    } else {
        fragColor.xyz = vec3(0.0);
    }
}

