vec3 map_to_sphere(vec2 p)
{
    float theta = (p.x-0.5)*PI*2.;
    float phi = (p.y-0.5)*PI*2.;
    return vec3(sin(phi)*sin(theta), sin(phi)*cos(theta), cos(phi));
}

vec2 map_from_sphere(vec3 p)
{
    p = normalize(p);
    return vec2(atan(p.x,p.y)/PI/2.+0.5, acos(p.z)/PI/2.+0.5);
}

vec4 bufferImage( vec2 uv, vec4 data, float frame, bool fix ) {
    for (int i = 0; i < 50; i++) {
