//prev closest triangle buffer
//render

float sdObj(vec3 p, in object o)
{
    float d = distance(p, o.X) - o.bR;
    if(d > o.bR) return d;
    return udTriangle(p, o.a, o.b, o.c) - 0.002;
}

object getObject(int id)
{
    object o;
    if(id == maxN-1) return o;
    vec4 a = texel(ch0, i2xy(ivec3(id, 0, 0))); 
    o.X = a.xyz; o.bR = a.w;
    o.a = texel(ch0, i2xy(ivec3(id, 1, 0))).xyz;
    o.b = texel(ch0, i2xy(ivec3(id, 2, 0))).xyz;
    o.c = texel(ch0, i2xy(ivec3(id, 3, 0))).xyz;
    o.id = id;
    return o;
}

float sdObjID(vec3 p, int id)
{
    vec4 X = texel(ch0, i2xy(ivec3(id, 0, 0)));
    float d = distance(p, X.xyz) - X.w;
    if(d > X.w || id > tot_n) return d;
    return mix(udTriangle(p, texel(ch0, i2xy(ivec3(id, 1, 0))).xyz,
                      	 texel(ch0, i2xy(ivec3(id, 2, 0))).xyz,
                         texel(ch0, i2xy(ivec3(id, 3, 0))).xyz) - 0.002, d, clamp(d/X.w, 0., 1.));
}

struct vtx
{
    vec3 X;
    int id;
};

vtx getVertex(vec3 p, int i)
{
    vtx a;
    a.X = p;
    a.id = i;
    return a;
}

void findClosest(vec3 p, inout vtx co, int mit)
{    
    float t = distance(p, co.X); 
    for(int s = min(0,iFrame); s < mit; s++)
    {
    	int pid = co.id;
        int dir = int(inverseSF(normalize(p - co.X), float(link0)).x);
        vec4 nb = texel(ch0, i2xy(ivec3(co.id, dir%link0, 1)));
        for(int i = min(0,iFrame); i<4; i++)
        {
            vec3 vpos = texel(ch0, i2xy(ivec3(toI(nb, i), 4, 0))).xyz;
            float tt = distance(p, vpos);
            if(tt < t)
            {
                t = tt;
                co = getVertex(vpos, toI(nb, i));
            }
        }   

        if(pid == co.id) break;
    }
}

#define maxd 20.
#define mind 0.003
#define maxs 64

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}


float map(vec3 p, vec3 d, inout vtx co)
{
    //find closest vertex
    findClosest(p, co, 5);

    float DE = distance(co.X, p) - 0.001;
    float a = 1. + 0.15*clamp(dot(normalize(p - co.X), d), -1., 0.);

    //compute sdf in this direction to the vertex
    int dir = int(inverseSF(normalize(p - co.X), float(link0)).x);
    vec4 nb = texel(ch0, i2xy(ivec3(co.id, dir%link0, 2)));
    for(int i = 0; i < 5; i++)
    {
        DE = min(DE, sdObjID( p, toI(nb, i)));
    }

    return min(a*DE, p.z+0.5);
}

vec4 calcNormal(vec3 p, float dx, in vtx co) {
	const vec3 k = vec3(1,-1,0);
	return   (k.xyyx*map(p + k.xyy*dx, vec3(0,0,1), co) +
			 k.yyxx*map(p + k.yyx*dx,vec3(0,0,1), co) +
			 k.yxyx*map(p + k.yxy*dx,vec3(0,0,1), co) +
			 k.xxxx*map(p + k.xxx*dx,vec3(0,0,1), co))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

vec3 col(vec3 p, in vtx co)
{
    //voronoi neighbors projected on the sphere surface
    float t = 1e10;
    vec3 D = normalize(p - co.X);
    int cid = co.id;
    int dir = int(inverseSF(D, float(link0)).x);
    vec4 nb = texel(ch0, i2xy(ivec3(co.id, dir%link0, 1)));
    for(int i = min(0,iFrame); i<0; i++)
    {
        vec3 vpos = texel(ch0, i2xy(ivec3(toI(nb, i), 4, 0))).xyz;
        float tt = dirBisectDist(D, co.X, vpos, 0.);
        if(tt < t)
        {
            t = tt;
            cid = toI(nb, i);
        }
    }   

    return 0.5 + 0.5 * sin(vec3(1,2,3)*float(cid));
}


//voronoi diagram ray marching
vec4 rayMarch(vec3 p, vec3 d, inout vtx co)
{    
    float td = 0.; float DE = 1e10;
    for(int i = min(0, iFrame); i < maxs; i++)
    {
        //march
        DE = map(p, d, co);

        p += DE*d;
        td += DE;

        //outide of the scene
        if(td > maxd) return vec4(p, -1.);
        //has hit the surface
        if(DE < mind*td)
        {
            p += - mind*td*d;
            break;
        }
    }
    return vec4(p, DE);
}

float shadow_march(vec4 pos, vec4 dir, float distance2light, float light_angle, inout vtx co)
{
	float light_visibility = 1.;
	float ph = 1e5;
    float td = dir.w;
	pos.w = map(pos.xyz, dir.xyz, co);
	for (int i = min(0, iFrame); i < 20; i++) 
    {
		dir.w += pos.w;
		pos.xyz += pos.w*dir.xyz;
		pos.w = map(pos.xyz, dir.xyz, co);
		float y = pos.w*pos.w/(2.0*ph);
        float d = (pos.w+ph)*0.5;
		float angle = d/(max(0.00001,dir.w-y-td)*light_angle);
        light_visibility = min(light_visibility, angle);
		ph = pos.w;
		if(dir.w >= distance2light) break;
		if(dir.w > maxd || pos.w < mind*dir.w) return 0.;
    }
	return 0.5 - 0.5*cos(PI*light_visibility);
}


#define FOV 2.
mat3 getRot(vec2 a)
{

   mat3 theta_rot = mat3(1, 0, 0,
                         0, cos(a.y), sin(a.y),
                         0, -sin(a.y), cos(a.y)); 

   mat3 phi_rot = mat3(cos(a.x), sin(a.x), 0,
        		       -sin(a.x), cos(a.x), 0,
        		        0, 0, 1); 
   return transpose(phi_rot*theta_rot);
}

vec3 getRay(vec2 angles, vec2 pos)
{
    mat3 camera = getRot(angles);
    return normalize(transpose(camera)*vec3(FOV*pos.x, FOV*pos.y, 1.));
}


#define ldir vec3(cos(iTime*0.1)*sin(PI*0.4),sin(iTime*0.1)*sin(PI*0.4), cos(PI*0.4))

vec3 ACESFilm(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 HDRmapping(vec3 color, float exposure)
{
	// Exposure tone mapping
    vec3 mapped = ACESFilm(color * exposure);
    // Gamma correction 
    return pow(mapped, vec3(1.0 / 2.2));
}

const float Br = 0.0025;
const float Bm = 0.0003;
const float g =  0.9800;
const vec3 nitrogen = vec3(0.650, 0.570, 0.475);
const vec3 Kr = Br / pow(nitrogen, vec3(4.0));
const vec3 Km = Bm / pow(nitrogen, vec3(0.84));

vec3 sky_color(in vec3 pos)
{
	// Atmosphere Scattering
	vec3 fsun = ldir.xzy;
	float brightnees = exp(-sqrt(pow(abs(min(5.*(pos.y-0.1),0.)),2.)+0.1));
	if(pos.y < 0.)
	{
		pos.y = 0.;
		pos.xyz = normalize(pos.xyz);
	}
    float mu = dot(normalize(pos), normalize(fsun));

	vec3 extinction = mix(exp(-exp(-((pos.y + fsun.y * 4.0) * (exp(-pos.y * 16.0) + 0.1) / 80.0) / Br) * (exp(-pos.y * 16.0) + 0.1) * Kr / Br) * exp(-pos.y * exp(-pos.y * 8.0 ) * 4.0) * exp(-pos.y * 2.0) * 4.0, vec3(1.0 - exp(fsun.y)) * 0.2, -fsun.y * 0.2 + 0.5);
	vec3 sky_col = brightnees* 3.0 / (8.0 * 3.14) * (1.0 + mu * mu) * (Kr + Km * (1.0 - g * g) / (2.0 + g * g) / pow(1.0 + g * g - 2.0 * g * mu, 1.5)) / (Br + Bm) * extinction;
	sky_col = 0.4*clamp(sky_col,0.,10.);
	return pow(sky_col,vec3(1./1.5)); 
}

vec3 ambient_sky_color(in vec3 pos)
{
	float y = pos.y;
	pos.xyz = normalize(vec3(1,0,0));
	return sky_color(pos)*exp(-abs(y));
}

vec4 ambient_occlusion(in vec4 pos, in vec4 norm, in vec4 dir, in vtx co)
{	
	vec3 pos0 = pos.xyz;
	vec3 ambient_color = ambient_sky_color(norm.xyz);
	pos.xyz += 0.03*dir.w*norm.xyz;
    pos.w = map(pos.xyz,  dir.xyz, co);
	pos.xyz += pos.w*norm.xyz;
	norm.w = length(pos0 - pos.xyz);
	float occlusion_angle = clamp(pos.w/norm.w,0.,1.);
	return vec4(ambient_color,1.)*(0.5-cos(3.14159265*occlusion_angle)*0.5);
}

void mainImage( out vec4 U, in vec2 pos )
{
    vec2 uv = (pos - size*0.5)/size.y;
    vec2 angles = (iMouse.z>0.)?(iMouse.xy/iResolution.xy)*vec2(2.*PI, PI):vec2(-0.3*iTime, PI*0.55);

    vec3 d = getRay(angles, uv);
    vec3 p = 0.*texel(ch0, i2xy(ivec3(94, 4, 0))).xyz-2.*getRay(angles, vec2(0.));
    vec4 prev = pixel(ch1, pos);
    if(iFrame < 1) prev.w = 0.;
    int cid = int(prev.w)%tot_n;
    vtx co = getVertex(texel(ch0, i2xy(ivec3(cid, 4, 0))).xyz,cid);
    findClosest(p, co, 16);
    cid = co.id;

    vec4 rm = rayMarch(p, d, co);
    float td = distance(rm.xyz, p);
    if(rm.w >= 0.) //hit object
    {
        vec3 color = 0.75+0.5*col(rm.xyz, co);
    	vec3 n = normalize(calcNormal(rm.xyz, mind*td,co).xyz);
        vec4 ao = ambient_occlusion(rm, vec4(n, rm.w), vec4(d, td), co);
        float sh = shadow_march(vec4(rm.xyz+n*td*mind, rm.w), vec4(ldir,td), 10., 0.12, co);
        U.xyz = color*(sh*sky_color(ldir.xzy)*max(dot(ldir, n),0.) + ao.xyz);
    }
    else //background
    {
        U.xyz = sky_color(d.xzy);
    }

    U.xyz = HDRmapping(U.xyz, 0.5);
    U.w = float(cid);
}

