/*
Invertible smootherstep (C2 continuous s-curve) build from 3 cubic segments.

 red  : cubic smootherstep
 blue : inverse of cubic smootherstep
 black: traditional quintic smootherstep
(green: sine based smootherstep)

Second derivatives scaled by 1/8 are plotted in the background.
*/

#define PLOT_SECONDDERIVATIVES
//#define PLOT_SINSMOOTHERSTEP


float Smoothstep(float x)
{
    x = clamp(x, 0.0, 1.0);

    return (x*x) * (x * -2.0 + 3.0);
}

// https://www.shadertoy.com/view/MsSBRh
float InverseSmoothstep(float y)
{
    return 0.5 - sin(asin(1.0-2.0*y)/3.0);
}

// traditional quintic smootherstep
float Smootherstep(float x)
{
    x = clamp(x, 0.0, 1.0);

    return (x*x*x) * (x * (x * 6.0 - 15.0) + 10.0);
}

// sine based smootherstep
float SinSmootherstep(float x)
{
    x = clamp(x, 0.0, 1.0);

    return x - sin(x * Pi2)/Pi2;
}

float CubicSmootherstep(float x)
{
    x = clamp(x, 0.0, 1.0);

    float a = 0.0;
    float b = 0.0;
    float c = 0.0;
    float d = 16.0/3.0;

    if(x > 0.25)
    {
        a = 1.0/6.0;
        b = -2.0;
        c = 8.0;
        d = -16.0/3.0;
    }

    if(x > 0.75)
    {
        a = -13.0/3.0;
        b = 16.0;
        c = -16.0;
        d = 16.0/3.0;
    }

    return a + x * (b + x * (c + d * x));
}

float InverseCubicSmootherstep(float y)
{
    y = clamp(y, 0.0, 1.0);

    if(y > 1.0/12.0 && y < 11.0/12.0)
    	return InverseSmoothstep(y * 1.0606601717798212 - 0.030330085889910707) * 0.7071067811865475 + 0.14644660940672627;

    float s = y < 0.5 ? 0.0 : 1.0;

    return abs(s - 0.5723571212766659 * pow(abs(s - y), 1.0/3.0));
}

// second derivatives
float SmootherstepD2(float x)
{
	return x * (60.0 + x * (-180.0 + 120.0 * x));    
}

float CubicSmootherstepD2(float x)
{
    if(x < 0.25) return 32.0*x;
    if(x > 0.75) return -32.0+32.0*x;

    return 16.0-32.0*x;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{
	vec2 uv = uv0 - 0.5;
    uv0 += 0.0;

    vec2 tex = uv0;
    tex -= iResolution.xy * 0.125;
    tex /= iResolution.xx * 0.125;

    tex *= 0.25+0.0375;

    vec3 col = vec3(0.0);

	col = mix(vec3(1.0), vec3(0.9), Grid(tex.xy * 4.0, 0.0));        
	col = mix(col, vec3(0.5), Grid(tex.xy * 1.0, 0.0));        
	col = mix(col, vec3(0.125), Cross(tex.xy * 1.0, 0.)); 	



    #ifdef PLOT_SECONDDERIVATIVES
    col = mix(col, vec3(0.0), 0.2*Graph(SmootherstepD2(tex.x)*0.125 - tex.y, 1.125));       
    col = mix(col, vec3(1.0, 0.1, 0.1), 0.2*Graph(CubicSmootherstepD2(tex.x)*0.125 - tex.y, 1.125));       

    #ifdef PLOT_SINSMOOTHERSTEP
    col = mix(col, vec3(0.0, 1.0, 0.2), 0.3*Graph(sin(tex.x*Pi2)*Pi2*0.125 - tex.y, 1.125));       
    #endif
    #endif

    col = mix(col, vec3(0.0), 0.90*Graph(Smootherstep(tex.x) - tex.y, 0.5));       

    col = mix(col, vec3(0.0, 0.5, 1.0), 1.0*Graph(InverseCubicSmootherstep(tex.x) - tex.y, 0.5));       
    col = mix(col, vec3(1.0, 0.1, 0.1), 1.0*Graph(CubicSmootherstep(tex.x) - tex.y, 0.5));   

    #ifdef PLOT_SINSMOOTHERSTEP
    col = mix(col, vec3(0.0, 1.0, 0.2), 0.75*Graph(SinSmootherstep(tex.x) - tex.y, 0.5));       
	#endif



    #if 1
    vec2 s = (uv0/iResolution.xy*2.0-1.0);
    s.x = 1.0-Pow2(s.x);    s.y = 1.0-Pow2(s.y);
    col *= mix(1.0, 0.4, Pow2(1.0-sqrt(s.x*s.y)));
    #endif

    outCol = vec4(GammaEncode(clamp01(col)), 1.0);    

}

