// Experiment with these parameters for variants
const float size  = 0.75 ;
const float offc  = 1.05;
const float width = 0.0125;
const int   rep   = 15 ;

#define PHI   (.5*(1.+sqrt(5.)))
#define PI    3.141592654
#define TAU   (2.0*PI)
#define TTIME (TAU*iTime)

const vec3 plnormal = normalize(vec3(1, 1, -1));
const vec3 n1 = normalize(vec3(-PHI,PHI-1.0,1.0));
const vec3 n2 = normalize(vec3(1.0,-PHI,PHI+1.0));
const vec3 n3 = normalize(vec3(0.0,0.0,-1.0));

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float dodec(in vec3 z) {
  vec3 p = z;
  float t;
  z = abs(z);
  t=dot(z,n1); if (t>0.0) { z-=2.0*t*n1; }
  t=dot(z,n2); if (t>0.0) { z-=2.0*t*n2; }
  z = abs(z);
  t=dot(z,n1); if (t>0.0) { z-=2.0*t*n1; }
  t=dot(z,n2); if (t>0.0) { z-=2.0*t*n2; }
  z = abs(z);

  float dmin=dot(z-vec3(size,0.,0.),plnormal);

  dmin = abs(dmin) - width*7.5*(0.55 + 0.45*sin(10.0*length(p) - 0.5*p.y + TTIME/9.0));

  return dmin;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

float df(vec2 p) {
  float d = 100000.0;
  float off = 0.30  + 0.25*(0.5 + 0.5*sin(TTIME/11.0));
  for (int i = 0; i < rep; ++i) {
    vec2 ip = p;
    rot(ip, float(i)*TAU/float(rep));
    ip -= vec2(offc*size, 0.0);
    vec2 cp = ip;
    rot(ip, TTIME/73.0);
    float dd = dodec(vec3(ip, off*size));
    float cd = length(cp - vec2(0.25*sin(TTIME/13.0), 0.0)) - 0.125*size;
    cd = abs(cd) - width*0.5;
    d = pmin(d, dd, 0.05);
    d = pmin(d, cd, 0.025);
  }
  return d;
}

vec3 postProcess(vec3 col, vec2 q, vec2 p) {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  const float r = 1.5;
  float d = max(r - length(p), 0.0)/r;
  col *= vec3(1.0 - 0.25*exp(-200.0*d*d));
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord.xy / iResolution.xy;
  vec2 p = 2.0*(q - 0.5);
  p.x *= iResolution.x/iResolution.y;
  float d = df(p);

  float fuzzy = 0.0025;

  vec3 col = vec3(0.0);

  const vec3 baseCol = vec3(240.0, 175.0, 20.0)/255.0;

  col += 0.9*baseCol*vec3(smoothstep(fuzzy, -fuzzy, d));

  vec3 rgb = 0.5 + 0.5*vec3(sin(TAU*vec3(50.0, 49.0, 48.0)*(d - 0.050) + TTIME/3.0));

  col += baseCol.xyz*pow(rgb, vec3(8.0, 9.0, 7.0)); 
  col *= 1.0 - tanh(0.05+length(8.0*d));

  float phase = TAU/4.0*(-length(p) - 0.5*p.y) + TTIME/11.0;

  float wave = sin(phase);
  float fwave = sign(wave)*pow(abs(wave), 0.75);

  col = abs(0.79*(0.5 + 0.5*fwave) - col);
  col = pow(col, vec3(0.25, 0.5, 0.75));
  col = postProcess(col, q, p);

  fragColor = vec4(col, 1.0);
}

// Rotation
#define R(p,a) p=cos(a)*p+sin(a)*vec2(-p.y,p.x);
#define kPI 3.141592

// maximum iteration count
#define kMAXITERS 100
#define kEPSILON 0.001

float dist(in vec3 p){
    // Rotate
    R(p.xz, iTime*.3);
    // Scale Z to flatten
    p.z *= 1.7;
    // Shift on Y to form heart
    //p.y -= smoothstep(0., 1., abs(p.x)) / 2.;
    //p.y -=pow(abs(p.x)/2.,1.7)*1.3;
    p.y -=pow(abs(p.x)/2.,-p.y*.3+1.5)*1.3;
    // Scale X to widen
    p.x *=.8;
 	return length(p) - 1.5;   
}

// Gets the normal
vec3 normal(in vec3 p) {
    vec2 eps = vec2(0.0001, 0);
    float baseDist = dist(p);
 	return normalize(vec3(
        dist(p + eps.xyy) - baseDist,
        dist(p + eps.yxy) - baseDist,
        dist(p + eps.yyx) - baseDist
        ));
}

// Calulcate a fresnel term for reflections
float fresnelTerm(in vec3 d, in vec3 n, in float power) {
	float fresnel = min(1., dot(d, n) + 1.0);
	fresnel = pow(fresnel, power);
    return fresnel;
}

vec4 iC(vec3 p, vec3 d,vec3 ba, vec3 bb){
	vec3 a=(ba-p)/d, // near
	b=(bb-p)/d, // far
	f=max(a,b), // furthest
	n=min(a,b); // nearest
	float x=min(f.x,min(f.y,f.z)), // furthest plane
	dist=max(n.x,max(n.y,n.z)), // nearest plane
	o=dist<0.?x:dist; // nearest in front
	//if(dist>=x||o<0.) return vec4(0,0,0,-100.); // d>=x = invalid, o>t = behind other geometry, o<0 behind

	vec3 normal = normalize(step(kEPSILON,abs(a-dist))-step(kEPSILON,abs(b-dist)))*sign(dist);
	//h.m=m;
    return vec4(normal, dist);
}

vec3 bkg(in vec3 p, in vec3 d, bool heart, vec2 uv) {
    vec4 box = iC(p, d, vec3(-100.), vec3(100.));
    p += d * box.w;
    if (heart) {
    	//float spec = (p.y < -99. && p.x>10. && abs(p.z+30.) < 30.) ? 1. : 0.;
    	float spec = p.y > -99. ? 0. : 
        (1.-smoothstep(40., 50., abs(p.x-50.))) * (1.-smoothstep(30., 40., abs(p.z+30.)));
        //    && p.x>10. && abs(p.z+30.) < 30.) ? 1. : 0.;
        return vec3(1,.2,.2) * (dot(d, normalize(vec3(3,2,-1)))*.3+.7) + spec;
    }

    vec3 col = vec3(d.y *.5 + .5);

    return col;
  //  return abs(box.zxy); 
}

// The main marching loop
vec3 marchRay(in vec3 p, in vec3 d, vec2 uv) {
    float td = 0.;

    for (int i=0; i<kMAXITERS; i++) {
        // Get distance to nearest surface
        float result = dist(p);
        td += result;
        if ((p.z) > 3.0) { break; }

        p += d * result * 0.6;

        if (result < kEPSILON * td) {
            // colision
            // normal
            // Get the normal, then clamp the intersection to the surface
    		vec3 n = normal(p);
            d = reflect(d, n);
            return bkg(p, d, true, uv);
        }
    }
    // Ray did not hit, background
    vec3 col = bkg(p, d, false, uv)*.6 + vec3(.2, .3, 1.);

	col = col*pow(clamp(0., 1., 1.2-length(uv)),.75);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    vec2 uv2 = uv;
    uv.y *= iResolution.y/iResolution.x;
    vec3 cp = vec3(0,.5,-4.5);
    vec3 rd = normalize(vec3(uv, 1)); 

    // Time varying pixel color
    vec3 col = marchRay(cp, rd, uv2);
	//col = col*pow(clamp(0., 1., 1.-length(uv2)),.75);
    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Tiling the Poincare Disc II
//
// Construct P,Q,R hyperbolic triangle, centred at the origin, with
// straight sides at angle PI/P at origin, and a circular side
// making angles PI/Q and PI/R with the first two sides. Fold into
// triangle by iterated reflection in its sides (so the triangle is a
// fundamental region for the group of reflections).
//
// Lots of options:
// 1,2,3: show different edge types
// a: alternative palette
// c: clip to unit disc
// h: halfplane
// i: colour by index
// m: mouse isometry
// n: null rotation
// p: progressive folding
// s: show sequence
// t: show parity
// z: exponential zoom out (halfplane only)
//
// This one does the folds in layers, so we can index and colour the
// tiles according to what layer they are in.
//
// Any P,Q,R with 1/P + 1/Q + 1/R < 1 can be used, and Q and R may be
// set to 0, which actually means "infinity", ie. a dihedral angle of
// 0 (so the triangle points are ideal).
//
// This one really shows up any inaccuracy in the GPUs cos and sin,
// so we use a table of cos(PI/n) for n = 0 up to 15. Note that the
// table has cos(PI/0) = 1.0 as 0 really means "infinity" here.
//
////////////////////////////////////////////////////////////////////////////////

int P = 5, Q = 2, R = 4;
int NSTEPS = 100; // Max number of folding steps
int PSTEPS = 50; // Number of "progressive" steps
int CYCLETIME = 2; // Time to show each triangle
bool dotwotone = true;       // Show parity of fundamental regions
bool doedge1 = true;         // Show x-axis edge
bool doedge2 = true;         // Show y-axis edge
bool doedge3 = true;         // Show circular edge
bool donullrotation = false; // Do a "null rotation"
bool domouse = true;         // Invert origin to mouse position
bool dozoom = false;         // Zoom out, in halfplane mode only
bool dohalfplane = false;    // Show the halfplane model, instead of the disc
bool doprogressive = false;   // Progressively increase max number of folds
bool doclip = true;         // Clip to unit disc
bool dosequence = true;      // Show sequence of tilings
bool doindexing = true;      // Use index for colour
bool dopalette = false;      // Use alternative palette

float PI = 3.141592654;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

// Approximate equality. Used for assertion checks.
// Maybe use relative error here?
bool eq(float a, float b) {
  return abs(a-b) < 1e-4;
}

float dihedral(vec2 n, vec2 m) {
  return -dot(n,m);
}

// The dihedral angle between a plane and a spherical face. If d is
// the perpendicular distance to plane, and s the sphere radius, then
// angle is acos(d/s)
float dihedral(vec2 n, vec2 C, float s) {
  return dot(C,n)/s;
}

float COS[] =
  // Look who doesn't trust cos on the GPU: cos(PI/n) for n = 0..31
  float[](1.0,-1.0,0.0,0.5,0.7071067812,0.8090169944,0.8660254038,
          0.9009688679,0.9238795325,0.9396926208,0.9510565163,0.9594929736,
          0.9659258263,0.9709418174,0.9749279122,0.9781476007,0.9807852804,
          0.9829730997,0.984807753,0.9863613034,0.9876883406,0.9888308262,
          0.9898214419,0.990685946,0.9914448614,0.9921147013,0.9927088741,
          0.9932383577,0.9937122099,0.9941379572,0.9945218954,0.9948693234);

float mycos(int p) {
  // cos(PI/p)
  if (p < COS.length()) return COS[p];
  return cos(PI/float(p));
}

float mysin(int p) {
  // sin(PI/p)
  float t = mycos(p);
  return sqrt(1.0-t*t);
}

vec2 A,B,C; // Fundamental mirrors, C is circle centre
float S,S2; // Circle radius and squared radius
// Constructing the fundamental region:
//
// A and B have dihedral p, then want a circle (C,s) such that:
// dihedral(A,C,s) = r
// dihedral(B,C,s) = q
// (q and r this way round for aesthetic reasons).
//
// Since the triangle can be any size, we solve for s = 1 and then scale:
// dot(C,A) = cos(r)
// dot(C,B) = cos(q)
// or, with matrices:
// transpose(mat2(A,B))*C = vec2(cos(r),cos(q))
//
// How the final scaling works is left as an exercise for the reader.
// We could optimize a little since m has a simple form, but it's neat
// doing it like this and the compiler might well do the optimization
// for us (fairly simple inlining and constant folding would do).
void solve(int p, int q, int r) {
  // Use the y-axis for the first side, for bilateral symmetry.
  A = vec2(1,0);
  B = vec2(-mycos(p),mysin(p));
  mat2 m = inverse(mat2(A,B));
  C = vec2(mycos(r),mycos(q))*m; // multiply by transpose
  // and set the correct radius = 1.0
  S2 = 1.0/(dot(C,C)-1.0);
  S = sqrt(S2);
  C *= S;
  assert(eq(dihedral(A,B),mycos(p)));
  assert(eq(dihedral(B,C,S),mycos(q)));
  assert(eq(dihedral(A,C,S),mycos(r)));
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 background = vec3(0.1);

vec3 diagram(vec2 z, vec2 mouse) {
  assert(P >= 2);
  assert(Q == 0 || Q >= 2);
  assert(R == 0 || R >= 2);
  assert(Q == 0 || R == 0 || (Q*R + R*P + P*Q) < P*Q*R);
  solve(P,Q,R);
  if (dohalfplane) {
    z.y += 1.0;
    if (dozoom) z *= exp(iTime);
    z.y += 1.0;
    z *= 2.0/dot(z,z);
    z.y -= 1.0;
  }
  // Map screen coordinate at mouse position to origin
  // We do this by inverting in an imaginary circle centred
  // at the mouse position to take the mouse to infinity,
  // then invert in the imaginary unit circle to bring it
  // back to the origin. Both circles are orthogonal to the
  // (real) unit circle, and so are hyperbolic lines and
  // the inversions are isometries.
  if (domouse && mouse != vec2(0)) {
    vec2 c = mouse;
    z -= c;
    z *= (dot(c,c)-1.0)/dot(z,z);
    z += c;
    z *= -1.0/dot(z,z);
  }
  if (donullrotation) {
    // One ideal fixed point: do with reflection in x = 0 and an
    // inversion in a circle going through (0,1).
    float theta = PI*iTime/float(CYCLETIME);
    if (theta != 0.0) {
      z.x = -z.x;
      float r = 1.0/tan(theta);
      r = (r<0.0?-1.0:1.0)*max(1e-2,abs(r));
      vec2 c = vec2(r,1);
      z -= c;
      z *= r*r/dot(z,z);
      z += c;
    }
  }
  if (dot(z,z) > 1.0) {
    if (doclip) return background;
    z /= dot(z,z); // Map exterior to interior if displaying.
  }
  int nsteps = NSTEPS, folds = 0, index = -1;
  if (doprogressive) nsteps = 1+int(iTime)%PSTEPS;
  for (index = 0; index < nsteps; index++) {
    if (P == 2) {
      // This optimization may not be worth it.
      folds += int(z.x < 0.0) + int(z.y < 0.0);
      z = abs(z);
    } else {
      // Enough folds to do the central region
      for (int i = 0; i < (P+1)/2; i++) {
        // A is (1,0) so maybe optimize this, or again, just let the
        // compiler do it.
        {
          float t = dot(z,A);
          if (t < 0.0) {
            z -= 2.0*t*A;
            folds++;
          }
        }
        {
          float t = dot(z,B);
          if (t < 0.0) {
            z -= 2.0*t*B;
            folds++;
          }
        }
      }
    }
    {
      vec2 z1 = z-C;
      float t = dot(z1,z1);
      if (t >= S2) break;
      z1 *= S2/t;
      z = z1+C;
      folds++;
    }
  }
  if (index == nsteps) return background;
  // We have now folded the original point z into the fundamental
  // region, now draw whatever should be in there. Things get smaller
  // as we move outwards in the disc, so need a scale factor.
  float scalefactor = abs(1.0 - dot(z,z));
  // Euclidean distances to edge of fundamental regions
  float d1 = abs(dot(z,A));
  float d2 = abs(dot(z,B));
  float d3 = abs(length(z-C)-S);
  // Desired widths, appropriately scaled.
  float ewidth = 0.5*scalefactor*0.02;
  float lwidth = 0.5*scalefactor*0.02;
  vec3 col = vec3(0,1,1);
  if (doindexing) {
    if (dopalette) col = 0.5 + 0.5*cos(float(index)*PI/3.0 + vec3(2, 1, 0));
    else col = hsv2rgb(vec3(float(index)/6.0,1,1));
  }
  // Haven't worked out how to do smooth colour transition yet.
  // If nearest to edge 1 or 2, then same colour, else
  // it's either colour index+1 or index-1 depending on what?
  if (dotwotone && folds%2 != 0) col *= 0.5;
  if (doedge1 || doedge2 || doedge3) {
    // Draw the edges of the region, if desired.
    float d = 1e8;
    if (doedge1) d = min(d,d1);
    if (doedge2) d = min(d,d2);
    if (doedge3) d = min(d,d3);
    col = mix(vec3(0),col,smoothstep(0.0,lwidth/*+fwidth(z.x)*/,d));
  }
  assert(!isnan(dot(col,col)));
  return col;
}

bool key(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

