vec4 discoStrip( float x )
{
    float patternLength = 30.0;
    float timePos = mod(iTime,patternLength);

    float pattern = mod((iTime / patternLength),6.0);

    float c = clamp(sin(iTime*4.0 + x)*5.0,0.0,1.0);
    vec4 color = vec4(c*0.7,c*0.5,c,1);

    if (pattern>=1.0)
        color = iqColor(vec3(0.5),vec3(0.5),vec3(1.0),vec3(0.00, 0.33, 0.67) + vec3(x)/5.0, iTime) * vec4(1.3,0.8,0.2,1.0);;
    if (pattern>=2.0)
        color = iqColor(vec3(0.5),vec3(0.5),vec3(1.0, 1.0, 0.5),vec3(0.80, 0.90, 0.30) + vec3(x)/6.0, iTime);
    if (pattern>=3.0)
        color = vec4(sin(iTime*5.0 + x)*0.5+0.5,cos(iTime*5.0 + x)*0.7+0.3,cos(iTime*7.0 + x)*0.3+0.5,1);
    if (pattern>=4.0)
        color = iqColor(vec3(0.5),vec3(0.5),vec3(1.0,1.0,0.5),vec3(0.8,0.90,0.30) + vec3(x)/3.0*1.5, iTime) * vec4(0.3,0.8,1.5,1.0);
    if (pattern>=5.0)
        color = iqColor(vec3(0.5),vec3(0.5),vec3(1.0, 1.0, 0.5),vec3(0.80, 0.90, 0.30) + vec3(x)/6.0, iTime) * vec4(0.7,1.5,0.3,1.0);/**/

    float fade = clamp(-(abs(timePos-patternLength/2.0)-patternLength/2.0)-0.5,0.0,1.0);

    return color*fade;
}

vec4 dotPattern( vec2 d )
{   
    d-=vec2(11.0);

    d=mod(d,40.0);
    if (d.x>=20.0) d.x=39.0-d.x;
    if (d.y>=20.0) d.y=39.0-d.y;

    if (d.x-0.0 == 0.0 )
        return discoStrip( d.y );
    if (d.x-19.0 == 0.0 )
        return discoStrip( d.y );

    if (d.y-0.0 == 0.0 )
        return discoStrip( d.x );
    if (d.y-19.0 == 0.0 )
        return discoStrip( d.x );

    return vec4(0.0);
}

vec4 discoPattern( vec2 pos )
{    
    pos+=vec2(edgeSize*0.5);
    vec2 idx = pMod2(pos,vec2(edgeSize*1.0));
    float l = length(pos)*3.0;

    vec4 dotColor = clamp(dotPattern(idx),vec4(0.0),vec4(1.0));

    return l<edgeSize ? dotColor : vec4(0);
}

vec4 disco(vec3 pos, vec3 dir)
{
	vec3 n=normalize(vec3(sdf2(pos+vec3(0.01,0,0))-sdf2(pos+vec3(-0.01,0,0)),
						  sdf2(pos+vec3(0,0.01,0))-sdf2(pos+vec3(0,-0.01,0)),
						  sdf2(pos+vec3(0,0,0.01))-sdf2(pos+vec3(0,0,-0.01))));

    n=abs(n);

    vec4 c = vec4(0);
    if (n.x > n.y && n.x > n.z)
        c=discoPattern(pos.yz);
    if (n.y > n.x && n.y > n.z)
        c=discoPattern(pos.xz);
    if (n.z > n.y && n.z > n.x)
        c=discoPattern(pos.xy);

    return c;
}

vec4 magicCube( vec3 pos, vec3 dir )
{
  vec3 oPos = pos;

  float dist = sdf2(pos+1.0);
  if (dist<0.000001)
  	return vec4(0);

  for (int x=0; x<100; x++)
  {
      dist = sdf2(pos+1.0);

      float distFade = max(0.0, 1.0-length(pos-oPos)*0.05 );

      if (distFade==0.0)
          break;

      if (dist<0.00001)
      {
          return mix(disco(pos, dir),vec4(1.0),1.0-distFade);
      }
      pos += dir * abs(dist);       
  }

  return vec4(1.0);
}

