float cubic(float x) { return x*x*(3.0-2.0*x); }

float Read(vec2 uv)
{
    float v = texelFetch(iChannel0, ivec2(uv), 0).x;

    return v;
}

  //  #define EMULATE_LOW_PRECISION

float texLoad(vec2 tex)
{
    vec2 uv = tex * iResolution.xy;

    uv += 0.5;

    vec2 uvI = floor(uv);
    vec2 uvF = fract(uv);

#ifdef EMULATE_LOW_PRECISION
    {
        float s = 256.0;
        uvF = round(uvF * s) / s;
    }
#endif

    float v = mix(mix(Read(uvI + vec2(0.0, 0.0)), Read(uvI + vec2(1.0, 0.0)), uvF.x),
                  mix(Read(uvI + vec2(0.0, 1.0)), Read(uvI + vec2(1.0, 1.0)), uvF.x), uvF.y);

#ifdef EMULATE_LOW_PRECISION
    {
        float s = 256.0;
        v = floor(v * s) / s;
    }
#endif

    return v;
}

