bool intersect(Ray ray)
{
Object ptr,p;
int j;
bool inter=false,i=false;
float t;
vec3 pt,gpts;
float mint=MAXFLOAT;
Rgb col,colm;

for (j=0;j<numobj;j++)
	{
	ptr=objs[j];
	if (ptr.type==SPHERE) { i=sphereinter(ptr,ray); }
	else if (ptr.type==PLANE) { i=triinter(ptr,ray); }
	else { i=false; }
	if (i)
		{
		inter=true;
		if (gt<mint)
			{
			p=ptr; mint=gt; gpts=gpt;
			colm=ptr.diff;
			}
		}
	}
if (inter)
	{
	hit.object=p;
	hit.t=mint;
	gpt=gpts;
	if (p.type==SPHERE) { hit.normal=spherenorm(p,ray); }
	else if (p.type==PLANE) { hit.normal=trinorm(p,ray); }
	hit.pt=gpt;
	if (p.type==PLANE)
		{
		int ibx,ibz;

		ibx=int(gpt.x+textoff);
		ibz=int(gpt.z+textoff);
		if (((ibx+ibz)%2)==1) { colm.r=colm.g=colm.b=128.0; }
		else { colm.r=colm.g=colm.b=255.0; }
		}
	hit.col=colm;
	}
return(inter);
}

Rgb trace(Ray ray,int level)
{
vec3 pt,N;
Object obj;

ray.v=normalize(ray.v);
if (intersect(ray))
	{
