#define kernelSize 39
#define halfKernelSize 19

#define blurStrength 1.0
#define blurEdge 0.3

vec3 fastSurfaceBlur( sampler2D inputColor, sampler2D inputEdge, vec2 uv, vec2 blurStep)
{
	// Normalized gauss kernel
	float blurKernel[kernelSize] = float[kernelSize](
		1.9576873456045e-10,
        2.6102497941393e-09,
        2.5449935492859e-08,
        1.9341950974573e-07,
        1.192753643432e-06,
        6.134161594793e-06,
        2.6836956977219e-05,
        0.00010138405969172,
        0.00033456739698267,
        0.00097328697304049,
        0.0025143246803546,
        0.0058022877238952,
        0.012019024570926,
        0.022435512532395,
        0.037859927398416,
        0.057903418374049,
        0.080421414408401,
        0.10158494451587,
        0.11682268619326,
        0.12238567125008,
        0.11682268619326,
        0.10158494451587,
        0.080421414408401,
        0.057903418374049,
        0.037859927398416,
        0.022435512532395,
        0.012019024570926,
        0.0058022877238952,
        0.0025143246803546,
        0.00097328697304049,
        0.00033456739698267,
        0.00010138405969172,
        2.6836956977219e-05,
        6.134161594793e-06,
        1.192753643432e-06,
        1.9341950974573e-07,
        2.5449935492859e-08,
        2.6102497941393e-09,
        1.9576873456045e-10
   	);

	vec3 result = blurEdge*blurKernel[halfKernelSize]*texture(inputColor,uv).rgb;
	float norma = blurEdge*blurKernel[halfKernelSize];

	float mainEdge = texture(inputEdge,uv).x;

	// Right direction
	float weight = blurEdge;
	for(int i = 1; i<halfKernelSize; i++){
		vec2 currentPos = uv+float(i)*blurStep;

		weight-=abs(texture(inputEdge,currentPos).x-mainEdge)/blurStrength;
		if(weight<=0.0) continue;

		float coef = weight*blurKernel[halfKernelSize+i];
		result+=coef*texture(inputColor,currentPos).rgb;
		norma+=coef;
	}
	// Left direction
	weight = blurEdge;
	for(int i = 1; i<halfKernelSize; i++){
		vec2 currentPos = uv-float(i)*blurStep;

		weight-=abs(texture(inputEdge,currentPos).x-mainEdge)/blurStrength;
		if(weight<=0.0) continue;

		float coef = weight*blurKernel[halfKernelSize-i];
		result+=coef*texture(inputColor,currentPos).rgb;
		norma+=coef;
	}
    return result/norma;
}

