float remap01(float minInput, float maxInput, float domain)
{
    return clamp( (domain - minInput) / (maxInput - minInput), 0., 1.); 
}

float remap(float minInput, float maxInput, float minOutput, float maxOutput, float domain)
{
    //return minOutput * (1 - remap01) + maxOutput * remap01;
    return remap01(minInput, maxInput, domain) * (maxOutput - minOutput) + minOutput;
}

vec2 bound(vec2 uv, vec4 boundingBox)
{
	return (uv - boundingBox.xy) / (boundingBox.zw - boundingBox.xy);
}

vec4 Head (vec2 uv)
{
   	//Constants
    float len = length(uv);

    vec4 mainCol = vec4(.9, .7, 0., 1.);
    vec3 outlineCol = vec3(0.75, 0.5, 0.);
    vec3 cheekCol = vec3(1., 0.1, 0.1);

    float radius = 0.5;
    float cheekRadius = 0.2;

    float blur = 0.01;
    float shadingAttenuation = 0.5;

    //mainCircle
    mainCol.a = smoothstep(radius, radius - blur, len);

    //Adding radial gradient
    float edgeShading = remap01(radius / 1.5, radius, len);
    edgeShading *= edgeShading;
    mainCol.rgb *= 1. - edgeShading * shadingAttenuation;

    //Adding outline
    mainCol.rgb = mix(mainCol.rgb, outlineCol, smoothstep(radius - (blur * 3.), radius - (blur * 2.), len));

    //Adding highlights
    float highlight = smoothstep(radius - (blur * 9.), radius - (blur * 9.5), len);
    highlight *= remap(radius - (blur * 9.), -0.1 , 0.75, 0., uv.y);
    mainCol.rgb = mix(mainCol.rgb, vec3(1.), highlight);

    //Adding cheeks
    len = length(uv - vec2(.225, -.175));
    float cheek = smoothstep(cheekRadius, cheekRadius * .2, len)  * .3;
    cheek *= smoothstep( cheekRadius - (cheekRadius * 3.), cheekRadius - (cheekRadius * 2.), len);
    mainCol.rgb = mix(mainCol.rgb, cheekCol, cheek);

 	return mainCol;
}

vec4 Eye(vec2 uv)
{
    uv -= .5;
    //uv.y += uv.x * uv.x * cos(iTime); 

    float len = length(uv);
    float eyeRadius = 0.5;
    float irisRadius = 0.3 ;
    float pupilRadius = 0.15 * ( cos(iTime)   *0.5 + 1.25 );
    float highlightRadius = 0.1;
    float blur = 0.02;

    //base eye
    vec3 irisCol = vec3(0.25, 0.25, 0.75);
    vec4 mainCol = mix( vec4(1.) , vec4(irisCol, 1.) , smoothstep(.1, .7, len) * 0.5);
    vec3 highlightCol = vec3(1.);

    //eye shadow
    mainCol *= 1. - smoothstep(eyeRadius - (blur * 5.), eyeRadius, len) * .3 * clamp(-uv.y -uv.x, 0., 1.);

    //iris and outline
    irisCol *= 1. + smoothstep(irisRadius, irisRadius * 0.1, len);

    mainCol.rgb = mix(mainCol.rgb, vec3(0.), smoothstep(irisRadius, irisRadius - blur, len));
    mainCol.rgb = mix(mainCol.rgb, irisCol, smoothstep(irisRadius - blur, irisRadius - (blur * 2.), len));

    //pupil
    mainCol.rgb = mix(mainCol.rgb, vec3(0.), smoothstep(pupilRadius, pupilRadius - blur, len));

    //highlighs
    float highlight = smoothstep(highlightRadius, highlightRadius - (blur * 3.), length(uv - vec2(-.15, .15)));
    highlight += smoothstep(highlightRadius/1.5, highlightRadius/1.5 - (blur * 3.), length(uv - vec2(.1, -.1)));
    mainCol.rgb = mix(mainCol.rgb, highlightCol, highlight);

    //
    mainCol.a = smoothstep(eyeRadius, eyeRadius - blur, len);	
    return mainCol;
}

vec4 Mouth(vec2 uv)
{
    uv -= .5;
    uv.y *= ( cos(iTime ) * 0.25 + 1.75);//2, 1,5, 
    uv.x *= ( cos(iTime + 3.) * 0.5 + 1.5) ; //2, 1
    uv.y -= uv.x * uv.x * (cos(iTime) + 1.);
    float len = length(uv);

    vec4 mainCol = vec4(.5, .2, .1, .1);
    float mouthRadius = 0.5;
    float blur = 0.02;

    //teeth
    float newLen = length(uv - vec2(0, 0.5));
    vec3 teethCol = vec3(1.) * smoothstep(.6, .35, len);

    mainCol.rgb = mix(mainCol.rbg, teethCol, smoothstep(.4, .4 - blur, newLen));

    //tongue
    newLen = length(uv - vec2(0, -0.5));
    vec3 tongueCol = vec3(1., 0.1, 0.1);

   	mainCol.rgb = mix(mainCol.rbg, tongueCol, smoothstep(.5, .1, newLen));

    mainCol.a = smoothstep(mouthRadius, mouthRadius - blur, len);
    return mainCol;
}

vec4 Smiley (vec2 uv)
{
    vec4 col = vec4(0.);

    uv.x = abs(uv.x);

	vec4 head = Head(uv);
    vec4 eye = Eye( bound(uv, vec4(.03, -.1, .37, .25)) );
    vec4 mouth = Mouth( bound(uv, vec4(-.3, -.4, .3, -.1)) );

    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);

    return col;
}

