        float idc = floor(max(q.x,q.y)/ modD + iTime*4.*0.25);



        if(d.y == 2. || d.y == 1.){
            //col += pal(0.5,1., vec3(.7,0.1,-0.3),0.8,idc*1.5 + idG.y*0.5 + iTime*0.1);
            col += pal(0.,0.9, vec3(.7,0.1,-0.3),0.8,idc*1.5 + length(p.xz)*1. + iTime*1.);


