    }


    col += glow*0.005;

    //col = pow(col, vec3(0.454545));

    col = mix(col,vec3(0.06,0.02,0.05), smoothstep(0.,1.,t*0.04));

    //col *= 1. - dot(uv,uv)*0.7;

