/*
Visualization of the basins of attraction of an alternating gradient descent
to two different algebraic curves.
This iteration can be used to find the intersections of these curves.

If the curves have intersections, points near these intersections converge
to these intersections, but there are also other points of attraction sometimes.

For every pixel, the color is choosen according to the point
to which the pixel coordinate converges under the iteration,
quite similar to newton fractals.

The name i've choosen comes from the related Bezout's Theorem
(see https://en.wikipedia.org/wiki/B%C3%A9zout%27s_theorem)
and because it's an alliteration, of course!

You can play around with different curve combinations below.

If you uncomment MOUSE_ITERATION, the point to which the
current mouse position converges is shown.
*/

float fac;
const float pi=3.14159265358979;

const int num_iterations=24;
const int aa=2;

//#define MOUSE_ITERATION

#define CURVE1(p,grad) saddle(p,grad)
#define CURVE2(p,grad) circle(p,grad)

//#define CURVE1(p,grad) parabola(p,grad)
//#define CURVE2(p,grad) circle(p,grad)

//#define CURVE1(p,grad) trifoil(p*fac,grad);grad*=fac
//#define CURVE2(p,grad) deltoid(p,grad)

//#define CURVE1(p,grad) deltoid(p,grad)
//#define CURVE2(p,grad) deltoid(p*vec2(-1,1)*fac,grad);grad*=fac*vec2(-1,1)

//#define CURVE1(p,grad) lemniscate(p,grad)
//#define CURVE2(p,grad) parabola(p,grad)

//#define CURVE1(p,grad) circle(p,grad)
//#define CURVE2(p,grad) bicorn(p,grad)

//#define CURVE1(p,grad) devil(p,grad)
//#define CURVE2(p,grad) lemniscate(p,grad)

//#define CURVE1(p,grad) kappa(p,grad)
//#define CURVE2(p,grad) deltoid(p,grad)

//#define CURVE1(p,grad) neile(p,grad)
//#define CURVE2(p,grad) lemniscate(p,grad)

//#define CURVE1(p,grad) trisectrix(p.yx,grad);grad=grad.yx
//#define CURVE2(p,grad) parabola(p,grad)

//#define CURVE1(p,grad) ampersand(p.yx*fac,grad);grad=grad.yx*fac
//#define CURVE2(p,grad) bicorn(p*vec2(1,-1),grad);grad*=vec2(1,-1)

//#define CURVE1(p,grad) saddle(p,grad)
//#define CURVE2(p,grad) squircle(p*fac,grad);grad=grad*fac

//#define CURVE1(p,grad) hyperbola(p,grad)
//#define CURVE2(p,grad) saddle(p,grad)

float hyperbola(vec2 p, out vec2 grad){
	grad = p.yx;

	return p.x*p.y-fac*fac;
}

float squircle(vec2 p, out vec2 grad){
	float x_2 = p.x*p.x;
	float y_2 = p.y*p.y;

	grad = 4.*vec2(x_2*p.x,y_2*p.y);

	return x_2*x_2+y_2*y_2-.27;
}

float ampersand(vec2 p, out vec2 grad){
	float x_2 = p.x*p.x;
	float y_2 = p.y*p.y;

	grad=vec2(((-24.*p.x+63.)*p.x-38.-12.*y_2)*p.x+11.*y_2,(-16.*y_2+(-12.*p.x+22.)*p.x+6.)*p.y);

	float tmp=(x_2+y_2-2.*p.x);
	return (y_2-x_2)*(p.x-1.)*(2.*p.x-3.)-4.*tmp*tmp;
}

float trisectrix(vec2 p, out vec2 grad){
	float x_2 = p.x*p.x;
	float y_2 = p.y*p.y;

	grad=2.*vec2((p.x-fac)*3.*p.x+y_2,(fac+2.*p.x)*p.y);

	return 2.*p.x*(x_2+y_2)-fac*(3.*x_2-y_2);
}

float trifoil(vec2 p, out vec2 grad){
	float y_2=p.y*p.y;

	grad=vec2(((4.*p.x-3.)*p.x+4.*y_2)*p.x+3.*y_2,(4.*y_2+(4.*p.x+6.)*p.x)*p.y);

    return (((p.x - 1. )*p.x+2.*y_2)*p.x+3.*y_2)*p.x + y_2*y_2;
}

float deltoid(vec2 p, out vec2 grad){
	float y_2=p.y*p.y;

	grad = vec2(((-4.*p.x+24.)*p.x+(-4.*y_2-36.))*p.x -24.*y_2,  (-4.*y_2 + (-4.*p.x-48.)*p.x -36.  ) * p.y);

	return (((-p.x+8.)*p.x-18.-2.*y_2)*p.x-24.*y_2)*p.x + (-y_2-18.)*y_2+27.;
}

float circle(vec2 p, out vec2 grad){
	grad = vec2(2.*p.x,2.*p.y);
	return p.x*p.x+p.y*p.y-.27*.27;
}

float saddle(vec2 p, out vec2 grad){
	grad = vec2(fac*3.*p.x*p.x,-1.);
	return fac*p.x*p.x*p.x-p.y;
}

float parabola(vec2 p, out vec2 grad){
	grad = vec2(2.*p.x,-1.);
	return p.x*p.x-p.y;
}

float kappa(vec2 p, out vec2 grad){
	float x_2=p.x*p.x;
	float y_2=p.y*p.y;

	grad = vec2((2.*x_2+y_2)*2.*p.x,(x_2-fac*fac)*2.*p.y);
	return x_2*(x_2+y_2) - fac*fac * y_2;
}

float neile(vec2 p, out vec2 grad){
	grad = vec2(-3.*fac*fac*p.x*p.x,2.*p.y);
	return p.y*p.y-fac*fac*p.x*p.x*p.x;
}

float lemniscate(vec2 p, out vec2 grad){
	float x_2 = p.x*p.x;
	float y_2 = p.y*p.y;

	grad = vec2((x_2+(y_2-fac*fac))*4.*p.x,(y_2+x_2+fac*fac)*4.*p.y);

	return (x_2+y_2)*(x_2+y_2)-2.*fac*fac*(x_2-y_2);
}

float bicorn(vec2 p, out vec2 grad){
	float x_2=p.x*p.x;
	float y_2=p.y*p.y;

	grad = vec2((-2.*x_2+(2.*fac-4.*p.y)*fac-y_2)*p.x,((2.*fac-3.*p.y)*fac-2.*x_2)*fac-x_2*p.y)*2.;

	float tmp=x_2+2.*fac*p.y-fac*fac;
	return y_2*(fac*fac-x_2)-tmp*tmp;
}

float devil(vec2 p, out vec2 grad){
	float x_2=p.x*p.x;
	float y_2=p.y*p.y;

	grad = 2.*vec2((-2.*x_2+1.)*p.x,(2.*y_2-fac*fac)*p.y);

	return (-x_2+1.)*x_2+(y_2-fac*fac)*y_2;
}

mat2 rot(float angle){
	return mat2(cos(angle),sin(angle),-sin(angle),cos(angle));
}

vec2 iteration(vec2 p){
	float p_val1, p_val2;
	vec2 p_grad1, p_grad2;

	p_val1=CURVE1(p,p_grad1);
	p-=(p_grad1*p_val1)/dot(p_grad1,p_grad1);

	p_val2=CURVE2(p,p_grad2);
	p-=(p_grad2*p_val2)/dot(p_grad2,p_grad2);

	return p;
}

float spectral(float x){
        return clamp(abs(mod((x/pi+1.)*4.,8.)-4.)-2.,-.75,.75)*.5/.75+.5;
}

vec3 to_col(vec2 z){
	float direction =atan(z.x,z.y);
	return vec3(spectral(direction),spectral(direction+pi/2.),spectral(direction-pi));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float aspect_ratio = iResolution.x / iResolution.y;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= aspect_ratio;

	vec2 mouse = (iMouse.xy+1.) / iResolution.xy;
	mouse-=.5;
	mouse.x *= aspect_ratio;

	vec2 mouse2 = iMouse.zw / iResolution.xy;
	mouse2-=.5;
	mouse2.x *= aspect_ratio;

	const float curve_width = .001;
	const float dot_size = .005;

	const float axis_width = .005;

	vec3 bg_col = vec3(0);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 second_curve_col = vec3(0,1,1);
	const vec3 dot_col = vec3(1,0,0);

    float border = 2./iResolution.y;

	#ifdef MOUSE_ITERATION
	fac=exp(mouse2.y*10.);
	float zoom = exp(mouse2.x*10.);

	vec2 p0=zoom*mouse;
	#else
    float zoom;
    if(iMouse.xy==vec2(0)){
        fac=7.1;
        zoom=1.;
    }
    else{
		fac=exp(mouse.y*10.);
		zoom = exp(mouse.x*10.);
    }
	#endif

	uv*=zoom;
	border*=zoom;

	float curve_dis = 1e38;
	float second_curve_dis = 1e38;
	float dot_dis = 1e38;
	float axis_dis = 1e38;

	const mat2 aa_rot=mat2(1.,.5,-.5,1.);

	vec3 col_sum=vec3(0);

	for(int x=0;x<aa;x++){
		for(int y=0;y<aa;y++){
			vec2 aa_offset=(vec2(x,y)-.5*(float(aa)-1.))/float(aa);
			aa_offset*=zoom*vec2(aspect_ratio,1)/iResolution.xy;
			vec2 p1=uv+aa_offset;

			for(int i=0;i<num_iterations;i++){
				p1=iteration(p1);
			}

			col_sum+=pow(to_col(p1),vec3(2.2));
		}
	}

	bg_col=pow(col_sum/(float(aa)*float(aa)),vec3(1.0/2.2));
	bg_col*=.75;

	#ifdef MOUSE_ITERATION
	dot_dis=min(dot_dis,distance(uv,p0)-dot_size*zoom);
	float p0_val1, p0_val2;
	vec2 p0_grad1, p0_grad2;
	for(int i=0;i<num_iterations;i++){
		p0=iteration(p0);
	}

	dot_dis=min(dot_dis,distance(uv,p0)-dot_size*zoom);
	#endif

	float val1, val2;
	vec2 grad1, grad2;

	val1=CURVE1(uv,grad1);
	curve_dis=min(curve_dis,abs(val1)/length(grad1));

	val2=CURVE2(uv,grad2);
	second_curve_dis=min(second_curve_dis,abs(val2)/length(grad2));

	axis_dis=min(axis_dis, abs(uv.x));
	axis_dis=min(axis_dis, abs(uv.y));

	vec3 color = bg_col;
	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(second_curve_col,color,smoothstep(0., border, second_curve_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));

	fragColor = vec4(color,1);
}

