void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	vec4 color = vec4(0, 0, 0, 1);
    float _Frame = float(iFrame);

    // uvs
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv05 = uv - 0.5;
    vec2 uva = abs(uv05);
    vec2 uvf = mod(floor(uva * iResolution.xy) + _Frame, 2.0);


    // directions
	vec2 topright = vec2(1, 1);
	vec2 bottomright = vec2(1, -1);

    // diagonal quadrant
	float dQuad = sign(uv05.x) * sign(uv05.y) * 0.5 + 0.5;
	float l = mix(uvf.x, uvf.y, dQuad);

    // direction depends on the quadrant
	vec2 dir = mix(topright, bottomright, dQuad);
	dir*= sign(uv05.y);

    // direction is nverted 1/2
	dir*= mod(floor(uv.y * iResolution.y) + _Frame, 2.0) * 2. - 1.;
    color.rg = dir;

    // blue channel defines the direction of the swap
	color.b = uv05.y < 0.0 ? 1.0 : 0.0;
    // we adjust the swap direction outside of the square to give a nice effect
	color.b+= round(uva.x + uva.y);
	if (color.b > 1.5) color.b = 0.0;
	color.b = 1. - color.b;

	// alpha channel defines if there is a sort or not on the line
	float isTopLeft = uv05.x > 0.0 && uv05.y > 0.0 ? 1.0 : 0.0;
	float nosort = isTopLeft;
	nosort*= uv05.y * iResolution.y > 1.0 ? 0.0 : 1.0;
	color.a-= nosort;


    fragColor = color;
}

