// @author ciphrd <https://instagram.com/ciphrd>
// @license MIT
//
// This buffer both generates a sort filter and picks which one should be used 
// between A, B and C, given the time
//
// Output of this buffer will be the vector field used by the sort buffer (D)
//


vec4 getVfield (vec2 uv) {
	vec4 color = vec4(1.0);

	vec2 topright = vec2(1.0, 1.0);

	// size of a texel
	vec2 texelSize = vec2(1./iResolution.x, 1./iResolution.y);

	// we differentiate 1/2 pixels on the y axis
	vec2 iuv = floor(uv * iResolution.xy);
	float r = mod(iuv.y, 2.0) * 2. - 1.; // r = -1 or 1.0

	// here goes the rules to define the displacement map
	vec2 dir = topright * r * (mod(float(iFrame), 2.0) * 2. - 1.);

    // we invert on the left to preserve consistency
    dir*= round(uv.x) * 2. - 1.;

	// color defined by the direction
	color = vec4(dir, round(uv.x), 1.0);

	// we stop on the middle
	float m = step(1./iResolution.x, abs(uv.x-0.5));
	color.a = m;

	return color;   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // sample buffer A & B
    vec4 vfieldA = texture(iChannel0, uv);
    vec4 vfieldB = texture(iChannel1, uv);
    vec4 vfieldC = getVfield(uv);

    // the vector field changes periodically
    float t = mod(iTime, 15.0);

    vec4 color = vfieldA;

    if (t > 10.0) {
    	color = vfieldC;   
    } else if (t > 5.0) {
     	color = vfieldB;   
    }

    // we swap the blue channel to change the direction of the sort
    float t2 = mod(iTime, 30.0);
    if (t2 > 15.0) {
    	color.b = 1. - color.b;   
    }

    fragColor = color;
}

