// @author ciphrd <https://instagram.com/ciphrd>
// @license MIT
//
// This is where the sort happens
// 
// A sorting filter (vector field), is grabbed from the buffer C.
// Buffer C controls which buffer is picked
//


// returns a grayscale based on the average of the 3 components
float gscale (in vec3 color) {
	return (color.r + color.g + color.b) / 3.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uvs 
    vec2 uv = fragCoord.xy / iResolution.xy;

    // threshold is defined by the mouse x
    float threshold = 0.04; //+ 0.98 * iMouse.x/iResolution.x;

    // initialization
    if (iFrame < 30) {
     	fragColor = texture(iChannel0, uv);
        return;
    }



    vec4 vfield = texture(iChannel2, uv);
    vec2 dr = vfield.xy / iResolution.xy; // pixel space to square space
    vec2 p = uv + dr;

    // we make p loop on x
    if (p.x < 0.0) p.x = 1. - p.x;
    if (p.x > 1.0) p.x = fract(p.x);

    // sample the pixels to test
    vec4 actv = texture(iChannel1, uv);
    vec4 comp = texture(iChannel1, p);

    // if we are next to a border on the Y-axis, the prevent the sort from happening
    if (uv.y+dr.y < 0.0 || uv.y+dr.y > 1.0) {
    	fragColor = actv;
        return;
    }

    // sample the vector field of the texel to compare, to get blue and alpha
    vec4 vfieldComp = texture(iChannel2, uv + dr);

    // the alpha component encodes if whether or not a sort is possible
    if (vfield.a < 0.5 || vfieldComp.a < 0.5) {
		fragColor = actv;
        return;
    }


    // we can sort the texel with the other one
    vec4 color = actv;
    float gAct = gscale(actv.rgb);
    float gCom = gscale(comp.rgb);

    // we separate the vector field dir into 2 "categories"
    float classed = sign(dr.x*2. + dr.y);

    if (classed < 0.0) {
        // given the blue component value, we sort one way or the other
		if (vfield.b > 0.5) {
			if (gCom > threshold && gAct > gCom) {
				color = comp;
			}
		} else {
			if (gAct > threshold && gAct < gCom) {
				color = comp;
			}
		}
	} else {
		if (vfield.b > 0.5) {
			if (gAct > threshold && gAct < gCom) {
				color = comp;
			}
		} else {
			if (gCom > threshold && gAct > gCom) {
				color = comp;
			}
		}
	}

    fragColor = color;
}

