////////////////////////////////////////////////////////////////////////////////
//
// Tiling the Poincare Disc
//
// Construct 2,P,Q hyperbolic triangle, centred at the origin, with
// sides on the x and y axes, and a circular side making angles PI/P
// and PI/Q with the first two sides. Fold into triangle by iterated
// reflection in its sides (so the triangle is a fundamental region
// for the group of reflections). Show resulting tiling.
//
// Lots of options:
// 1,2,3: show different edge types
// c: clip to unit disc
// h: halfplane
// i: use ideal triangle (2,inf,inf)
// m: mouse isometry
// n: null rotation
// p: progressive folding
// t: show parity
// z: exponential zoom out (halfplane only)
//
////////////////////////////////////////////////////////////////////////////////

int P = 7, Q = 4; // Triangle is (2,P,Q) with 1/P + 1/Q < 1/2

bool dotwotone = true;      // Show parity of fundamental regions
bool doedge1 = true;        // Show x-axis edge
bool doedge2 = true;        // Show y-axis edge
bool doedge3 = true;        // Show circular edge
bool donullrotation = true; // Do a "null rotation"
bool domouse = true;        // Invert origin to mouse position
bool dozoom = false;        // Zoom out, in halfplane mode only
bool dohalfplane = false;   // Show the halfplane model, instead of the disc
bool doideal = false;       // Use ideal (2,inf,inf) triangle instead of (2,P,Q)
bool doprogressive = false; // Progressively increase max number of folds
bool doclip = true;        // Clip to unit disc

float PI = 3.141592654;

// Compute parameters for hyperbolic triangle, with angles 2,a,b. You
// might want to draw a picture for this. Triangle sides are x=0,y=0
// and a circular arc, centre (x,y) and radius r (with r2 = r*r). The
// arc needs to be a "straight line" in the disc model, which means it
// must intersect the unit disc at right angles. This results in
// condition (i) by Pythagoras' Theorem. The arc must intersect the
// x-axis at angle a, and the y-axis at angle b, which give conditions
// (ii) and (iii).

vec4 solve(float a, float b) {
  // (i) r^2 + 1 = x^2 + y^2 - condition to be orthogonal to unit circle
  // (ii) r cos(a) = x => x = r/cos(a)
  // (iii) r cos(b) = y => y = r/cos(b)
  float r2 = 1.0/(cos(a)*cos(a) + cos(b)*cos(b) - 1.0);
  float r = sqrt(r2);
  float x = r*cos(a);
  float y = r*cos(b);
  return vec4(x,y,r,r2);
}

float ds;
vec3 diagram(vec2 z, vec2 m) {	
    ds = max(1.0,abs(1.0-dot(z,z)));
    ds /= (0.5*iResolution.y);
  vec4 T = doideal ? solve(PI/3.0,0.0) : solve(PI/float(P),PI/float(Q));
  if (dohalfplane) {
    z.y += 1.0;
    if (dozoom) z *= exp(0.41*iTime);
    z.y += 1.0;
    z *= 2.0/dot(z,z);
    z.y -= 1.0;
  }
  // Map origin to m with an inversion
  if (domouse && m != vec2(0)) {
    float k = 1.0/dot(m,m);
    vec2 c = k*m; // m inverted in unit circle
    float t = (k-1.0)/dot(z-c,z-c);
    z = t*z+(1.0-t)*c;
    z.x = -z.x; // Keep chirality
  }
  if (doclip && dot(z,z) > 1.0) return vec3(0);
  if (donullrotation) {
    // Can't do this with an inversion, so map to halfplane, where
    // it's just a  translation.
    vec2 c = vec2(0,-1);
    z -= c;
    z *= 2.0/dot(z,z);
    z.x += 0.2*iTime;
    z *= 2.0/dot(z,z);
    z += c;
  }
  vec2 a = T.xy; float r = T.z, r2 = T.w;
  int nsteps = !doprogressive ? 50 : min(50,int(iTime));
  int i, folds = 0;
  for (i = 0; i < nsteps; i++) {
    int folds1 = int(z.x < 0.0) + int(z.y < 0.0);
    z = abs(z);
    float k = dot(z-a,z-a);
    if (k < r2) {
      folds1++;
      z = r2*(z-a)/k + a;
    }
    if (folds1 == 0) break;
    folds += folds1;
  }
  if (i == nsteps) return vec3(0);
  // We have now folded the original point z into the fundamental
  // region, now draw whatever should be in there. Things get smaller
  // as we move outwards in the disc, so need a scale factor.
  float scalefactor = abs(1.0 - dot(z,z));
  vec3 col = vec3(1,1,0);
  // Euclidean distances to edge of fundamental regions
  float d1 = abs(z.x);
  float d2 = abs(z.y);
  float d3 = abs(length(z-a)-r);
    //ds = fwidth(length(z));
  // Desired widths, appropriately scaled.
  float ewidth = scalefactor*0.00+ds;
  float lwidth = scalefactor*0.005;
  if (dotwotone) {
    // Keep Fabrice happy with a smooth blend between tiles
    // This is easy with only two tile colours.
    float d = min(min(d1,d2),d3);
    col *= 0.5+0.5*smoothstep(-ewidth,+ewidth, folds%2 == 0 ? d : -d);
  }
  if (doedge1 || doedge2 || doedge3) {
    // Draw the edges of the region, if desired.
    float d = 1e8;
    if (doedge1) d = min(d,d1);
    if (doedge2) d = min(d,d2);
    if (doedge3) d = min(d,d3);
    // But make him unhappy with a pixel-independent minimum line width.
    col = mix(vec3(0),col,smoothstep(0.5*lwidth,lwidth+2.0*ds,d));
    //if (fwidth(length(z)) > 0.1) col = vec3(0);
  }
  return col;
}

