float circle(vec2 pos, vec2 centre, float radius) {

	return length(pos - centre) - radius;
}

float edge(float dist, float thickness) {
    return abs(dist) - thickness;
}

#define ID_LEFT 1.0
#define ID_MIDDLE 2.0
#define ID_RIGHT 3.0

vec2 measure(vec2 uv )
{ 
    vec3 col = vec3(0);
    vec2 center = vec2(0);
    float rad = 0.25;
    float thicc = 0.005;
    vec2 movingCentre = 0.125 * vec2( sin(iTime), cos(iTime*1.1));

    // the red one
	float c1 = circle(uv, vec2(-0.25, 0), 0.252);
    // the blue one
	float c4 = circle(uv, vec2(0.25, 0), 0.252);
    // the green one
    float c3 = circle(uv, movingCentre, 0.202);
    // the hole around the green one
    float hole = circle(uv, movingCentre, 0.252);

    float left = max(c1, -hole);
    if (left <= 0.0) {
        return vec2(left, ID_LEFT);
    }

    float right = max(c4, -hole);
    if (right <= 0.0) {
        return vec2(right, ID_RIGHT);
    }
    if (c3 <= 0.0) {
        return vec2(c3, ID_MIDDLE);
    }
}

#define THICC 0.005
#define NORM_DELTA 0.00001

vec3 trace(vec2 uv) {
    vec2 di = measure(uv);
    vec2 dx = measure(uv + vec2(-NORM_DELTA, 0));
    vec2 dy = measure(uv + vec2(0, -NORM_DELTA));
    vec2 dx2 = measure(uv + vec2(NORM_DELTA, 0));
    vec2 dy2 = measure(uv + vec2(0, NORM_DELTA));
    vec2 normal = normalize(vec2(dx2.x - dx.x, dy2.x - dy.x));
    vec2 toLight = normalize(vec2(-0.5, 1.0));
    float lightD = dot(normal, toLight);
    vec3 lighting =  vec3(lightD, lightD, lightD);
    vec3 col = vec3(0);

    if (di.y == ID_LEFT) {
        if (di.x < -THICC) {
            float m = fract(di.x*50.0);
            col = vec3(0.5,0.1,0.1);
            if (m < 0.15) {
                col *= 0.7;
            }
        } else {
            col = vec3(1,1,1);
        }
    }

    if (di.y == ID_RIGHT) {
        if (di.x < -THICC) {
            float m = fract(di.x*50.0);
            col = vec3(0.1,0.1,0.5);
            if (m < 0.15) {
                col *= 0.7;
            }
        } else {
            col = vec3(1,1,1);
        }
    }

    if (di.y == ID_MIDDLE) {
        if (di.x < -THICC) {
            float m = fract(di.x*50.0);
            col = vec3(0.1,0.5,0.1);
            if (m < 0.15) {
                col *= 0.7;
            }
        } else {
            col = vec3(1,1,1);
        }
    }

    if (di.x < -THICC && di.y > 0.0) {
        float ratio = cos(di.x * 10.0);
		col += lighting * 0.5 * ratio;
    }
    return clamp(col, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    float aspect = iResolution.y / iResolution.x;


    vec3 col = vec3(0);
    for (int i=-2; i <= 2; i++) {
        float dx = 0.20 * float(i);
	    for (int j = -2; j <= 2; j++) {
            float dy = 0.20 * float(j);
            vec2 subpixel = fragCoord+vec2(dx,dy);
		    vec2 uv = (subpixel/iResolution.xy-vec2(0.5)) 
                * vec2(1, aspect);
            col += trace(uv);
        }
    }

    // Output to screen
    fragColor = vec4(col / 25.0, 1.0);
}

#define PI 3.1415926535897932384626433832795

//#define u_k vec2(.001, .001)
#define in_w = 449
#define in_h = 483


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float v = 0.0;
    vec2 u_k = vec2(sin(iTime * 2.0) * .05 + .051, cos(iTime * 2.0) * .050 + .051);
    vec2 c = fragCoord * u_k - u_k/2.0;
    v += sin((c.x+iTime));
    v += sin((c.y+iTime)/2.0);
    v += sin((c.x+c.y+iTime)/2.0);
    c += u_k/2.0 * vec2(sin(iTime/3.0), cos(iTime/2.0));
    v += sin(sqrt(c.x*c.x+c.y*c.y+1.0)+iTime);
    v = v/2.0;
    vec3 col = vec3(1, sin(PI*v), cos(PI*v));

    // Output to screen
    fragColor = vec4(col*.5 + .5, 1);
}

