// See "Scrolling Texture Heightmap" for tunnel construction.

float PrBoxDf (vec3 p, vec3 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrTorus4Df (vec3 p, float ri, float rc);
vec3 SphFib (vec3 v, float n);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);
vec2 Rot2D (vec2 q, float a);
float Hashfv3 (vec3 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (vec2 vId);
vec4 LoadTx (vec2 uv);

vec3 ltDir, vbalPos;
vec2 trkFx, trkFy, trkAx, trkAy, trkFw, trkAw;
float dstFar, tCur, tunRad, lmpSep, txOrg, txAsp, vbalSpn, vbalDir;
int idObj;
const int idWal = 1, idFlr = 2, idLmp = 3, idArch = 4, idRail = 5, idTie = 6,
   idVbal = 7, idVspk = 8, idVcap = 9;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 TrackPos (float z)
{
  return vec3 (dot (trkAx, sin (trkFx * z)), dot (trkAy, sin (trkFy * z)), z);
}

vec3 TrackVel (float z)
{
  return normalize (vec3 (dot (trkAx, trkFx * cos (trkFx * z)),
     dot (trkAy, trkFy * cos (trkFy * z)), 1.));
}

float TSurfHt (vec2 tc)
{
  return LoadTx (tc * txAsp).x;
}

float RdTun (vec3 p)
{
  float a, rh;
  a = 0.5 * (atan (- p.x, p.y) / pi + 1.);
  rh = TSurfHt (vec2 ((p.z - txOrg) / (2. * pi * tunRad), a));
  return tunRad + dot (trkAw, sin (trkFw * p.z)) -
     rh * (1. - 0.5 * smoothstep (0.5, 1., p.y / tunRad));
}

float TunDf (vec3 p)
{
  vec3 q;
  vec2 rt;
  float dMin, d;
  dMin = dstFar;
  p.xy -= TrackPos (p.z).xy;
  d = RdTun (p) - length (p.xy);
  DMIN (idWal);
  d = (0.82 - 0.02 * p.x * p.x) * tunRad + p.y;
  DMIN (idFlr);
  return 0.5 * dMin;
}

float TunRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = TunDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 TunNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  for (int j = 0; j < 4; j ++) {
    v[j] = TunDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float VbalDf (vec3 p, float dMin)
{
  vec3 q, qn, uf;
  float d, rad, len, h, sbFac;
  sbFac = 1.7;
  dMin /= 0.6 * sbFac;
  q = p / sbFac;
  rad = 1.;
  qn = normalize (q);
  uf = SphFib (qn, 120.);
  d = PrSphDf (q, rad * (1. - 0.05 * smoothstep (0.1, 0.2, length (qn - uf))));
  DMIN (idVbal);
  q = RotToDir (uf, vec3 (0., 0., 1.), q - uf);
  h = Hashfv3 (41. * uf);
  len = 0.3 * (1. - 0.2 * h);
  q.z -= len;
  d = PrCylDf (q, 0.04 * (1. - 0.1 * q.z / len), len);
  DMIN (idVspk);
  q.z -= len + 0.05;
  q.xy = Rot2D (q.xy, pi * h / 3.);
  q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 0.08;
  d = PrSphDf (q * vec3 (1., 1., 1.3), 0.1);
  DMIN (idVcap);
  return 0.6 * sbFac * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d;
  dMin = dstFar;
  qq = p;
  qq.xy -= TrackPos (qq.z).xy;
  q = qq;
  d = length (max (abs (vec2 (abs (q.x) - 0.8, q.y + 0.82 * tunRad)) -
      vec2 (0.06, 0.25), 0.));
  DMIN (idRail);
  q.y -= -0.82 * tunRad - 0.05;
  q.z = mod (q.z + 1., 2.) - 1.;
  d = PrBoxDf (q, vec3 (1., 0.2, 0.1));
  DMIN (idTie);
  q = qq;
  q.z = mod (q.z + lmpSep, 2. * lmpSep) - lmpSep;
  d = PrTorus4Df (q, 0.16, tunRad - 0.16) - 0.15 *
     (0.7 + 0.3 * sqrt (abs (sin (31. * atan (q.y, q.x)))));
  DMIN (idArch);
  q.y -= tunRad - 0.5;
  d = PrSphDf (q, 0.3);
  DMIN (idLmp);
  q = p - vbalPos;
  d = PrSphDf (q, tunRad - 0.9);
  if (d < 0.1) {
    q.yz = Rot2D (q.yz, vbalSpn);
    q.xz = Rot2D (q.xz, vbalDir);
    dMin = VbalDf (q, dMin);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  e = vec2 (0.002, 0.);
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, q;
  vec2 vf;
  float dstObj, dstTun, dFac, s, att;
  dstTun = TunRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (min (dstTun, dstObj) < dstFar) {
    if (dstTun < dstObj) {
      ro += dstTun * rd;
      vn = TunNf (ro);
      dFac = (1. - smoothstep (0.2, 0.5, dstTun / dstFar));
      q.xy = TrackPos (ro.z).xy;
      ro.xy -= q.xy;
      if (idObj == idWal) {
        vn = VaryNf (8. * ro, vn, 8. * dFac * dFac * (1. - 0.5 * smoothstep (0.7, 1., - vn.y)));
        s = Fbm3 (32. * ro);
        col4 = vec4 (mix (vec3 (0.4, 0.4, 0.45), vec3 (0.45, 0.4, 0.3), smoothstep (0.4, 0.5, s)), 0.05);
      } else if (idObj == idFlr) {
        s = dFac * (1. - smoothstep (-0.2, -0.1, dot (rd, vn)));
        vn = RippleNorm (ro.zx, vn, 4. * s);
        vn = VaryNf (16. * ro, vn, s);
        col4 = vec4 (vec3 (0.5, 0.35, 0.2) * (1. - 0.2 * smoothstep (0.4, 0.85,
           length (vec2 (ro.x, mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep)) / tunRad)), 0.);
      }
      ro.xy += q.xy;
    } else {
      ro += dstObj * rd;
      q = ro - vbalPos;
      vn = ObjNf (ro);
      if (idObj >= idVbal) {
        q.yz = Rot2D (q.yz, vbalSpn);
        q.xz = Rot2D (q.xz, vbalDir);
      }
      if (idObj == idLmp) {
        col4 = vec4 (vec3 (1., 1., 0.7) * 0.5 * (1. - vn.y), -1.);
      } else if (idObj == idRail) {
        col4 = vec4 (0.5, 0.5, 0.55, 0.3);
      } else if (idObj == idTie) {
        col4 = vec4 (0.25, 0.15, 0., 0.);
      } else if (idObj == idArch) {
        col4 = vec4 (0.3, 0.3, 0.25, 0.05);
        vn = VaryNf (32. * ro, vn, 2.);
      } else if (idObj == idVbal) {
        col4 = vec4 (0.5, 0.3, 0.1, 0.1);
        vf = vec2 (8., 8.);
      } else if (idObj == idVspk) {
        col4 = vec4 (0.5, 0.6, 0.5, 0.4);
        vf = vec2 (64., 0.1);
      } else if (idObj == idVcap) {
        col4 = vec4 (0.9, 0., 0., 0.3);
        vf = vec2 (64., 2.);
      }
      if (idObj == idVbal || idObj == idVspk || idObj == idVcap) {
        if (idObj == idVbal) col4.rgb = mix (col4.rgb, 1.5 * vec3 (0.2, 0.2, 1.), 
           SmoothBump (0.45, 0.55, 0.005, mod (0.7 * tCur, 1.)));
        else if (idObj == idVspk || idObj == idVcap) col4.rgb = mix (col4.rgb, 1.5 * vec3 (1., 1., 0.9),
           SmoothBump (0.49, 0.51, 0.005, mod (0.5 * length (q) - 0.7 * tCur, 1.)));
        vn.yz = Rot2D (vn.yz, vbalSpn);
        vn.xz = Rot2D (vn.xz, vbalDir);
        vn = VaryNf (vf.x * q, vn, vf.y);
        vn.xz = Rot2D (vn.xz, - vbalDir);
        vn.yz = Rot2D (vn.yz, - vbalSpn);
      }
      if (col4.a >= 0.) col4.rgb *= (1. - 0.4 * smoothstep (0.4, 0.85,
         abs (mod (ro.z + lmpSep, 2. * lmpSep) - lmpSep) / tunRad));
      else col = col4.rgb;
    }
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, ltDir), 0.) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
    if (idObj == idFlr || idObj == idRail || idObj == idTie)
       col *= 0.9 + 0.1 * smoothstep (0., 0.5, length (ro.xz - vbalPos.xz) - 2.);
    s = min (dstTun, dstObj);
    att = SmoothMin (350. / pow (s, 2.), 1., 0.5) * (1. - smoothstep (60., 70., s));
    col *= att;
  } else col = vec3 (0.);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 ro, rd, vd;
  vec2 uv;
  float el, az, tunPos, zmFac, t;
  uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  uv.x *= iResolution.x / iResolution.y;
  stDat = Loadv4 (vec2 (0., 0.));
  tunPos = stDat.y;
  txOrg = stDat.z;
  txAsp = stDat.w;
  stDat = Loadv4 (vec2 (1., 0.));
  tunRad = stDat.y;
  az = stDat.z;
  el = stDat.w;
  stDat = Loadv4 (vec2 (2., 0.));
  tCur = stDat.y;
  trkFx = 2. * pi * vec2 (0.005, 0.0087);
  trkAx = tunRad * vec2 (2.2, 1.7);
  trkFy = 2. * pi * vec2 (0.017, 0.0227);
  trkAy = tunRad * vec2 (0.13, 0.07);
  trkFw = 2. * pi * vec2 (0.05, 0.028);
  trkAw = tunRad * vec2 (0.07, 0.04);
  vd = TrackVel (tunPos);
  az += 0.5 * pi + atan (- vd.z, vd.x);
  el += asin (vd.y) - 0.02 * pi;
  zmFac = 3.5 - 0.5 * SmoothBump (0.3, 0.7, 0.1, abs (az) / pi);
  t = tunPos + tunRad * sign (0.5 * pi - abs (az)) * (4. + clamp (6. * (Fbm1 (0.02 * tCur) - 0.5), -2.5, 2.5));
  vbalPos = TrackPos (t);
  vd = TrackVel (t);
  vbalDir = -0.5 * pi - atan (vd.z, - vd.x);
  vbalSpn = -0.5 * pi * tCur;
  vuMat = StdVuMat (el, az);
  ro = TrackPos (tunPos) + vuMat * vec3 (0., 0.15, - 0.5) * tunRad;
  dstFar = 100.;
  lmpSep = 10.;
  ltDir = vuMat * normalize (vec3 (0., 0.5, -1.));
  rd = vuMat * normalize (vec3 (uv , zmFac));
  rd.zx = Rot2D (rd.zx, 0.1 * atan (ro.x));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorus4Df (vec3 p, float ri, float rc)
{
  vec2 q;
  q = vec2 (length (p.xy) - rc, p.z);
  q *= q;
  return sqrt (sqrt (dot (q * q, vec2 (1.)))) - ri;
}

vec3 SphFib (vec3 v, float n)
{   // Keinert et al's inverse spherical Fibonacci mapping
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  const float phi = 1.618034;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vfMin;
}

