float GetDist(in vec3 p){
    float d = MAX_DIST;
    p = fract(abs(p)/3.)*3.-1.5;
    d = length(p)-(0.2+0.4*abs(cos(iTime)));
    d = smin(d,
            length(p+0.2)-(0.2+0.4*abs(sin(iTime)))
            );
    d = smin(d,
            length(p-0.5)-(0.2+0.4*abs(sin(iTime)+1.))
            );
    return d;
}



vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(0.1,0);
    vec3 n = d - vec3(
    	GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));
    return normalize(n);

}
RM RayMarch(vec3 ro, vec3 rd){
    float d0 = 0.;
    int steps = 0;
    for(int i=0;i<MAX_STEPS;i++){
        steps = i;
        vec3 p = ro + rd*d0;
        float dS = GetDist(p);
        d0 += dS;
        if (d0>MAX_DIST || dS < SURF_DIST) break;
    }

    return RM(d0,steps);
}





