float sdObj(vec3 p, in object o)
{
    float d = distance(p, o.X);
    if(d > 1.5*o.bR) return d - o.bR; //outside 1.5x bounding sphere
    return udTriangle(p, o.a, o.b, o.c) - 0.005;
}

