vec2 random(vec2 pt , float t){
    return vec2(
        fract(456512.6161616*cos(dot(vec3(pt,t) , vec3(150.9898944,30.54614646,151.054608))) ),
    	fract(566516.6161616*cos(dot(vec3(pt,t) , vec3(135.54568544,-12540.50540,642.5419840))) ));
}


vec2 noise(vec2 st, float t){
    vec2 i = floor(st);
    vec2 f = fract(st);
	float it = floor(t);
    float ft = fract(t);
    // Four corners in 2D of a tile
    vec2 a = random(i,it);
    vec2 b = random(i + vec2(1.0, 0.0),it);
    vec2 c = random(i + vec2(0.0, 1.0),it);
    vec2 d = random(i + vec2(1.0, 1.0),it);
	vec2 as = random(i,it+1.);
    vec2 bs = random(i + vec2(1.0, 0.0),it+1.);
    vec2 cs = random(i + vec2(0.0, 1.0),it+1.);
    vec2 ds = random(i + vec2(1.0, 1.0),it+1.);
    vec2 u = f*f*(3.0-2.0*f);
	//float ut = ft*ft*(3.0-2.0*ft);
	float ut = ft;
	return mix(mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y
        ,
        mix(as, bs, u.x) +
            (cs - as)* u.y * (1.0 - u.x) +
            (ds - bs) * u.x * u.y
        ,ut);
}

