// HYPERBOLIC CODE FROM http://roy.red/generating-spherical-and-hyperbolic-tilings-in-glsl-.html#generating-spherical-and-hyperbolic-tilings-in-glsl
// Super interesting read, I am still in the process of understanding all of it, so I copied the UV generation
// and added some textures on top to make it shiny


#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

#define PI acos(-1.)

#define pi acos(-1.)
#define tau (1.*pi)

// Adjust p, q, r
const float p = 3.; const float q=4.; const float r=3.;
float space = float(sign(q*r+p*r+p*q-p*q*r));
vec3 fold( vec3 p, in vec3 dir, inout int n) {
// Reflect if we're outside the fundamental region
    float dt = dot(p,dir);
    if (dt < 0.) {
        n = n + 1;
        return p-2.*dt*dir*vec3(1,1,space);
    }
    return p;
}
float sdBox(vec2 p, vec2 s){
	float d = 10e8;
    p = abs(p) - s;
    d = max(p.x,p.y);
    return d;
}
float xor(float a, float b){
	return float(int(a)^ int(b));
}
vec4 noise(float t){return texture(iChannel0,vec2(floor(t), floor(t))/256.);}

vec3 get(vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec2 z = uv;
    z *= 2.5;

    // - HYPERBOLIC UV - //

    if(length(z) > 1.)
    	z /= dot(z,z);

    // Stereographically project
    vec3 w=vec3(2.*z,1.-space*dot(z,z))*1./(1.+space*dot(z,z));

    // Hyperbolic rotation
    float amt = mod(iTime, 2.45*2.);

    float envA = smoothstep(0.,1.,amt/2.45)*0.2;
    float envB = smoothstep(0.,1.,amt/2.45 - 1.)*0.2;

    if(amt > 2.45)
        amt -= 2.45*2.;
    w.yz *= mat2(cosh(amt), sinh(amt), sinh(amt),cosh(amt));
    //w.yz *= mat2(cosh(amt), sinh(amt), sinh(amt),cosh(amt));
    // Calculate cutting planes
    float ab = -cos(PI/p);
    float bc = -cos(PI/q);
    float ac = -cos(PI/r);
    vec3 a = vec3(1.,0.,0);
    vec3 b = vec3(ab,sin(PI/p),0.);
    float c0 = ac;
    float c1 = (bc-b[0]*c0)/b[1];
    vec3 c = vec3(c0,c1,sqrt(abs(1.-c0*c0-c1*c1)));
    if (space==0.)
        c[2] = .5;

    // Fold across the planes a few times
    int n=0; int m = 0;
    for (int i=0;i<10;i++){
        w = fold(w,a,m);
        w = fold(w,b,m);
        w = fold(w,c,n);
    }


    // - COLORING - //
    //w.x += iTime;

    float d = 10e7;
    vec4 no = noise(float(n + m));
    vec2 p = w.xy;
    //p = sin(p*7.);
    #define pmod(p,x) (mod(p,x) - 0.5*x)
    //vec2 j = pmod(p, 20.);
    vec2 j = p;
    j.x -= 2.5;
    j = sin(j*7.);
    vec3 col = vec3(0);
    //col += w*1.;


    vec3 ff = 1. - vec3(1.)*(dot(w*1.,a*8.));

    ff = clamp(ff, 0.,1.);
    //col += ff*0.1;

    p.x -= 0.5;
    float db = sdBox(j, vec2(0.2,0.9));
    #define pal(a,b,c,d,e) (a + b*sin((c)*(d)+(e)))
    vec3 cc = pal(0., 0.5, vec3(4.7,4.4,3.4), 1.7,.4 + w*2. + iTime*0.);

    cc = texture(iChannel1,w.xy, 1.3 + dot(uv*0.5,uv*0.5)*5.).xyz;

    cc = sin(cc*(5.4) + vec3(0.,0. ,.4 - envB + envA));
    cc = max(cc, 0.);

    //cc.bg *= 0.2 +  pow(1. - length(w.x), 2.)*0.8;
    float bb = length(cc) * pow(1. - length(w), 1.)*1.;
    cc = sin(cc*1.  + bb*20.);
    cc *= pow(1. - length(w.x)*1.25, 1.)*1.;
    cc = smoothstep(0.,1.,cc);
    cc = smoothstep(0.,1.,cc);
    col += cc;


    col -= smoothstep(0.,1.*dFdy(uv.x),-db );
    col = max(col, 0.);
    vec3 cb = smoothstep(0.,1.*dFdy(uv.x),-db )*pal(0.5, 0.5, vec3(0.7,0.4,0.9), 0.7 ,0.4 + uv.x + iTime);

    //col += cb;

    col = clamp(col, 0., 1.);

    //col = vec3(1);

    //col *= 1. - exp2(-abs(length(uv) - 0.4)*20.)*1.;
    //col *= 1. - exp2(-abs(length(uv) - 0.4)*20.)*1.;

    float edge = exp2(-abs (length(uv) - 0.4)*50.)*1.;

    edge = smoothstep(0.,1.,edge);
    edge = smoothstep(0.,1.,edge);
    //edge = smoothstep(0.,1.,edge);

    //col = pow(col, vec3(1. + pow(edge, 0.5)));
    col = max(col, 0.);
    col.gb = pow(col.gb, vec2(1. + pow(edge, 0.5) ));

    col *= 1. - edge*1.0;

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;  
    vec3 col = vec3(0);

    float aa = 4.;

    for(float i =0.; i < aa*aa + min(float(iFrame),0.); i++){
    	col += get(fragCoord + vec2(mod(i,aa),floor(i/aa))/aa);
    }
    col /= aa*aa;

    col = clamp(col, 0., 1.);
    //col *= 0.999;
    //col = 1. - col;



    //col *= max((1. + 1.*cos(iTime*1.+length(uv)*2.+vec3(4,2.5,1.5)))*1., 0.);
    //col *= max((1.5 + 1.4*cos(iTime+length(uv)*2.+vec3(1,1.5,1.5)))*1., 0.);
    col *= 2.;
    col *= 1. - pow(smoothstep(0.,1.,dot(uv,uv)*0.9),0.9)*1.;
    //col = 1. - col;
    col = max(col, 0.);

    col = pow(col, vec3(0.45454));
    fragColor = vec4(col,1.0);
}

/*
Visualization of the Polar Curve of an elliptic curve
with respect to the mouse point.

The Polar of a Curve (degree n) with respect to a point p
is a curve with degree n-1 which goes through all
singularities (none for an elliptic curve)
and all points on the curve whose tangents pass through p.

For a projective curve given by a homogenous equation
its homogenous equation can be calculated by computing the dot product of
the projective coordinates of a point with the curve's gradient.

The blue curve is the elliptic curve
The green dot is the mouse point
The cyan curve is the Polar of the elliptic curve with respect to the mouse point
The red dots are the intersection points of the elliptic curve and its Polar
The green lines are the tangents at the intersection points of the elliptic curve and its Polar

For some mouse positions the iterative computation of the
intersection points fail.

see https://en.wikipedia.org/wiki/Polar_curve
*/

//#define SHOW_BASINS
//#define SHOW_INITAL_POINTS
//#define ANIMATE_CURVE

const int grad_descent_iterations=12;

float border;

const float eps = .005;

const float pi=3.14159265358979;

float spectral(float x){
        return clamp(abs(mod((x/pi+1.)*4.,8.)-4.)-2.,-.75,.75)*.5/.75+.5;
}

vec3 to_col(vec2 z){
	float direction =atan(z.x,z.y);
	return vec3(spectral(direction),spectral(direction+pi/2.),spectral(direction-pi));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float aspect_ratio = iResolution.x / iResolution.y;

	vec2 uv = gl_FragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= aspect_ratio;

	vec2 mouse = (iMouse.xy+1.) / iResolution.xy;
	mouse-=.5;
	mouse.x *= aspect_ratio;

	const float line_width = .001;
	const float curve_width = .001;
	const float dot_size = .01;

	const float axis_width = .005;

	const vec3 bg_col = vec3(1,1,1);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 line_col = vec3(0,1,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 polar_curve_col = vec3(0,1,1);
	const vec3 dot_col = vec3(1,0,0);
	const vec3 initial_dot_col = vec3(0,0,0);
	const vec3 mouse_dot_col = vec3(0,1,0);

    border = 2./iResolution.y;

	float curve_dis = 1e38;
	float polar_curve_dis = 1e38;
	float dot_dis = 1e38;
	float initial_dot_dis = 1e38;
	float mouse_dot_dis = 1e38;
	float axis_dis = 1e38;
	float line_dis = 1e38;

	//elliptic curve parameters (weierstrass normal form)

	#ifdef ANIMATE_CURVE
	float anim=sin(mod(iTime*.1,2.*pi));

	float g_2 = .3 + .2*anim;
	float g_3 = -.05 + .05*anim;
	#else
	float g_2 = .3;
	float g_3 = -.05;
	#endif

	vec2 p0=mouse;

	//initial approximations of the intersection points
	vec2[6] int_pnts=vec2[](vec2(.0,.25),vec2(.0,-.25),
	                        vec2(-.25*aspect_ratio,.25),vec2(-.25*aspect_ratio,-.25),
	                        vec2(.25*aspect_ratio,.25),vec2(.25*aspect_ratio,-.25));

	//compute intersection points by alternately gradient descenting to curve and polar of curve
	float int_p_val, polar_int_p_val, p1_val, polar_p1_val;
	vec2 int_p_grad, polar_int_p_grad, p1_grad, polar_p1_grad;

	#ifdef SHOW_BASINS
	vec2 p1=uv;
	#endif

	#ifdef SHOW_INITAL_POINTS
	for(int i=0;i<6;i++){
		initial_dot_dis=min(initial_dot_dis,distance(int_pnts[i],uv)-dot_size);
	}
	#endif

	for(int i=0;i<grad_descent_iterations;i++){
		for(int j=0;j<6;j++){
			int_p_val = int_pnts[j].y*int_pnts[j].y - (( 4.*int_pnts[j].x*int_pnts[j].x - g_2 ) * int_pnts[j].x - g_3);
			int_p_grad = vec2(-12.*int_pnts[j].x*int_pnts[j].x+g_2,2.*int_pnts[j].y);
			int_pnts[j]-=(int_p_grad*int_p_val)/dot(int_p_grad,int_p_grad);

			polar_int_p_val=p0.x*(12.*int_pnts[j].x*int_pnts[j].x-g_2)+p0.y*(-2.*int_pnts[j].y)+(-2.*g_2*int_pnts[j].x-int_pnts[j].y*int_pnts[j].y-3.*g_3);
			polar_int_p_grad=vec2(p0.x*24.*int_pnts[j].x-2.*g_2,-2.*p0.y-2.*int_pnts[j].y);
			int_pnts[j]-=(polar_int_p_grad*polar_int_p_val)/dot(polar_int_p_grad,polar_int_p_grad);
		}

		#ifdef SHOW_BASINS
		p1_val = p1.y*p1.y - (( 4.*p1.x*p1.x - g_2 ) * p1.x - g_3);
		p1_grad = vec2(-12.*p1.x*p1.x+g_2,2.*p1.y);
		p1-=(p1_grad*p1_val)/dot(p1_grad,p1_grad);

		polar_p1_val=p0.x*(12.*p1.x*p1.x-g_2)+p0.y*(-2.*p1.y)+(-2.*g_2*p1.x-p1.y*p1.y-3.*g_3);
		polar_p1_grad=vec2(p0.x*24.*p1.x-2.*g_2,-2.*p0.y-2.*p1.y);
		p1-=.1*(polar_p1_grad*polar_p1_val)/dot(polar_p1_grad,polar_p1_grad);
		#endif
	}

	mouse_dot_dis=min(mouse_dot_dis,distance(p0,uv)-dot_size);

	for(int i=0;i<6;i++){
		int_p_val = int_pnts[i].y*int_pnts[i].y - (( 4.*int_pnts[i].x*int_pnts[i].x - g_2 ) * int_pnts[i].x - g_3);
		if(abs(int_p_val)<eps){
			dot_dis=min(dot_dis,distance(int_pnts[i],uv)-dot_size);
			int_p_grad = vec2(-12.*int_pnts[i].x*int_pnts[i].x+g_2,2.*int_pnts[i].y);
			vec3 tangent=vec3(normalize(int_p_grad),0);
			tangent.z=-dot(int_pnts[i],tangent.xy);

			line_dis=min(line_dis,abs(dot(vec3(uv,1),tangent)));
		}
	}

	float uv_val = uv.y*uv.y - (( 4.*uv.x*uv.x - g_2 ) * uv.x - g_3);
	vec2 uv_grad = vec2(-12.*uv.x*uv.x+g_2,2.*uv.y);

	curve_dis=min(curve_dis, abs(uv_val)/length(uv_grad)-curve_width);

	float polar_uv_val=p0.x*(12.*uv.x*uv.x-g_2)+p0.y*(-2.*uv.y)+(-2.*g_2*uv.x-uv.y*uv.y-3.*g_3);
	vec2 polar_uv_grad=vec2(p0.x*24.*uv.x-2.*g_2,-2.*p0.y-2.*uv.y);

	polar_curve_dis=min(polar_curve_dis, abs(polar_uv_val)/length(polar_uv_grad)-curve_width);

	axis_dis=min(axis_dis, abs(uv.x));
	axis_dis=min(axis_dis, abs(uv.y));

	vec3 color = bg_col;

	#ifdef SHOW_BASINS
	color=to_col(p1)*.5;
	#endif

	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(line_col,color,smoothstep(0., border, line_dis)));
	color=vec3(mix(polar_curve_col,color,smoothstep(0., border, polar_curve_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));
	color=vec3(mix(initial_dot_col,color,smoothstep(0., border, initial_dot_dis)));
	color=vec3(mix(mouse_dot_col,color,smoothstep(0., border, mouse_dot_dis)));

	fragColor = vec4(color,1);
}

// Original by sl2c: https://www.shadertoy.com/view/3d2cz1
// Zooming modification by Matthew Arcus, mla, 2020.

#define swap(x,y) {t=x;x=y;y=t;}


vec3 color(vec2 z) {
    float pi = 3.14159265359;
    float theta = pi/9.0;
    // someday I'll explain the cross-ratio magic that got me these numbers
    float r = 2.0 / (1.0 - sqrt(1.0 - 4.0 * sin(theta) * sin(theta)));
    float p = - r * cos(theta);
    bool fl = false;
    vec3[3] colors;
    colors[0] = vec3(1.0,0.0,0.0);
    colors[1] = vec3(0.0,1.0,0.0);
    colors[2] = vec3(0.0,0.0,1.0);
    vec3 t; // for temp space
    for(int i=0;i<100;i++) {
        if (z.x < 0.0) {
            z.x = -z.x;
            colors[2] = 1.0 - colors[2];
            fl = !fl;
            continue;
        }
        if (dot(z,z) < 1.0) {
            z /= dot(z,z);
            fl = !fl;
            swap(colors[0],colors[1]);
            continue;
        }
        z.x -= p;
        if (dot(z,z) > r*r) {
            z *= r * r / dot(z,z);
            fl = !fl;
            z.x += p;
            swap(colors[1],colors[2]);
            continue;
        }
        z.x += p;
        vec3 col = colors[0];
        if (fl) {
            col = 0.5 * col;
        }
    return col;
    }
    return vec3(1.0,1.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord - iResolution.xy * 0.5)/iResolution.y;
    uv *= exp(50.0-mod(iTime,50.0)-24.0);
        //uv.x += 1.0 * iTime;
        fragColor = vec4(color(uv),1.0);
}

float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

struct object
{
    vec2 p;
    vec2 r;
    int i;
};

float sdObj(vec2 p, in object o)
{
    switch(o.i)
    {
    case 0:
    	return sdBox(p - o.p, o.r);
    case 1:
    	return sdHexagram(p - o.p, o.r.x);
    case 2:
    	return sdRoundedX(p - o.p, o.r.x, o.r.y);
    default:
        return distance(p, o.p) - o.r.x;
    }
}

float eqDist(vec2 d, in object a, in object b)
{
    vec2 p = a.p;
    float td = 0.;
    for(int i = 0; i < 10; i++)
    {
        float dd = sdObj(p, b) - sdObj(p, a);
        p += dd*d;
        td += dd;
    }
    return td;
}

vec2 scale(vec2 pix)
{
    return 0.2*(pix - iResolution.xy*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = scale(fragCoord);

    object a, b;
    a.p = vec2(0); a.r = vec2(14,1); a.i = 2;
    b.p = 15.*vec2(sin(0.1*iTime),1.3*cos(0.1*iTime)); b.r = vec2(5.); b.i = 1;

 	float de0 = sdObj(uv, a);
    float de1 = sdObj(uv, b);
    vec3 cc;
    if(de0 < de1) cc = vec3(1,1.02,1.05);
    else cc =vec3(1.05,1.02,1.);
  	float de = min(de0, de1);
    vec2 d = normalize(scale(iMouse.xy) - a.p);
    float dd = eqDist(d, a, b);
    float e = exp(-sdSegment(uv, a.p, a.p + dd*d));

    vec3 col = smoothstep(0.4,0.6,0.5+0.5*sin(cc*de))/de 
               + vec3(0,0,1)*exp(-abs(de1-de0))
               + e*vec3(1,0.5,0.2);

    fragColor = vec4(col,1.0);
}

// "Cave Dolphins" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrSphDf (vec3 p, float r);
float PrEllipsDf (vec3 p, vec3 r);
float Minv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Hashfv3 (vec3 p);
vec3 Hashv3v3 (vec3 p);
float Noisefv3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 fishPos, ltPos, ltAx;
vec2 trkAx, trkAy, trkFx, trkFy;
float fishAngH, fishAngV, fishAngI, dstFar, tCur;
int idObj;
bool isSh, isPano;
const float pi = 3.14159;

vec3 TrackPath (float t)
{
  return vec3 (dot (trkAx, sin (trkFx * t)), dot (trkAy, sin (trkFy * t)), t);
}

vec3 TrackVel (float t)
{
  return vec3 (dot (trkAx * trkFx, cos (trkFx * t)), dot (trkAy * trkFy, cos (trkFy * t)), 1);
}

float VPoly (vec3 p)
{
  vec3 ip, fp, a, w;
  p *= 14.;
  ip = floor (p);
  fp = fract (p);
  a = vec3 (4.);
  for (float gz = -1.; gz <= 1.; gz ++) {
    for (float gy = -1.; gy <= 1.; gy ++) {
      for (float gx = -1.; gx <= 1.; gx ++) {
        w = vec3 (gx, gy, gz) + 0.8 * Hashv3v3 (ip + vec3 (gx, gy, gz)) - fp;
        a.z = dot (w, w);
        if (a.z < a.x) a.xy = a.zx;
        else a.y = min (a.z, a.y);
      }
    }
  }
  return sqrt (a.y - a.x);
}

float FishDf (vec3 p, float dMin)
{
  vec3 q;
  float szFac, dm, dBodyF, dBodyB, dMouth, dFinT, dFinP, dFinD, dEye;
  int id;
  szFac = 0.25;
  dMin /= szFac;
  p = (p - fishPos) / szFac;
  p.xz = Rot2D (p.xz, fishAngH);
  p.yz = Rot2D (p.yz, fishAngV);
  p.x = abs (p.x);
  p.z -= 0.2;
  p.yz = Rot2D (p.yz, 0.2 * fishAngI);
  q = p;
  q.z -= -0.06;
  dBodyF = PrEllipsDf (q, vec3 (0.07, 0.08, 0.24));
  q = p;
  q.z -= -0.12;
  q.yz = Rot2D (q.yz, fishAngI);
  q.z -= -0.16;
  dBodyB = PrEllipsDf (q, vec3 (0.035, 0.05, 0.25));
  q.z -= -0.22;
  q.yz = Rot2D (q.yz, 2. * fishAngI);
  q.xz -= vec2 (0.05, -0.05);
  q.xz = Rot2D (q.xz, 0.4);
  dFinT = PrEllipsDf (q, vec3 (0.08, 0.007, 0.04));
  q = p;
  q.yz -= vec2 (-0.03, 0.17);
  q.yz = Rot2D (q.yz, 0.1);
  q.y = abs (q.y) - 0.004;
  dMouth = PrEllipsDf (q, vec3 (0.025, 0.012, 0.06));
  q = p;
  q.yz -= vec2 (0.07, -0.1);
  q.yz = Rot2D (q.yz, 0.6);
  dFinD = PrEllipsDf (q, vec3 (0.005, 0.1, 0.035));
  q = p;
  q.xy = Rot2D (q.xy, 0.8);
  q.xz -= vec2 (0.07, -0.01);
  q.xz = Rot2D (q.xz, 0.6);
  dFinP = PrEllipsDf (q, vec3 (0.09, 0.004, 0.03));
  q = p;
  q -= vec3 (0.04, 0.01, 0.11);
  dEye = PrSphDf (q, 0.015);
  dm = SmoothMin (dBodyF, dBodyB, 0.03);
  dm = SmoothMin (dm, dFinT, 0.01);
  dm = SmoothMin (dm, dMouth, 0.015);
  dm = SmoothMin (dm, dFinD, 0.002);
  dm = SmoothMin (dm, dFinP, 0.002);
  if (dm < dEye) id = 2;
  else {
    dm = dEye;
    id = 3;
  }
  if (dm < dMin) {
    dMin = dm;
    idObj = id;
  }
  return dMin * szFac;
}

float ObjDf (vec3 p)
{
  vec3 q, db;
  float dMin, r, b;
  const float dSzFac = 15.;
  q = p;
  q.xy -= TrackPath (q.z).xy;
  r = floor (8. * Hashfv3 (floor (q)));
  q = fract (q);
  if (r >= 4.) q = q.yxz;
  r = mod (r, 4.);
  if (mod (r, 2.) == 0.) q.x = 1. - q.x;
  if (abs (r - 1.5) == 0.5) q.y = 1. - q.y;
  db = vec3 (length (vec2 (length (q.xy) - 0.5, q.z - 0.5)),
     length (vec2 (length (vec2 (q.z, 1. - q.x)) - 0.5, q.y - 0.5)),
     length (vec2 (length (1. - q.yz) - 0.5, q.x - 0.5)));
  dMin = 0.7 * (Minv3 (db) - 0.1 - 0.017 * VPoly (p));
  idObj = 1; 
  if (! isSh) dMin = FishDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 20; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += h;
    if (sh < 0.05 || d > ltDist) break;
  }
  return 0.5 + 0.5 * sh;
}

float WatShd (vec3 rd)
{
  vec2 p;
  float t, h;
  if (rd.y == 0.) rd.y = 0.0001;
  p = 20. * rd.xz / rd.y;
  t = tCur * 2.;
  h = sin (2. * p.x + 0.77 * t + sin (0.73 * p.y - t)) + sin (0.81 * p.y - 0.89 * t +
     sin (0.33 * p.x + 0.34 * t)) + 0.5 * (sin (1.43 * p.x - t) + sin (0.63 * p.y + t));
  h *= 0.04 * smoothstep (0.5, 1., rd.y);
  return h;
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = tCur * 4.;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0., 0.5, 0.8), vec3 (0.25, 0.4, 1.), 0.5 + 0.5 * rd.y) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + gd * 0.05);
}

float TurbLt (vec3 p, vec3 n, float t)
{
  vec4 b;
  vec2 q, qq;
  float c, tt;
  q = 2. * pi * mod (vec2 (dot (p.yzx, n), dot (p.zxy, n)), 1.) - 256.;
  t += 11.;
  c = 0.;
  qq = q;
  for (int j = 1; j <= 7; j ++) {
    tt = t * (1. + 1. / float (j));
    b = sin (tt + vec4 (- qq + vec2 (0.5 * pi, 0.), qq + vec2 (0., 0.5 * pi)));
    qq = q + tt + b.xy + b.wz;
    c += 1. / length (q / sin (qq + vec2 (0., 0.5 * pi)));
  }
  return clamp (pow (abs (1.25 - abs (0.167 + 40. * c)), 8.), 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn, vno, roo, ltVec, ltDir;
  float dstObj, ltDist, sh, atten, eDark, aDotL;
  roo = ro;
  isSh = false;
  dstObj = ObjRay (ro, rd);
  bgCol = BgCol (rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vno = vn;
    if (idObj == 1) {
      col4 = vec4 (mix (vec3 (0.7, 0.75, 0.7), vec3 (0.7, 0.7, 0.75),
         smoothstep (0.45, 0.55, Noisefv3 (41.1 * ro))), 0.3);
      col4 *= 1. - 0.2 * Noisefv3 (19.1 * ro);
      vn = VaryNf (64. * ro, vn, 2.);
    } else if (idObj == 2) {
      col4 = vec4 (vec3 (0.95, 0.8, 0.7) * (1. - 0.3 * smoothstep (-0.5, -0.4, vn.y)), 0.2);
    } else if (idObj == 3) {
      col4 = vec4 (0.5, 1., 0.5, -1.);
    }
    ltVec = roo + ltPos - ro;
    ltDist = length (ltVec);
    ltDir = ltVec / ltDist;
    aDotL = dot (ltAx, - ltDir);
    atten = min (1., 0.3 + (isPano ? smoothstep (-1., 0., aDotL) :
       smoothstep (0.6, 0.9, aDotL)) / (1. + 0.3 * ltDist * ltDist));
    if (col4.a >= 0.) {
      isSh = true;
      eDark = (idObj == 1) ? 0.8 + 0.2 * smoothstep (0.2, 0.3, VPoly (ro)) : 1.;
      sh = ObjSShadow (ro, ltDir, ltDist);
      col = atten * col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.) +
         col4.a * step (0.95, sh) * pow (max (dot (reflect (rd, vn), ltDir), 0.), 64.)) * eDark;
    } else col = col4.rgb;
    col += 0.2 * step (0.95, sh) * TurbLt (0.05 * ro, abs (vno), 0.5 * tCur) *
       (1. - smoothstep (0.5, 0.8, dstObj / dstFar)) * smoothstep (-0.2, -0.1, vno.y);
    col = mix (col, bgCol, 0.1 + 0.9 * smoothstep (0., 0.85, dstObj / dstFar));
  } else col = bgCol + WatShd (rd);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv;
  float az, el, zmFac, fSpd, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  isPano = (mPtr.z <= 0.);
  if (isPano) az = pi;
  else {
    t = mod (0.02 * tCur, 2.);
    az = pi * SmoothBump (0.25, 0.75, 0.07, mod (t, 1.)) * (2. * floor (t) - 1.);
  }
  el = 0.;
  if (! isPano && mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  if (abs (uv.y) < 0.85) {
    trkAx = vec2 (2., 0.9);
    trkAy = vec2 (1.3, 0.66);
    trkFx = vec2 (0.2, 0.23);
    trkFy = vec2 (0.17, 0.24);
    fSpd = 0.2;
    t = fSpd * tCur;
    ro = TrackPath (t);
    ro.xy += 0.05 * sin (0.05 * pi * tCur);
    vd = normalize (TrackVel (t));
    vuMat = StdVuMat (el + sin (vd.y), az + atan (vd.x, vd.z));
    t += (isPano ? 0.2 : 0.4) * sign (0.5 * pi - abs (az));
    fishPos = TrackPath (t);
    vd = normalize (TrackVel (t));
    fishAngH = atan (vd.x, vd.z);
    fishAngV = sin (vd.y);
    fishAngI = 0.1 * sin (pi * tCur);
    zmFac = isPano ? 0.6 : 2.2;
    uv /= zmFac;
    rd = vuMat * normalize (isPano ? vec3 (sin (uv.x + vec2 (0., 0.5 * pi)), uv.y).xzy : vec3 (uv, 1.));
    ltPos = vuMat * vec3 (0., 0.3, 0.);
    ltAx = vuMat * vec3 (0., 0., 1.);
    dstFar = 16.;
    col = ShowScene (ro, rd);
  } else col = vec3 (0.);
  fragColor = vec4 (col, 1.);
}

