////////////////////////////////////////////////////////////////////////////////
//
// Pappus's theorem on Hyperbolic Plane.
//
// Doing geometry on the hyperbolic plane.
//
// No animation, just the mouse, which moves things around.
//
////////////////////////////////////////////////////////////////////////////////

bool halfspace = false; // Set true for the half-plane model!

float rdot(vec3 r1, vec3 r2) {
  // Minkowski inner product
  return dot(vec3(1,1,-1)*r1,r2);
}

// Normalize a point
vec3 hnormalize(vec3 p) {
  if (p.z < 0.0) p = -p;
  return p/sqrt(-rdot(p,p));
}

// Normalize a line
vec3 lnormalize(vec3 p) {
  return p/sqrt(rdot(p,p));
}

// Distance from point
// cosh(x) = y = 1 + 0.5*x*x + O(x^4)
// so for x close to 1 (so y is small)
// acosh(y) = x = sqrt(2*(y-1)) is good approximation
float hpoint(vec3 p, vec3 q) {
  //return acosh(-rdot(p,q));
  return sqrt(-2.0*(rdot(p,q)+1.0));
}

// Distance from line
float hline(vec3 p, vec3 q) {
  return abs(rdot(p,q));
}

// We want that rdot(p,hcross(p,q)) = rdot(q,hcross(p,q)) = 0
vec3 hcross(vec3 p, vec3 q) {
  return cross(p,q)*vec3(-1,-1,1);
}

vec3 linejoin(vec3 p, vec3 q) {
  return lnormalize(hcross(p,q));
}

vec3 pointjoin(vec3 p, vec3 q) {
  return hnormalize(hcross(p,q));
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 color(vec3 p, float time) {
  vec3 col = vec3(0.2);
  vec3 A0 = hnormalize(vec3(-0.5,0.8,1));
  vec3 C0 = hnormalize(vec3(0.4,0.9,1));
  float k0 = 0.5;
  vec3 B0 = hnormalize(k0*A0+(1.0-k0)*C0);
  vec3 A1 = hnormalize(vec3(-0.5,-0.8,1));
  vec3 C1 = hnormalize(vec3(0.9,-0.3,1));
  float k1 = 0.6;
  vec3 B1 = hnormalize(k1*A1+(1.0-k1)*C1);
  vec3 L0 = linejoin(A0,C0);
  vec3 L1 = linejoin(A1,C1);
  vec3 AB0 = linejoin(A0,B1);
  vec3 AB1 = linejoin(A1,B0);
  vec3 BC0 = linejoin(B0,C1);
  vec3 BC1 = linejoin(B1,C0);
  vec3 CA0 = linejoin(C0,A1);
  vec3 CA1 = linejoin(C1,A0);
  vec3 P = pointjoin(AB0,AB1);
  vec3 Q = pointjoin(BC0,BC1);

  if (true) {
    float d = 1e8;
    d = min(d,hline(p,L0));
    d = min(d,hline(p,L1));
    d = min(d,hline(p,AB0));
    d = min(d,hline(p,AB1));
    d = min(d,hline(p,BC0));
    d = min(d,hline(p,BC1));
    d = min(d,hline(p,CA0));
    d = min(d,hline(p,CA1));
    d = min(d,hline(p,linejoin(P,Q)));
    col = mix(vec3(1,1,0),col,smoothstep(0.01,0.02,d));
  }
  if (true) {
    float d = 1e8;
    d = min(d,hpoint(p,A0));
    d = min(d,hpoint(p,B0));
    d = min(d,hpoint(p,C0));
    d = min(d,hpoint(p,A1));
    d = min(d,hpoint(p,B1));
    d = min(d,hpoint(p,C1));
    d = min(d,hpoint(p,P));
    d = min(d,hpoint(p,Q));
    d = min(d,hpoint(p,pointjoin(CA0,CA1)));
    col = mix(vec3(1,0,0),col,smoothstep(0.05,0.06,d));
  }
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 aacol = vec3(0);
  float AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      // Normalized pixel coordinates (from 0 to 1)
      vec2 z = (2.0 * (fragCoord+vec2(i,j)/AA) - iResolution.xy)/iResolution.y;
      if (halfspace) {
        z.y += 2.0;
        z *= 2.0/dot(z,z);
        z.y -= 1.0;
      }
      if (iMouse.x > 0.0) {
        vec2 m = (2.0 * iMouse.xy -iResolution.xy) / iResolution.y;
        // Map origin to m with an inversion
        float k = 1.0/dot(m,m);
        vec2 c = k*m; // m inverted in unit circle
        z -= c;
        z *= (k-1.0)/dot(z,z);
        z += c;
        z.x = -z.x;
      }
      z = rotate(z,0.1*iTime);
      float r2 = dot(z,z);
      vec3 col = vec3(0);
      if (r2 < 1.0) {
        vec3 p = vec3(2.0*z,1.0+r2)/(1.0-r2);
        col = color(p,iTime*0.2);
      }
      aacol += col;
    }
  }
  fragColor = vec4(aacol/(AA*AA),1.0);
}

float max_distance = 1000.;
float plank = .001;
int max_iter = 128;
float EPSILON = .001;




float displacement(vec3 p, float scale)
{
    return sin(scale*p.x)*sin(scale*p.y)*sin(scale*p.z);
}

float octahedronSDF( vec3 p, float s)
{
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}
float sceneSDF(vec3 p)
{
    return (length(p) - .8)*(cos(iTime)*.5+.5) + octahedronSDF(p,.8)*(cos(iTime+3.14)*.5+.5) + (sin(iTime * 2. - 3.14*.5)*.5+.5) * displacement(p,5.) / 3.;
}

vec3 estimateNormal(vec3 p)
{
	return normalize(vec3(
		sceneSDF(vec3(p.x+EPSILON,p.y,p.z)) - sceneSDF(vec3(p.x-EPSILON,p.y,p.z)),
		sceneSDF(vec3(p.x,p.y+EPSILON,p.z)) - sceneSDF(vec3(p.x,p.y-EPSILON,p.z)),
		sceneSDF(vec3(p.x,p.y,p.z+EPSILON)) - sceneSDF(vec3(p.x,p.y,p.z-EPSILON))
		));
}

vec3 calculateDeffuse(vec3 p)
{
	vec3 deffuse = vec3(0, 0, 0);
    vec3 normal = estimateNormal(p);
    vec3 lightdir = normalize(vec3(2.,2.,-3.) - p);
    float diff = max(dot(normal,lightdir),0.0);
    deffuse += diff * (1.,1.,1.);
	return deffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.x;
    vec3 rd = vec3(uv, 1.);

    vec3 ro = vec3(.0,0.,-3.);

    vec3 col = vec3(167, 147, 204);

    for(int iter=0; iter<max_iter; iter++)    
    {
        float curr = sceneSDF(ro);

        //Hit condition
        if(plank > curr)
        {
            col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
            col*= (.1 + calculateDeffuse(ro));
        }


        ro += rd*curr;
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

