float random( vec2 p )
{
    vec2 K1 = vec2(23.14069263277926, 2.665144142690225); // 23.14=e^pi (Gelfond's constant) et 2.66=2^sqrt(2) (GelfondâSchneider constant)
    return fract( cos( dot(p,K1) ) * 12345.6789 );
