// Fonction principale
// du Jeux de la Vie
// par John Conway vers l'annÃ©e 1965
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    if( iFrame == 0 ) { // Initialisation
        if(random(fragCoord) < 0.5) {
    		col = vec3(0.0, 0.0, 0.0);
        } else {
            col = vec3(1.0, 1.0, 1.0);
        }
    } else { // Execution
     	col = texelFetch( iChannel0, ivec2(fragCoord), 0 ).xyz;
        vec3 col_g = texelFetch( iChannel0, ivec2(fragCoord + vec2(-1.0, 0.0) ), 0 ).xyz;
        vec3 col_d = texelFetch( iChannel0, ivec2(fragCoord + vec2( 1.0, 0.0) ), 0 ).xyz;
        vec3 col_h = texelFetch( iChannel0, ivec2(fragCoord + vec2( 0.0, 1.0) ), 0 ).xyz;
        vec3 col_b = texelFetch( iChannel0, ivec2(fragCoord + vec2( 0.0,-1.0) ), 0 ).xyz;
        vec3 col_gb = texelFetch( iChannel0, ivec2(fragCoord + vec2(-1.0,-1.0) ), 0 ).xyz;
        vec3 col_db = texelFetch( iChannel0, ivec2(fragCoord + vec2( 1.0,-1.0) ), 0 ).xyz;
        vec3 col_gh = texelFetch( iChannel0, ivec2(fragCoord + vec2(-1.0, 1.0) ), 0 ).xyz;
        vec3 col_dh = texelFetch( iChannel0, ivec2(fragCoord + vec2( 1.0, 1.0) ), 0 ).xyz;

        int nbVivants = 0;
        if( col_g == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_d == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_h == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_b == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_gb == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_db == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_gh == vec3(1.0,1.0,1.0)) nbVivants++;
        if( col_dh == vec3(1.0,1.0,1.0)) nbVivants++;

        if(col == vec3(0.0, 0.0, 0.0) ) { // MORT
            if( nbVivants == 3) col = vec3(1.0, 1.0, 1.0); // NAISSANCE
        } else { // VIVANT
   			if( nbVivants < 2) col = vec3(0.0, 0.0, 0.0); // MORT PAR ISOLEMENT
            if( nbVivants == 2 || nbVivants == 3) col = vec3(1.0, 1.0, 1.0);
            if( nbVivants > 3) col = vec3(0.0, 0.0, 0.0); // MORT PAR SURPOPULATION
        }
    }    
    fragColor = vec4(col, 1.0); // Ecriture de la nouvelle cellule dans le buffer de sortie
}

