// http://www.cs.cmu.edu/~kmcrane/Projects/QuaternionJulia/paper.pdf

vec4 quatMult( vec4 q1, vec4 q2 )
{
    vec4 r;
    r.x = q1.x*q2.x - dot( q1.yzw, q2.yzw );
    r.yzw = q1.x*q2.yzw + q2.x*q1.yzw + cross( q1.yzw, q2.yzw );
    return r;
}

vec4 quatSq( vec4 q )
{
    vec4 r;
    r.x = q.x*q.x - dot( q.yzw, q.yzw );
    r.yzw = 2.*q.x*q.yzw;
    return r;
}

#define ESCAPE_THRESHOLD 1e1

void iterateIntersect( inout vec4 q, inout vec4 qp, vec4 c, int maxIterations )
{
    for( int i=0; i<maxIterations; i++ )
    {
        qp = 2.0 * quatMult(q, qp);
        q = quatSq(q) + c;
        if( dot( q, q ) > ESCAPE_THRESHOLD )
        {
            break;
        }
    }
}


float map(in vec3 p)
{
    vec4 z = vec4( p, 0 );
	vec4 zp = vec4( 1, 0, 0, 0 );
    float t = iTime * 0.1;
    vec4 c = 0.5*vec4(cos(t),cos(t*1.1),cos(t*2.3),cos(t*3.1));
	iterateIntersect( z, zp, c, maxIterations );
	float normZ = length( z );
	float d = 0.5 * normZ * log( normZ ) / length( zp ); //lower bound on distance to surface
    return d;
}


