         U = 5.*(u-.5*R)/R.y, U0 = U;

    U = fract(U)*2.-1.;                        // tiling
    float y = U0.y,
          v = length(U)-.5;                    // disks
    v /= fwidth(v);                            // antialiasing
    if (U0.x<0.) v = 1.-v;                     // left: change background instead
    if (abs(U0.x)<1.) y=0.;                    // central colum: blue instead or red/green
    O = mix(vec4( y>0., y<0., y==0., 0),       // bottom: green instead of red
            vec4( 0, 0, abs(U0.x)>2., 0),      // mid columns: black instead of blue
            clamp(v,0.,1.));

    if ( abs(y)>1.5 && (int(u.x/2.)+int(u.y/2.)) % 2 == 0 ) O *= .8;
}

