#define BIASED_NORMAL 	1
#define MAX_BOUNCES     4
#define MAX_DIST		4.
#define ABSORB			vec3(0., 0.5, 1.3)

const int maxIterations = 6;

// http://www.cs.cmu.edu/~kmcrane/Projects/QuaternionJulia/paper.pdf

vec4 quatMult(vec4 q1, vec4 q2)
{
    vec4 r;
    r.x = q1.x * q2.x - dot(q1.yzw, q2.yzw);
    r.yzw = q1.x * q2.yzw + q2.x * q1.yzw + cross(q1.yzw, q2.yzw);
    return r;
}

vec4 quatSq(vec4 q)
{
    vec4 r;
    r.x = q.x * q.x - dot( q.yzw, q.yzw );
    r.yzw = 2. * q.x * q.yzw;
    return r;
}

#define ESCAPE_THRESHOLD 10.

float JuliaQuaternion(vec3 p, vec4 c)
{
    vec4 q = vec4( p, 0 );
	vec4 qp = vec4( 1, 0, 0, 0 );
    for(int i = 0; i < maxIterations; i++)
    {
        qp = 2.0 * quatMult(q, qp);
        q = quatSq(q) + c;
        if( dot( q, q ) > ESCAPE_THRESHOLD )
        {
            break;
        }
    }
	float nQ = length( q );
    return 0.5 * nQ * log( nQ ) / length( qp ); //lower bound on distance to surface;
}

float map(in vec3 p)
{
    float t = iTime * 0.1;
    vec4 c = 0.5 * vec4(cos(t), cos(t * 1.1), cos(t * 2.3), cos(t * 3.1));
	return JuliaQuaternion(p, c);
}


vec3 getSkyColor(vec3 rd)
{
    vec3 col = texture(iChannel0, rd).rgb;
    return col*col;
}


float rayMarch(in float sgn, in vec3 ro, in vec3 rd, in float offT)
{
  float t = offT;
  for (int i = 0; i < 60; i++)
  {
    float h = sgn * map(ro + rd * t);
    t += h;
    if (h < 0.001 || t > MAX_DIST)
        break;
  }
  return t;
}

#define EPS  0.02

#if BIASED_NORMAL
vec3 calcNormal(vec3 pos)
{
    float	ref;
    float	trans;
    vec3	absorb;
    vec3	col;
    vec2	eps = vec2(EPS, 0);
	float d = map(pos);
	return normalize(vec3(map(pos + eps.xyy) - d, map(pos + eps.yxy) - d, map(pos + eps.yyx) - d));
}
#else
vec3 calcNormal( in vec3 pos )
{
    const float ep = EPS;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}
#endif

vec3 Render(in vec3 ro, in vec3 rd)
{
    float sgn = 1.;
    float cref = 0.7;
    vec3  col = vec3(0);
    vec3  rel = vec3(1);
    float transp = 0.9;
    vec3  absorb = ABSORB;
    for(int i = 0; i < MAX_BOUNCES; i++)
    {
        float t = rayMarch(sgn, ro, rd, 0.02);
        if( t> MAX_DIST)
        {
            col += rel * getSkyColor(rd);
            break;
        }
        vec3 rabs = mix(absorb, vec3(0), (sgn + 1.) / 2.);
        vec3 beerlamb = exp(-rabs * t);
        vec3 p = ro + rd * t;
        vec3 n = sgn * calcNormal(p);
        vec3 refl = reflect(rd, n);
        vec3 refr = refract(rd, n, cref);
        //float fresnel = 1.0 - pow(dot(n, -rd), 2.);
        float fresnel = pow(1.0 - abs(dot(n, rd)), 2.0);
        float reflectorFactor = mix (0.2, 1.0, fresnel);
        float refractionFactor = mix (transp, 0., fresnel);

    	col += (1.0 - refractionFactor) * rel * beerlamb * getSkyColor(refl) * reflectorFactor;
    	rel *= refractionFactor * beerlamb;     

       	ro = p;     
        if (refr == vec3(0.0))
        {
            rd = refl;
        }
        else
        {
            rd = refr; 
            sgn *= -1.;
            cref = 1. / cref;
        }        
    }
    col += rel * getSkyColor(rd);
    return col;
}

vec3 contrast( in vec3 color, in float c)
{
    float t = 0.5 - c * 0.5; 
    return color * c + t;
}

vec3 vignette(vec3 color, vec2 q, float v)
{
    color *= 0.3 + 0.8 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), v);
    return color;
}

vec3 postProcess(in vec3 col, in vec2 q)
{
	col = pow(col, vec3(0.8));
	col = contrast(col, 1.2);
	col = vignette(col, q, 0.8);
	return col;
}


