vec4 image(vec2 U) {
    ivec2 pos = ivec2(U);
    if(pos.x>511) return vec4(0);
    if(pos.y>0) return texelFetch(iChannel0,pos-ivec2(0,1),0);
    float acc1 = 0.0;
    #ifdef INACCURATE
    for(int i = 0; i <= pos.x; i++) {
        acc1 += texelFetch(iChannel3,ivec2(i,0),0).x;
    }
    #else
    for(int i1 = 0; i1 <= pos.x;) {
        float acc2 = 0.0;
        for(int i2 = 0; i2 < 2 && i1 <= pos.x; i2++) {
            float acc3 = 0.0;
            for(int i3 = 0; i3 < 2 && i1 <= pos.x; i3++) {
                float acc4 = 0.0;
                for(int i4 = 0; i4 < 2 && i1 <= pos.x; i4++) {
                    float acc5 = 0.0;
                    for(int i5 = 0; i5 < 2 && i1 <= pos.x; i5++) {
                        float acc6 = 0.0;
                        for(int i6 = 0; i6 < 2 && i1 <= pos.x; i6++) {
                            float acc7 = 0.0;
                            for(int i7 = 0; i7 < 2 && i1 <= pos.x; i7++) {
                                float acc8 = 0.0;
                                for(int i8 = 0; i8 < 2 && i1 <= pos.x; i8++) {
                                    float acc9 = 0.0;
                                    for(int i9 = 0; i9 < 2 && i1 <= pos.x; i9++, i1++) {
                                        acc9 += texelFetch(iChannel3,ivec2(i1,0),0).x;
                                    }
                                    acc8 += acc9;
                                }
                                acc7 += acc8;
                            }
                            acc6 += acc7;
                        }
                        acc5 += acc6;
                    }
                    acc4 += acc5;
                }
                acc3 += acc4;
            }
            acc2 += acc3;
        }
        acc1 += acc2;
    }
    #endif
    acc1 /= floor(U.x+1.);
    return vec4(texelFetch(iChannel3,ivec2(pos.x,0),0).x,0,acc1,0);
}
