        float curr = max(tex.x-texelFetch(iChannel0,ivec2(511,0),0).z,0.);
        float prev = max(texelFetch(iChannel0,ivec2(U.x,1),0).x-texelFetch(iChannel0,ivec2(511,1),0).z,0.);
        O.rgb = vec3(curr,max(curr-prev,0.),prev) * vec3(2,8,1) + vec3(0,0.75*O.g,0);
        O = clamp(O,0.,1.);
    }
    return O;
}
