vec4 image(vec2 U) {
    ivec2 p = ivec2(U);
    vec4 O = vec4(0);
    if(p.y==0) {
        int which = int(U.x*16./R.x);
        p.x = int(mod(U.x*16.,R.x)/R.x*512.);
        vec4 curr1 = texelFetch(iChannel0,ivec2(p.x,0),0);
        vec4 curr2 = texelFetch(iChannel0,ivec2(511,0),0);
        vec4 prev1 = texelFetch(iChannel0,ivec2(p.x,1),0);
        vec4 prev2 = texelFetch(iChannel0,ivec2(511,1),0);
        float tC = curr2.z;
        float lC = curr1.z;
        float rC = (tC*512.-lC*float(p.x+1))/max(512.-float(p.x+1),1.);
        float tP = prev2.z;
        float lP = prev1.z;
        float rP = (tP*512.-lP*float(p.x+1))/max(512.-float(p.x+1),1.);
        O.r=-0.25/16.;
        int which1=0;
        if(which==  0) {which1=  0;O.r = 0.125*max(tC-rC,0.);}
        if(which==  1) {which1=  1;O.r = max(tC-tP,0.);}
        if(which==  2) {which1=  2;O.r = 0.125*max(tC-rP,0.);}
        if(which==  3) {which1=  3;O.r = 0.125*max(lC-rC,0.);}
        if(which==  4) {which1=  4;O.r = 0.125*max(lC-tP,0.);}
        if(which==  5) {which1=  5;O.r = max(lC-lP,0.);}
        if(which==  6) {which1=  6;O.r = 0.125*max(lC-rP,0.);}
        if(which==  7) {which1=  7;O.r = max(rC-rP,0.);}
        if(which==  8) {which1=  8;O.r = 0.125*max(tP-rP,0.);}
        if(which==  9) {which1=  9;O.r = 0.125*max(lP-rP,0.);}
        if(which== 10) {which1= 10;O.r = 0.125*max(lC-tC,0.);}
        if(which== 11) {which1= 11;O.r = max(tP-tC,0.);}
        if(which== 12) {which1= 12;O.r = 0.125*max(lP-tC,0.);}
        if(which== 13) {which1= 13;O.r = max(lP-lC,0.);}
        if(which== 14) {which1= 14;O.r = max(rP-rC,0.);}
        if(which== 15) {which1= 15;O.r = 0.125*max(lP-tP,0.);}

        O.rgb = 32.*O.r+vec3(0.25);
        if((which1&7)==0) O.rgb *= 1.*vec3(3,0,1)*0.25+0.25;
        if((which1&7)==1) O.rgb *= 1.*vec3(3,1,0)*0.25+0.25;
        if((which1&7)==2) O.rgb *= 1.*vec3(3,2,0)*0.25+0.25;
        if((which1&7)==3) O.rgb *= 1.*vec3(1,3,0)*0.25+0.25;
        if((which1&7)==4) O.rgb *= 1.*vec3(0,3,2)*0.25+0.25;
        if((which1&7)==5) O.rgb *= 1.*vec3(0,2,3)*0.25+0.25;
        if((which1&7)==6) O.rgb *= 1.*vec3(0,1,3)*0.25+0.25;
        if((which1&7)==7) O.rgb *= 1.*vec3(2,0,3)*0.25+0.25;

        //        O.rgb = vec3(0.0625*curr.x,8.0*abs(curr.x-prev.x),0.0625*max(curr.x-curr.z,0.));
    } else {
        vec4 next = texelFetch(iChannel1,p-ivec2(0,1),0);
        O.rgb = next.xyz;
    }
    O.a = 0.0;
    O = clamp(O,0.,1.);
    //O = floor(max(2.-abs(texelFetch(iChannel0,ivec2(U.x,1),0).yxzw*R.y-U.y),0.));
    //O = p.y==0 ? texelFetch(iChannel0,uv,0) : vec4(texelFetch(iChannel1,ivec2(U.x,U.y-1.),0).x,O.yzw);
    return O;
}
