void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float aspect_ratio = iResolution.x / iResolution.y;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= aspect_ratio;

	vec2 mouse = iMouse.xy / iResolution.xy;
	mouse-=.5;
	mouse.x *= iResolution.x / iResolution.y;

	vec2 mouse2 = iMouse.zw / iResolution.xy;
	mouse2-=.5;
	mouse2.x *= iResolution.x / iResolution.y;

	const float line_width = .001;
	const float curve_width = .001;
	const float dot_size = .005;

	const float axis_width = .005;

	const vec3 bg_col = vec3(1,1,1);
	const vec3 axis_col = vec3(0,0,0);
	const vec3 line_col = vec3(0,1,0);
	const vec3 second_line_col = vec3(1,1,0);
	const vec3 curve_col = vec3(0,0,1);
	const vec3 dot_col = vec3(1,0,0);
	const vec3 text_col = vec3(0,0,0);

    border = 2./iResolution.y;

	float curve_dis = 1e38;
	float second_curve_dis = 1e38;
	float dot_dis = 1e38;
	float axis_dis = 1e38;
	float line_dis = 1e38;
	float second_line_dis = 1e38;
	float text_dis_sq = 1e38;
	float text_dis = 1e38;

	//elliptic curve parameters (weierstrass normal form)
	float g_2 = .3;
	float g_3 = -.05;

	//initial approximations for various points (not exactly lying on the curve)
	//you can play around with these values, but the gradient descent
	//can get unstable if the points are too far away from the curve

	vec2 p0=vec2(0.043,-0.193); //0: the inflection point
	vec2 p1=vec2(-.3,.05*sin(mod(iTime,6.28318530717959))); //p
	vec2 p2=vec2(.1,-.1); //q

	//compute points lying exactly on the curve by gradient descent
	const int grad_descent_iterations=8;
	float p0_val, hesse_p0_val, p1_val, p2_val;
	vec2 p0_grad, hesse_p0_grad, p1_grad, p2_grad;

	for(int i=ZERO;i<grad_descent_iterations;i++){
		p0_val = p0.y*p0.y - (( 4.*p0.x*p0.x - g_2 ) * p0.x - g_3);
		p0_grad = vec2(-12.*p0.x*p0.x+g_2,2.*p0.y);
		p0-=(p0_grad*p0_val)/dot(p0_grad,p0_grad);

		//compute inflection point by alternately gradient descenting to curve and hessian of curve
		hesse_p0_val = -96.*g_2*p0.x*p0.x + 96.*p0.x*p0.y*p0.y - 8.*g_2*g_2 - 288.*g_3*p0.x;
		hesse_p0_grad = vec2(-192.*g_2*p0.x + 96.*p0.y*p0.y - 288.*g_3,192.*p0.x*p0.y);
		p0-=(hesse_p0_grad*hesse_p0_val)/dot(hesse_p0_grad,hesse_p0_grad);

		p1_val = p1.y*p1.y - (( 4.*p1.x*p1.x - g_2 ) * p1.x - g_3);
		p1_grad = vec2(-12.*p1.x*p1.x+g_2,2.*p1.y);
		p1-=(p1_grad*p1_val)/dot(p1_grad,p1_grad);

		p2_val = p2.y*p2.y - (( 4.*p2.x*p2.x - g_2 ) * p2.x - g_3);
		p2_grad = vec2(-12.*p2.x*p2.x+g_2,2.*p2.y);
		p2-=(p2_grad*p2_val)/dot(p2_grad,p2_grad);
	}

	vec2 p3=third_intersection(p1,p2,g_2,g_3); //p*q
	vec2 p4=third_intersection(p0,p3,g_2,g_3); //p+q
	vec2 p5=third_intersection(p0,p1,g_2,g_3); //-p

	dot_dis=min(dot_dis,distance(p0,uv)-dot_size); //0
	dot_dis=min(dot_dis,distance(p1,uv)-dot_size); //p
	dot_dis=min(dot_dis,distance(p2,uv)-dot_size); //q
	dot_dis=min(dot_dis,distance(p3,uv)-dot_size); //p*q
	dot_dis=min(dot_dis,distance(p4,uv)-dot_size); //p+q
	dot_dis=min(dot_dis,distance(p5,uv)-dot_size); //-p

	vec3 l0=line(p1,p2);
	vec3 l1=line(p0,p4);

	line_dis=min(line_dis,abs(dot(vec3(uv,1),l0)));
	line_dis=min(line_dis,abs(dot(vec3(uv,1),l1)));

	vec3 l2=line(p1,p5);

	p0_grad = vec2(-12.*p0.x*p0.x+g_2,2.*p0.y);
	vec3 tangent=vec3(normalize(p0_grad),0);
	tangent.z=-dot(p0,tangent.xy);

	second_line_dis=min(second_line_dis,abs(dot(vec3(uv,1),l2)));
	second_line_dis=min(second_line_dis,abs(dot(vec3(uv,1),tangent)));

	float uv_val = uv.y*uv.y - (( 4.*uv.x*uv.x - g_2 ) * uv.x - g_3);
	vec2 uv_grad = vec2(-12.*uv.x*uv.x+g_2,2.*uv.y);

	curve_dis=min(curve_dis, abs(uv_val)/length(uv_grad)-curve_width);

	axis_dis=min(axis_dis, abs(uv.x));
	axis_dis=min(axis_dis, abs(uv.y));

	vec2[4] p_positions=vec2[](vec2(.03,.05)-p1,vec2(-.04,-.03)-p3,vec2(.13,.03)-p4,vec2(.03,.03)-p5);
	vec2[3] q_positions=vec2[](vec2(-.01,-.05)-p2,p_positions[1]+vec2(-.1,.0),p_positions[2]+vec2(-.1,.0));

	vec2 plus_position=p_positions[2]+vec2(-.05,1e-7);
	vec2 minus_position=p_positions[3]+vec2(.05,1e-7);
	vec2 zero_position=vec2(.003,.0425)-p0;
	vec2 asterisk_position=p_positions[1]+vec2(-.05,0.);

	text_dis_sq=min(text_dis_sq,plus_dis_sq(uv+plus_position));
	text_dis_sq=min(text_dis_sq,minus_dis_sq(uv+minus_position));
	text_dis_sq=min(text_dis_sq,asterisk_dis_sq(uv+asterisk_position));
	text_dis_sq=min(text_dis_sq,zero_dis_sq(uv+zero_position));

	for(int i=ZERO;i<4;i++){
		text_dis_sq=min(text_dis_sq,p_dis_sq(uv+p_positions[i]));
	}
	for(int i=ZERO;i<3;i++){
		text_dis_sq=min(text_dis_sq,q_dis_sq(uv+q_positions[i]));
	}

	text_dis=sign(text_dis_sq)*sqrt(abs(text_dis_sq));

	vec3 color = bg_col;

	color=vec3(mix(axis_col,color,smoothstep(0., border, axis_dis)));
	color=vec3(mix(line_col,color,smoothstep(0., border, line_dis)));
	color=vec3(mix(second_line_col,color,smoothstep(0., border, second_line_dis)));
	color=vec3(mix(curve_col,color,smoothstep(0., border, curve_dis)));
	color=vec3(mix(dot_col,color,smoothstep(0., border, dot_dis)));
	color=vec3(mix(text_col,color,smoothstep(-border/2., border/2., text_dis)));

	fragColor = vec4(color,1);
}

////////////////////////////////////////////////////////////////////////////////
//
// Projective Conic Envelope
//
// Copyright (c) Matthew Arcus, 2020
// MIT License: https://opensource.org/licenses/MIT
//
// More projective magic - generate an ellipse as an envelope of lines.
// In fact, we start with the ellipse and find a network of lines
// enveloping it: at each point p, find tangents to the ellipse,
// intersect these with a fixed axis at x = -3 - this will be the
// source of the rays. From axis intersection point, map to a radial
// parameter, then find closest displayed ray, map that back to the
// axis and find the tangents from there & these are the lines that
// actually get displayed.
//
// Mouse changes size and orientation of ellipse.
//
////////////////////////////////////////////////////////////////////////////////

const float PI =  3.141592654;

vec3 join(vec3 p, vec3 q) {
  // Return either intersection of lines p and q
  // or line through points p and q, r = kp + jq
  return cross(p,q);
}

float line(vec3 p, vec3 q) {
  return abs(dot(p,q)/(p.z*length(q.xy)));
}

// Set tan1 and tan2 to the two tangents to conic X from point p.
// Return false if no tangents (eg. inside an ellipse).
bool tangents(vec3 p, mat3 X, out vec3 tan1, out vec3 tan2) {
  vec3 polar = X*p; // Line between tangents
  float a = polar.x, b = polar.y, c = polar.z;
  // Two points on the polar line. Q is the nearest point to origin,
  // R is at infinity, ie. is direction vector.
  vec3 Q = vec3(a,b,-(a*a+b*b)/c);
  vec3 R = vec3(-b,a,0);

  // Find intersection of QR with conic, ie. dot(Q+kR,X*(Q+kR)) = 0
  float A = dot(R,X*R), B = dot(Q,X*R), C = dot(Q,X*Q);
  float D = B*B-A*C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float k1,k2;
  if (B > 0.0) {
    k1 = (-B-D)/A; k2 = C/(A*k1);
  } else {
    k2 = (-B+D)/A; k1 = C/(A*k2);
  }
  tan1 = join(p,Q+k1*R); tan2 = join(p,Q+k2*R);
  return true;
}

vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 3.0;
  float t = 0.2*iTime+PI/6.0;
  float A = 0.25, B = 1.0;
  if (iMouse.x > 0.0) {
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    t += PI*(m.y);
    B = exp(-m.x);
  }
  float cost = cos(t), sint = sin(t);
  // Conic matrix - conic is points p with pXp = 0
  mat3 X = mat3(A,0,0,0,B,0,0,0,-1);
  // Apply tranformation to conic matrix.
  mat3 P = mat3(cost,sint,0, -sint,cost,0, 0,0,1);
  X = transpose(P)*X*P;

  vec3 p = vec3(scale*(2.0*fragCoord - iResolution.xy)/iResolution.y,1);

  vec3 col = vec3(0);

  float lwidth0 = 0.0;
  float lwidth1 = max(0.02,fwidth(p.x));

  vec3 tan1,tan2,tan11,tan12,tan21,tan22;
  if (tangents(p,X,tan1,tan2)) {
    float N = 64.0;
    vec3 axis = vec3(1,0,3);
    float index1,index2;

    vec3 p1 = join(tan1,axis);
    float t1 = atan(p1.y/p1.z);
    t1 += 0.1*iTime;
    t1 *= N/PI; t1 = round(t1); index1 = t1; t1 *= PI/N;
    t1 -= 0.1*iTime;
    p1 = vec3(-3,tan(t1),1);
    p1.y = sign(p1.y)*min(abs(p1.y),1e4); // Fix up silly values
    if (tangents(p1,X,tan11,tan12)) {
      vec3 c1 = hsv2rgb(vec3(index1/N,1,1));
      float d = line(p,tan11);
      col = mix(c1,col,smoothstep(lwidth0,lwidth1,d));
      float tt = dot(normalize(tan2.xy),normalize(tan12.xy));
      // Try not to get the wrong tangent here. There must be a better
      // way of doing this.
      if (abs(tt) < 0.99) {
        d = line(p,tan12);
        col = mix(c1,col,smoothstep(lwidth0,lwidth1,d));
      }
    }

    vec3 p2 = join(tan2,axis);
    float t2 = atan(p2.y/p2.z);
    t2 += 0.1*iTime;
    t2 *= N/PI; t2 = round(t2); index2 = t2; t2 *= PI/N;
    t2 -= 0.1*iTime;
    p2 = vec3(-3,tan(t2),1);
    p2.y = sign(p2.y)*min(abs(p2.y),1e4); // Fix up silly values
    if (tangents(p2,X,tan21,tan22)) {
      vec3 c2 = hsv2rgb(vec3(index2/N,1,1));
      float d = line(p,tan22);
      col = mix(c2,col,smoothstep(lwidth0,lwidth1,d));
      float tt = dot(normalize(tan1.xy),normalize(tan21.xy));
      // Try not to get the wrong tangent here.
      if (abs(tt) < 0.99) {
        d = line(p,tan21);
        col = mix(c2,col,smoothstep(lwidth0,lwidth1,d));
      }
    }
  }
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb*3.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

