void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(floor(pos));
    if(any(greaterThan(p, sN*N-1))) discard;

    ivec3 sid = xy2i(p); ID = sid.x;
    obj = getObject(ID);
   	if(sid.z == 0) //object computation
    {    
        if(sid.y > 3) discard;

        //compute the force and the torque
        //TODO
        vec3 F = -0.0002*obj.X;

        obj.P += F*dt;
        obj.X += obj.P*dt/obj.m;

        if(iFrame<1) //initial conditions
        {
            obj.X = 2.*(hash33(pos.xyx + pos.yyx*4.5743) - 0.5);
            obj.P = 0.01*2.*(hash33(pos.yxy + pos.xxy*PI) - 0.5);
            obj.L = vec3(0.);
            obj.q = vec4(0.);
            obj.type = 0;
            obj.m = 1.;
        } 

        U = saveObject(sid.y);
    }
    else //graph computation
    {
        d = vec4(1e6);
        lnk = vec4(-1);

        sortlinks(ID, sid.y, sid.y);

        sortneighbors(ID, sid.y);

        sortrandom(sid.x, sid.y);

        U = lnk;
    }
}

vec4 getParticlePos(int id)
{ 
    return texel(ch0, i2xy(ivec3(id, 0, 0)));
}

float dist2p(vec3 p, int id)
{
    return distance(getParticlePos(id).xyz,p);
}

bool iscoincidence(int id)
{
    bool c = (id == ID) || (id < 0) || any(equal(lnk,vec4(id)));
    return c;
}

void sort(int idtemp, int R) //sort closest objects in N directions
{
    if(iscoincidence(idtemp)) return; //particle already sorted
    vec4 px = getParticlePos(idtemp);
    vec3 dir = fibonacci_sphere(R, link0);
    float t = dirBisectDist(dir, obj.X, px.xyz);
    //insertion sort
    if(d.x > t)
    {
        d = vec4(t, d.xyz);
        lnk = vec4(idtemp, lnk.xyz);
    }
    else if(d.y > t && t > d.x)
    {
        d.yzw = vec3(t, d.yz);
        lnk.yzw = vec3(idtemp, lnk.yz);
    }
    else if(d.z > t && t > d.y)
    {
        d.zw = vec2(t, d.z);
        lnk.zw = vec2(idtemp, lnk.z);
    }
    else if(d.w > t && t > d.z)
    {
        d.w = t;
        lnk.w = float(idtemp);
    }
}

void sortlinks(int id, int R, int R0)
{
    vec4 nb = texel(ch0, i2xy(ivec3(id, R, 1)));
    for(int i = min(0, iFrame); i<4; i++) 
        sort(int(nb[i]), R0);
}

void sortneighbors(int id, int R)
{
    vec3 dir = fibonacci_sphere(R, link0);
    vec4 d4 = iSF(dir, float(link0));
    for(int i = min(0, iFrame); i < 4; i++)
    {
         int dir = int(d4[i]);
         vec4 nb = texel(ch0, i2xy(ivec3(id, dir, 1)));
    	 for(int j = min(0, iFrame); j < 4; j++) 
         {
         	sortlinks(int(nb[j]), (16*iFrame+4*j+i)%link0, R); 
         }
    }  
}

void sortrandom(int id, int R)
{
    for(int i = min(0, iFrame); i<2; i++) sort((12*iFrame+ID+i)%tot_n, R);
}

