//prev closest particle buffer

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

float bisectDist(vec3 p, vec3 a, vec3 b)
{
    return distance(a,b)*0.5 - dot(p - a, normalize(b - a));
}

void check_neighbors(vec3 p, inout vec3 cxy, inout int cid)
{
    int dir = int(inverseSF(normalize(p - cxy), float(link0)).x);
    float t = distance(p, cxy);
    vec4 nb = texel(ch0, i2xy(ivec3(cid, dir, 1)));
    for(int i = min(0,iFrame); i<4; i++)
    {
        vec3 X = texel(ch0, i2xy(ivec3(nb[i], 0, 0))).xyz;
        float tt = distance(p, X);
        if(tt < t)
        {
            t = tt;
            cxy = X;
            cid = int(nb[i]);
        } 
    }
}

//find closest voronoi cell 
void findClosest(vec3 p, inout vec4 cobj, int mit)
{    
    int cid = int(cobj.w);
    vec3 cxy = cobj.xyz;

    for(int s = min(0,iFrame); s < mit; s++)
    {
    	int pid = cid;
        check_neighbors(p, cxy, cid);          
        if(pid == cid) break;
    }

    cobj = vec4(cxy,cid);
}

