vec4 rand3d(vec3 uv)
{
    float pz=uv.z*256.;
    float z=floor(pz);
    vec2 uv1=uv.xy+z*vec2(17,31)/256.;
    vec2 uv2=uv1+vec2(17,31)/256.;
    return mix(textureLod(RandTex,uv1,0.),textureLod(RandTex,uv2,0.),pz-z);
}

vec4 getRand(vec2 coord)
{
    vec4 c=vec4(0);
    c+=texture(RandTex,coord+.003*iTime);
    c+=texture(RandTex,coord/2.+.003*iTime)*2.;
    c+=texture(RandTex,coord/4.+.003*iTime)*4.;
    c+=texture(RandTex,coord/8.+.003*iTime)*8.;
    return c/(1.+2.+4.+8.);
}

void getTriSubDiv(vec3 pos, inout vec3 p1, inout vec3 p2, inout vec3 p3)
{
    mat3 m = mat3((p2-p1)*.5,(p3-p1)*.5,p1);
    vec3 q = inverse(m)*pos;
    q/=q.z;
    vec3 qf=floor(q);
    float f=step(1.,q.x-qf.x+q.y-qf.y);
    p1 = m*vec3( qf.xy+f,            1 );
    p2 = m*vec3( qf.xy+vec2(1.-f,f), 1 );
    p3 = m*vec3( qf.xy+vec2(f,1.-f), 1 );
    p1=normalize(p1);
    p2=normalize(p2);
    p3=normalize(p3);
}

void getOctaTri(vec3 dir, inout vec3 p1, inout vec3 p2, inout vec3 p3)
{
    vec3 s=sign(dir); p1=vec3(s.x,0,0); p2=vec3(0,s.y,0); p3=vec3(0,0,s.z);
    if(s.x*s.y*s.z>0.) { s=p2;p2=p3;p3=s; }
}

vec3 galaxy(vec3 dir, vec3 p1, vec3 p2, vec3 p3, vec2 offs, float rot, float quench, float size)
{
    vec3 pm=normalize(p1+p2+p3);
    vec3 bx=normalize(p2-p1);
    vec3 bz=normalize(cross(p2-p1,p3-p1));
    vec3 by=normalize(cross(bz,bx));
    //vec3 by=normalize(p3-(p1+p2)*.5);
    dir=(dir-pm)*mat3(bx,by,bz);
    float lavg=sqrt(length(cross(p2-p1,p3-p1)));
    dir.xy-=offs*lavg;
    float ang=atan(dir.y,dir.x);
    dir=rotZ(rot,dir);
    dir*=vec3(1./(1.-quench),1,1);
    float l=length(dir.xy);
    lavg*=size;
    float m=(1.-smoothstep(.0,.2*lavg,l))*(exp2(-(l/lavg))+.5*abs(cos(l/lavg*60.+ang)));
    // fade out smaller tha 1 pixel galaxies
    m*=min(.35/fwidth(l/(.2*lavg)),1.);
    return vec3(m);
}

vec3 mySpaceEnv(vec3 dir)
{
    vec3 p1,p2,p3;
    vec3 col=vec3(0);
    dir=(normalize(dir));
    vec3 bx=normalize(vec3(1,2,3)), by=normalize(bx.yxz*vec3(-1,1,0)), bz=cross(bx,by);
    mat3 m=mat3(bx,by,bz);
    getOctaTri(dir,p1,p2,p3);
    vec3 c = normalize((p1+p2+p3)), cp=vec3(0);
    vec2 offs; float rot; float quench=0.;
    vec3 br=vec3(1.);
    float size=1.;
    vec3 allbr=vec3(1.);
    for(int i=0;i<10;i++)
    {
        c = normalize(p1+p2+p3);
        vec3 rc=m*c;
        if (abs(rc.z)<abs(rc.y) || abs(rc.z)<abs(rc.x)) rc=rc.zxy;
        if (abs(rc.z)<abs(rc.y) || abs(rc.z)<abs(rc.x)) rc=rc.zxy;
        vec2 ruv=(rc.xy/rc.z*.5+.5)*7.;
        vec4 r=(textureLod(RandTex,ruv,0.));
        vec4 r2=(textureLod(RandTex,ruv+.3,0.));
        offs=(r.xy-.5)*.3;
        rot=r.y*PI2;
        quench=r.z;
        size=1.3*r.w;
        allbr=br*(r2.xyz*.5+1.);
        if(length(cp-c)>.0001) 
            col+=allbr*galaxy(dir,p1,p2,p3,offs,rot,quench,size);
        getTriSubDiv(dir, p1, p2, p3);
        cp=c;
        br*=.97*vec3(.9,.86,.82); // decay with distance and red shift for more distant galaxies
    }
    col+=allbr*galaxy(dir,p1,p2,p3,offs,rot,quench,size);
    return col;
}

