// environment just a sky and some dark floor
vec4 myenv(vec3 pos, vec3 dir, float period_)
{
    #ifndef ENABLE_MATERIALS
        vec3 light=normalize(getLightDir());
        float d=max(0.,dot(light,dir));
        vec3 bx=normalize(vec3(light.yx*vec2(1,-1),0));
        vec3 by=normalize(cross(bx,light));
        vec2 d2=vec2(dot(dir,by),dot(dir,bx));
        float ang=atan(d2.y,d2.x);
        vec4 r=textureLod(RandTex,vec2(ang,length(d2)*2.),0.);
        float ampl=mix((.7+.3*sin(ang+24.*length(d2)))*(r.x*.4+.8),1.,exp2(-dot(d2,d2)*40.));
        return vec4(
            vec3(1,.8,.5)*ampl*1.2*(4.*pow(d,20.)+1.*pow(d,4.))+mySpaceEnv(dir)
            ,1);
    #endif
    vec3 sun = normalize(getLightDir());
    vec3 skyPos=pos+dir/abs(dir.z)*(120.-pos.z);
    float cloudPat=(1.+.4*(getRand(skyPos.xy*.0002).x-.5));
    vec3 colHor=vec3(.3,.4,.5)+.4;
    float dirl=dot(dir,sun);
    vec3 clouds=mix(vec3(1.)*(1.-2.*dirl),vec3(.8,1.,1.2),cloudPat);
    vec3 colSky=mix(vec3(1.5,.75,0.)*3.,clouds,clamp(7.*dir.z,0.,1.));
    //colSky=mix(colSky,vec3(1),cloudPat);
    //colSky*=mix(1.,cloudPat,dir.z*5.);
    vec3 colFloor=vec3(.45);

    vec3 col=mix(colSky,colFloor,1.-smoothstep(-.01,.01,dir.z));
    col=mix(colHor,col,clamp(abs(dir.z*5.)-.1,0.,1.));

    col*=.9;

    //float sunang=acos(dot(dir,sun));
    float sunang=atan(length(cross(dir,sun)),dot(dir,sun));
    col+=15.*clamp(2.*exp(-sunang/.02),0.,1.);
    col+=2.*clamp(2.*exp(-sunang/.20),0.,1.);

    return vec4(col,1);
}


float distTire(vec3 p, float r)
{
    p=abs(p);
    float d=1000.;
    d=min(d,length(p)-r);
    float ang = atan(p.z,p.y);
    float l=length(p.zy);
    p.x+=cos(ang*100.)*.005*smoothstep(.87*r,1.*r,l);
    d=max(d,distTorus(p.yzx+vec3(0,0,.03),r*.78,r*.28));
    d=max(d,-l+r*.61);
    float dx=.04;
    float xfr=mod(p.x,dx);
    float x=p.x-xfr+dx*.5;
    d=max(d,-distTorus(p.yzx-vec3(0,0,x),sqrt(r*r-x*x),.01));
    return d;
}


float distRim(vec3 p, float r)
{
    r*=.6;
    vec3 p0=p;
    p.x=abs(p.x);
    //p.yz=(p.y>p.z)?p.zy:p.yz; // only first 1/8 segment
    p=p.zxy;
    float d=1000.;
    d=min(d,sdRoundedCylinder(p,r,.01,.6*r));
    p-=vec3(0,.6*r,0);
    d=-smin(-d,sdRoundedCylinder(p,.97*r,.01,.6*r),.015);
    d=-smin(-d,sdRoundedCylinder(p,.89*r,.01,.8*r),.015);
    float d_i=sdRoundedCylinder(p,.77*r,.01,1.9*r);
    d=-smin(-d,d_i,.015);

    float d2=length(p0-vec3(-r*.6,0,0))-r*1.05;
    d2=max(d2,d_i);
    d2=-smin(-d2,sdRoundedCylinder(p,.4*r,.01,.8*r),.1);
    d2=abs(d2)-.005;
    d2=max(d2,-p0.x);

    float mang,ang;
    float ang0 = atan(p.z,p.x);
    float dang=PI2/5.;
    mang=mod(ang0,dang);
    ang=ang0-mang+dang*.5;

    d2=-smin(-d2,(length(p.xz-.95*r*cos(ang-vec2(0,1.57)))-.25*r),.01);
    d=min(d,d2);
    dang=PI2/5.;
    mang=mod(ang0,dang);
    ang=ang0-mang+dang*.5;
    d=min(d,max(abs(p.y+.6*r)-.22*r,(length(p.xz-.3*r*cos(ang-vec2(0,1.57)))-.05*r)));
    d=min(d,sdRoundedCylinder(p-vec3(0,-.23,0),.1*r,.01,.25));
    float l=length(p0.zx);
    d=min(d,sdRoundedCylinder(p0.zxy-vec3(0,-.06,0),.7*r,.02,.05-.015*fermi((length(p.xz)-.6*r)/.003)));
    return d;
}

