float lorentz(float x) { return 1./(1.+x*x); }

vec2 distM(vec3 p)
{
    float d=1000., mat=-1., d_mat=d;

    if(enable_car)
    {
        vec2 dm=distCarM(carTrafo(p)*vec3(1,-1,1));
        d=dm.x; mat=dm.y; d_mat=d;
    }

    if(enable_terr)
    {
        d=min(d,distTerr(p));
    }

    SET_PREV_MAT(FLOOR);

    //#define DEBUG
    #ifdef DEBUG
    vec3 accFrontAxe = texelFetch(PhysTex,ivec2(7,0),0).xyz;
    vec3 accRearAxe  = texelFetch(PhysTex,ivec2(8,0),0).xyz;
    d=min(d,sdLine(p,carTrafoInv((WheelPosFL+WheelPosFR)*.3,1.),carTrafoInv((WheelPosFL+WheelPosFR)*.3,1.)+accFrontAxe)-.1);
    d=min(d,sdLine(p,carTrafoInv((WheelPosRL+WheelPosRR)*.4,1.),carTrafoInv((WheelPosRL+WheelPosRR)*.4,1.)+accRearAxe)-.1);
    SET_PREV_MAT(DBG_GREEN);
    #endif

    return vec2(d,mat);
}

float dist(vec3 p) { return distM(p).x; }

vec3 getGradOld(vec3 p,float delta)
{
    float v=dist(p);
    vec2 d=vec2(delta,0); return vec3( dist(p+d.xyy)-v,
                                       dist(p+d.yxy)-v,
                                       dist(p+d.yyx)-v )/delta;
}

/// my own version of a looped getGrad() (is asymmetric)
vec3 getGrad2(vec3 p,float delta)
{
    vec4 d=vec4(0,0,0,1); 
    vec3 s=vec3(0);
    // use a loop here keep compiler from inlining this in win (thanks iq for the hint!)
    for(int i=min(0,iFrame);i<4;i++)
    {
      	s+=(d.xyz-d.w)*dist(p+d.xyz*delta);
        d=d.wxyz;
    }
    return s/delta;
}

// cheap tetrahedral gradient (is symmetric)
vec3 getGrad(vec3 p,float delta)
{
    vec3 g=vec3(0);
    for(int i=min(0,iFrame);i<4;i++) { vec3 v=vec3((i&1)*2-1,(i/2)*2-1,1); vec3 d=v.xxz*v.zyy;  g+=d*dist(p+d*delta*.289); }
    return g/(delta*1.732);
}

float march(inout vec3 p, vec3 dir)
{
    //if(!intersectBox(p-bbpos,dir,bbsize)) { enable_car=false; }
    vec3 pc=carTrafo(p);
    vec3 pdir=carTrafo(dir,0.);
    //enable_car=true;
    if(!intersectBox(pc-bbpos,pdir,bbsize)) { enable_car=false; }
    if(terrbbsize!=vec3(0) && (!intersectBox(p-terrbbpos,dir,terrbbsize))) { enable_terr=false; }
    //if(!(intersectBox(pc-bbpos1,pdir,bbsize1)||intersectBox(pc-bbpos2,pdir,bbsize2))) { enable_car=false; }
    vec3 p0=p;
    float eps=.001;
    float dmin=100000.;
    bool findmin=false;
    float d=dist(p);
    vec3 pmin=p;
    for(int i=min(0,iFrame);i<150;i++)  // min(0,iFrame) avoids unrolling of loop - thx Dave_Hoskins
    {
        float dp=d;
        d=dist(p);
        p+=dir*d*.7;
#ifdef SHADOW
        if (d<dp) findmin=true;
        if (findmin && d<dmin) { dmin=d; pmin=p; }
#endif
        if (d<eps) return 0.;
#ifndef SHADOW
        if (dmin<50. && d>50.) break;
#else
        if (dmin<150. && d>150.) break;
#endif
        //if (length(p)>100.) break;
    }
    float lmin=length(pmin-p0);
    return clamp(dmin/lmin/.05,0.,1.);
}

float wstep(float w, float thr, float x)
{
    return smoothstep(thr-w*.5,thr+w*.5,x);
}

float getAO(vec3 pos, vec3 n)
{    
    float ao=1.;
    float sc=.025;
    float amb=.3;
    // use loop here to keep compiler from inlining this in win (thanks iq for the hint!)
    for( int i=min(iFrame,0); i<5; i++ )
    {
    	ao*=mix(dist(pos+n*sc)/sc*1.4,1.,amb);
    	ao=clamp(ao,0.,1.);
        sc*=2.;
        amb=min(amb+.1,.5);
    }
    return ao;
   	/*
    ao*=dist(pos+n*.02)/.02*1.4*.7+.3;
   	ao=clamp(ao,0.,1.);
    ao*=dist(pos+n*.05)/.05*1.4*.6+.4;
    ao=clamp(ao,0.,1.);
    ao*=dist(pos+n*.1)/.1*1.4*.5+.5;
    ao=clamp(ao,0.,1.);
    ao*=dist(pos+n*.2)/.2*1.4*.5+.5;
    ao=clamp(ao,0.,1.);
    ao*=dist(pos+n*.4)/.4*1.4*.5+.5;
    ao=clamp(ao,0.,1.);*/
}

vec3 lighting(vec3 pos, vec3 dir, vec3 pos0, float reflections, inout float outfres, inout float outao)
{
    vec3 pc=carTrafo(pos);
    vec3 glasspos=vec3(1000.);
    vec3 glassn=vec3(0.);
    float mat=distM(pos).y;
    if(mat==GLASS)
    {
        glasspos=pos;
        glassn=normalize(getGrad(pos,.001));
        enable_glass=false;
        march(pos,dir);
    }
    mat=distM(pos).y;

    vec3 light=getLightDir();
    float sh=1.;
#ifdef SHADOW
    vec3 posS=pos+.017*light;
    enable_car=true;
    sh=march(posS,light);
#endif
    enable_car=true;
    vec3 n=getGrad(pos,.001);
    if (length(n)<.001) n=vec3(0,0,1);
    n=normalize(n);
    if(mat==BG) n=vec3(0,0,1);

    float ao=1.;
    ao=getAO(pos,n);
    //ao=pow(ao,.5);
    ao=ao*.7+.3;
    //ao=clamp(ao*2.-1.,0.,1.);

    //vec3 light = normalize(vec3(-.5,.5,2));
    float diff=clamp(dot(n,light),0.,1.);

    diff=min(diff,sh);

    // no ao in lighted areas
    ao=mix(ao,1.,diff);
    ao=clamp(ao,0.,1.);
    //return vec3((diff*.6+.4)*(ao));


    Material mtrl=getMaterial(mat);

    // evironmental reflection
    n=normalize(n);
    if(mat==CARBODY) // brushed metal surface
    {
        vec4 r1=rand3d(pc*.1);
        vec4 r2=rand3d(pc*.05);
        // scramble normals randomly
        //n+=clamp((r1.xyz-.5)*5.,0.,1.)*.015 + clamp((r2.xyz-.5)*5.,0.,1.)*.015;
        // darken randomly
        //n*=clamp(r1.w*3.-1.,0.,1.)*.05 + clamp(r2.w*3.-1.,0.,1.)*.07 + .88;
    }
    vec3 R=reflect(dir,n);
    vec3 refl=myenv(pos,R.xyz,1.).xyz;
    //refl=refl*1.2+.3;
    float fres=abs(dot(R,n));
    fres=1.-fres;
    fres*=fres*fres;
    fres=fres*.9+.1;
    float fres0=fres;
    #ifdef SCRATCHES
    vec3 n0=n;
    int numscr=7;
    float dang=1.57*2./float(numscr);
    float ang=.5;
    refl*=1.;
    vec3 drefl=vec3(0);
    float sum=0.;
    vec3 pi=pc;
    vec3 heading=vec3(0,0,1);
    vec3 tan1=vec3(1,0,0);
    vec3 tan2=vec3(0,1,0);
    // ...trying to implement some micro scratches
    for(int i=0;i<numscr*3;i++)
    {
        n=n0;
        vec2 cs=cos(ang+vec2(0,-1.57));
        mat2 m=mat2(cs,cs.yx*vec2(-1,1));
        //dFdx()
        vec2 dn2d=(texture(iChannel0,(m*pi.xy)*vec2(6.,.1)*mtrl.scratchScale+vec2(0,.5/256.)).x-.5)*mtrl.scratchScale;
        dn2d=pow(abs(dn2d),vec2(.7))*sign(dn2d);
        dn2d=dn2d*m;
        //dn2d=dn2d.yx*vec2(1,-1);
        n+=abs(dot(n0,heading))*carTrafoInv(dn2d.x*tan1+dn2d.y*tan2,0.);
        n=normalize(n);
        R=reflect(dir,n);
        float fres=abs(dot(R,n));
        fres=1.-fres;
        fres*=fres*fres;
        fres=fres*.4+.6;
        float fact=(abs(dot(n0,-dir))*.8+.2)*fres;
        //fact=.2;
        drefl+=fact*myenv(pos,R.xyz,1.).xyz;
        sum+=fact;
        ang+=dang;
        pi=pi.zxy;
        heading=heading.yzx;
        tan1=tan1.yzx;
        tan2=tan2.yzx;
    }
    drefl/=float(numscr);
    refl=mix(refl,drefl,mtrl.scratchy);
    #endif

    #ifdef RENDER_GLASS
    vec3 Rg=reflect(dir,glassn);
    vec3 glassrefl=myenv(pos,Rg.xyz,1.).xyz;
    //glassrefl=glassrefl*1.2+.3;
    float glassfres=abs(dot(Rg,glassn));
    glassfres=1.-glassfres;
    glassfres*=glassfres*glassfres;
    glassfres=glassfres*.7+.3;
    if(glassn==vec3(0)) glassfres=0.;
    #endif

    vec3 rcol=vec3(1);
    fres=(mtrl.refl<0.)?fres*-mtrl.refl:mtrl.refl;
    rcol=(mtrl.refl<0.)?vec3(1):mtrl.col;
    vec3 col=mtrl.col;
    if(mat==FLOOR) {
        col+=(texture(iChannel0,pos.xy*2.,-1.2).x-.5)*.3;
        //col*=.9+.2*texture(iChannel2,pos.xy*.2).xyz;
        #ifdef WET_ASPHALT
        col*=.35+.65*step(-.66,pos.z);
        fres=fres0*mix(-mtrl.refl,1.,(1.-smoothstep(-.661,-.659,pos.z))*exp(-length(pos.xy)/50.));
        #endif
    }

    float zr=length(pos-pos0)/300.;
    //diff=sqrt(diff);

    outfres=fres;
    if(glassfres!=0.) outfres=glassfres;
    fres*=reflections;
    glassfres*=reflections;

    outao=ao;

	vec3 bg=myenv(pos0,dir,1.).xyz;
	// diff, ao, refl
	vec3 finalcol = mix(col,rcol*refl,fres)*mix(vec3(1.2,1.4,1.5)*.35,vec3(1.,1,.9),diff)*ao*1.5;
	//finalcol=col;
	// fog
	//finalcol = mix(finalcol,bg,1.-exp(-zr));
	finalcol = mix(finalcol,vec3(.35),1.-clamp(exp(-(zr-.1)/2.5),0.,1.));
	#ifdef RENDER_GLASS
	finalcol=mix(finalcol*sqrt(1.-glassfres),glassrefl,glassfres);
	#endif
	return finalcol;
}

