void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 sc=(fragCoord-Res*.5)/Res.x;

    float zoom=1.5+(500.*exp2(-iTime));
    #ifdef SHADEROO
    zoom=exp(-iMouseData.z/5000.);
    #endif
    zoom*=CamDistFact;

    vec3 CarPos = carTrafoInv(vec3(0),1.);
    vec3 pos=vec3(0,0,4.5)*zoom;
    vec3 dir=normalize(vec3(sc,-.8));

    calcWheelOffsets(-.1,.2);

    vec4 q=vec4(0,0,0,1);
    float th=-(iMouse.y-Res.y*.5)/Res.y*6.;
    float ph=-(iMouse.x-Res.x*.5)/Res.x*10.;
    if(iMouse.x<1.) { th=1.45; ph=-iTime*.25; }
    //th=clamp(th,-1.65,1.65);
    q=multQuat(q,axAng2Quat(vec3(0,0,1),ph));
    q=multQuat(q,axAng2Quat(vec3(1,0,0),th));
    pos=transformVecByQuat(pos,q)-vec3(0,0,.2);
    dir=transformVecByQuat(dir,q);
    pos=camTrafoInv(pos,1.);
    // get camera out of terrain
    for(int i=0; i<50 ; i++) {  float d=distTerr(pos); if (d>.1) break; pos+=normalize(getGradTerr(pos,.01))*max(d,.1); }
    // make sure cam is always above ground
    //pos.z=max(hTerr(pos)-.6,pos.z);
    dir=camTrafoInv(dir,0.);

    #ifdef CAM_ANIM
    if(iMouse.x<1.)
    {
        pos=getCamAnimPos(iTime);
        dir=normalize(getCamAnimDir(iTime));
        vec3 right=normalize(cross(dir,vec3(0,0,1)));
        vec3 up=cross(right,dir);
        dir=normalize(dir+right*sc.x+up*sc.y);
    }
    #endif

    vec3 pos0=pos;
    float m=march(pos,dir);

    float refl=1.;
    #ifdef RENDERED_REFECTIONS
    refl=0.;
    #endif

    float fres=0., ao=0.;
    #ifdef ENABLE_MATERIALS
        fragColor.xyz=lighting(pos,dir,pos0,refl,fres,ao);
    #else
        vec3 g=getGrad(pos,.001);
        vec3 n=g/(length(g)+.00001);
        enable_car=true;
        ao=.5*clamp(dist(pos+n*.5)/.5,0.,1.)+.5*clamp(dist(pos+n*.25)/.25,0.,1.);
        //ao=clamp(dist(pos+n*.5)/.5,0.,1.)*clamp(dist(pos+n*.25)/.25,0.,1.);
        vec3 rdir=reflect(dir,n);
        //pos+=n*.001;
        //float sh=march(pos,reflect(dir,n));
        fres=1.-abs(dot(rdir,n));
        fres*=fres*fres;
        fres=fres*.7+.3;
        vec3 r=myenv(vec3(0),rdir,1.).xyz*.5;
        //fragColor.xyz=mix((.3*n+.6)*(ao*.7+.3),r,fres);
        //fragColor.xyz=vec3(1,.85,.6)*(dot(n,normalize(vec3(1,-1,1)))*.4+.1*n+.5)*(ao*.7+.3)+r*fres;
        fragColor.xyz=mix(
                        vec3(1.,.6,.4)*(1.+r*.6),
                        vec3(.2,.4,.3)*.6,
                        1.-(ao)*.7
                        )*(dot(n,normalize(vec3(1,-1,1)))*.3+.1*n+.7)*1.;
        //fragColor.xyz=mix(fragColor.xyz,vec3(.8,.9,1),.25*exp(-m));
        //*(ao*.7+vec3(.1,.4,.2))*(.5+r*2.);
    #endif

    #ifdef RENDERED_REFECTIONS
    enable_glass=true;
    vec3 n=getGrad(pos,.001);
    if (length(n)<.001) n=vec3(0,0,1);
    n=normalize(n);
    fragColor.xyz=n*.5+.5;
    dir=reflect(dir,n);
    pos+=dir*.003;
    float mat=distM(pos).y;
    march(pos,dir);
    float dummyfres,dummyao;
    vec3 lcol=lighting(pos,dir,pos0,1.,dummyfres,dummyao);
    fragColor.xyz=mix(fragColor.xyz,lcol,(m!=0.)?0.:fres);

    #ifdef CAM_ANIM
    fragColor*=1.-exp(-getCamAnimBr(iTime)*getCamAnimBr(iTime)/.01);
    #endif
    //fragColor=myRandInterpol(fragCoord/iResolution.xy*.1);
    #endif

    fragColor.xyz=mix(myenv(vec3(0),dir,1.).xyz,fragColor.xyz,exp2(-abs(dist(pos))/10.));

	fragColor.w=1.;
}

