// created by florian berger (flockaroo) - 2020
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// physics driving sim

//#ShaderooNumPasses 1
#define NumPasses 1

#define RandTex iChannel2
#define PhysTex iChannel0
#ifdef SHADEROO
#include Include_A.glsl
#endif
vec4 myRandInterpol(vec2 uv)
{
    //#define COARSE_TERRAIN
    #ifdef COARSE_TERRAIN
    return textureLod(RandTex,uv,0.);
    #endif
    uv=fract(uv);
    ivec2 c0 = ivec2(fract(uv        )*256.);
    ivec2 c1 = ivec2(fract(uv+1./256.)*256.);
    vec2  f = fract(uv*256.);
    vec4 c00=texelFetch(RandTex,ivec2(c0.x,c0.y),0);
    vec4 c10=texelFetch(RandTex,ivec2(c1.x,c0.y),0);
    vec4 c01=texelFetch(RandTex,ivec2(c0.x,c1.y),0);
    vec4 c11=texelFetch(RandTex,ivec2(c1.x,c1.y),0);
    //f=mix(f,.5-.5*cos(f*PI2*.5),.5);
    f=.5-.5*cos(f*PI2*.5);
    return mix(mix(c00,c10,f.x),mix(c01,c11,f.x),f.y);
}

const vec3 terrbbpos=vec3(0,.0,.0);
#ifdef CHAIN_WORLD
const vec3 terrbbsize=vec3(260.,260.,60.)*2.+50.;
#else
const vec3 terrbbsize=vec3(0);
#endif

float distTerr(vec3 p)
{
    vec3 p2=p*.0001;
    vec4 noise = vec4(0);
    float sc;
    sc=1.; 
    for(int i=0;i<3;i++)
    {
        noise += (myRandInterpol(p2.xy*sc)-.5)/sc;
        noise += (myRandInterpol(p2.yz*sc)-.5)/sc;
        noise += (myRandInterpol(p2.zx*sc)-.5)/sc;
        sc*=2.;
    }
    noise += abs(myRandInterpol(p2.xy*sc*5.)-.5)/sc/5.;
    noise += abs(myRandInterpol(p2.yz*sc*5.)-.5)/sc/5.;
    noise += abs(myRandInterpol(p2.zx*sc*5.)-.5)/sc/5.;

    #if   defined(CHAIN_WORLD)
    return distChainCircle(p+noise.xyz*10.,200., 6, 60.,20.,20.);
    #elif defined(TORUS2_WORLD)
    return min(distTorus(p+noise.xyz*10.,100.,40.),distTorus((p-vec3(100,0,0)).zxy+noise.xyz*10.,100.,40.));
    #elif defined(TORUS_WORLD)
    return distTorus(p+noise.xyz*10.,100.,40.);
    #elif defined(HOLLOW_SPHERE_WORLD)
    return -length(p+noise.xyz*10.)+100.;
    #elif defined(SPHERE_WORLD)
    return length(p+noise.xyz*10.)-60.;
    #elif defined(PLANE_WORLD)
    return dot(p+noise.xyz*10.,normalize(vec3(1,.5,.7)));
    #elif defined(HOLLOW_TORUS)
    return -distTorus(p+noise.xyz*10.,100.,40.);
    #else
    return p.z+noise.x;
    #endif
}

/// my own version of a looped getGrad()
vec3 getGradTerr4loop(vec3 p,float delta)
{
    vec4 d=vec4(0,0,0,1); 
    vec3 s=vec3(0);
    // use a loop here keep compiler from inlining this in win (thanks iq for the hint!)
    for(int i=min(0,iFrame);i<4;i++)
    {
      	s+=(d.xyz-d.w)*distTerr(p+d.xyz*delta);
        d=d.wxyz;
    }
    return s/delta;
}

// straight fwd gradient - windows dont like this... wtf...
vec3 getGradTerr0(vec3 p,float delta)
{
    vec3 d=vec3(delta*.5,0,0);
    return vec3(
        distTerr(p+d.xyz)-distTerr(p-d.xyz),
        distTerr(p+d.zxy)-distTerr(p-d.zxy),
        distTerr(p+d.yzx)-distTerr(p-d.yzx)
        )/delta;
}

// cheap tetrahedral gradient
vec3 getGradTerrTetr(vec3 p,float delta)
{
    vec3 g=vec3(0);
    for(int i=min(0,iFrame);i<4;i++) { vec3 v=vec3((i&1)*2-1,(i/2)*2-1,1); vec3 d=v.xxz*v.zyy;  g+=d*distTerr(p+d*delta*.289); }
    return g/(delta*1.732);
}

// full straight fwd grad as loop
vec3 getGradTerr6loop(vec3 p,float delta)
{
    vec3 g=vec3(0);
    vec3 d=vec3(delta*.5,0,0);
    for(int i=min(0,iFrame);i<6;i++) { g+=d*distTerr(p+d); d=-d.zxy; }
    return g/(.5*delta*delta);
}

vec3 getGradTerr(vec3 p,float delta)
{
    return getGradTerr6loop(p,delta);
}

// transform to car coords
vec3 carTrafo(vec3 p, float translate)
{
#ifdef USE_SIMDATA
    vec4 q=texelFetch(PhysTex,ivec2(3,0),0);
    vec3 pos=texelFetch(PhysTex,ivec2(0,0),0).xyz;
    ///offs.z=-hTerr(-offs);
    //q=multQuat(getTiltQuat(-offs),q);
    return transformVecByQuat(p-translate*pos,inverseQuat(q));
#else
    return p;
#endif
}

vec3 carTrafo(vec3 p)
{
    return carTrafo(p,1.0);
}

// transform to world coords
vec3 carTrafoInv(vec3 p, float translate)
{
#ifdef USE_SIMDATA
    vec4 q=texelFetch(PhysTex,ivec2(3,0),0);
    vec3 pos=texelFetch(PhysTex,ivec2(0,0),0).xyz;
    //offs.z=-hTerr(-offs);
    //q=multQuat(getTiltQuat(-offs),q);
    return transformVecByQuat(p,q)+pos*translate;
#else
    return p;
#endif
}

vec3 camTrafoInv(vec3 p, float translate)
{
#ifdef USE_SIMDATA
    vec4 q=texelFetch(PhysTex,ivec2(6,0),0);
    vec3 pos=texelFetch(PhysTex,ivec2(0,0),0).xyz;
    //offs.z=-hTerr(-offs);
    //q=multQuat(getTiltQuat(-offs),q);
    return transformVecByQuat(p,q)+pos*translate;
#else
    return p;
#endif
}

void calcWheelOffsets(float clmin, float clmax)
{
    vec3 wp;
    wp=carTrafoInv(WheelPosFL*vec3(1,1,1)+vec3(0,0,-.3),1.); WheelOffsFL = clamp(-distTerr(wp),clmin,clmax);
    wp=carTrafoInv(WheelPosFR*vec3(1,1,1)+vec3(0,0,-.3),1.); WheelOffsFR = clamp(-distTerr(wp),clmin,clmax);
    wp=carTrafoInv(WheelPosRL*vec3(1,1,1)+vec3(0,0,-.3),1.); WheelOffsRL = clamp(-distTerr(wp),clmin,clmax);
    wp=carTrafoInv(WheelPosRR*vec3(1,1,1)+vec3(0,0,-.3),1.); WheelOffsRR = clamp(-distTerr(wp),clmin,clmax);
    //hTerr(wp)-wp.z
}

#define keyTex iChannel1
#define KEY_I (texture(keyTex,vec2((105.5-32.0)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_A (texture(keyTex,vec2((65.5+ 0.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_W (texture(keyTex,vec2((65.5+22.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_R (texture(keyTex,vec2((65.5+17.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_S (texture(keyTex,vec2((65.5+18.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_D (texture(keyTex,vec2((65.5+ 3.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_F (texture(keyTex,vec2((65.5+ 5.)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_PLUS  (texture(keyTex,vec2((187.5)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_MINUS (texture(keyTex,vec2((189.5)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_LEFT  (texture(keyTex,vec2(( 37.5)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_RIGHT (texture(keyTex,vec2(( 39.5)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_DOWN  (texture(keyTex,vec2(( 40.5)/256.0,(0.5+0.0)/3.0)).x)
#define KEY_UP    (texture(keyTex,vec2(( 38.5)/256.0,(0.5+0.0)/3.0)).x)

#define CalcTorque(a,r) cross((r),transformVecByQuat((a),iquat)*Mass)/I

float mypow(float a, float b) { return exp2(log2(a)*b); }

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    #ifdef ZZT_AS_TEX
    vec2 uv = fragCoord.xy / Res0;
    fragColor = zztop((uv*2.-.75)*11.,-1.);
    #endif

    if (fragCoord.x>10.) discard;
    if (fragCoord.y>1.) discard;

    vec3  pos      = texelFetch(PhysTex,ivec2(0,0),0).xyz;
    vec3  vel      = texelFetch(PhysTex,ivec2(1,0),0).xyz;
    vec3  omega    = texelFetch(PhysTex,ivec2(2,0),0).xyz;
    vec4  quat     = texelFetch(PhysTex,ivec2(3,0),0);
    float SteerAng = texelFetch(PhysTex,ivec2(4,0),0).x;
    float camDist  = texelFetch(PhysTex,ivec2(4,0),0).y;
    vec4  wheelRot = texelFetch(PhysTex,ivec2(5,0),0);
    vec4  camQuat  = texelFetch(PhysTex,ivec2(6,0),0);
    vec4  iquat = inverseQuat(quat);

    calcWheelOffsets(-1.2,1.2);

    float dt=min(max(iTimeDelta/float(NumPasses),.0001),.03);

    float axDist=3.5;
    vec3 rearAxPos = vec3(0,1.7,0);

    //quat=vec4(0,0,0,1);
    vec3 bx=transformVecByQuat(vec3(1,0,0),quat);
    vec3 by=transformVecByQuat(vec3(0,1,0),quat);
    vec3 bz=transformVecByQuat(vec3(0,0,1),quat);

    vec3 acc=vec3(0);
    vec3 alpha=vec3(0);

    float Mass=1000.;
    vec3 S=vec3(2.,3.2,1.5);
    vec3 I=vec3(S.y*S.y+S.z*S.z, S.z*S.z+S.x*S.x, S.x*S.x+S.y*S.y)*Mass/12.;
    float mu = .7;
    #define GRAVITY 9.81
    //acc+=GRAVITY*vec3(0,0,-1);
    acc+=GRAVITY*-normalize(getGradTerr(pos,10.));
    float SpringOmega=10.;
    float SpringGamma=20.;

    vec3 n=normalize(getGradTerr(pos,2.));

    //float dTerr = (pos.z-hTerr(pos))*n.z;
    float dTerr1=distTerr(pos+by*1.1-bz*.8);
    float dTerr2=distTerr(pos-by*1.1-bz*.8);
    float dTerr = (dTerr1+dTerr2)*.5;

    float grip=step(0.,.1-dTerr);
    //float grip=max(step(0.,.1-dTerr1),step(0.,.1-dTerr2));

    // FIXME: just one central springforce for now
    //float dTerr=(pos.z-hTerr(pos))*n.z;
    acc += grip*(0.*GRAVITY+SpringOmega*SpringOmega*(.1-dTerr))*n;

    // FIXME: just angular spring for now... (use wheel forces instead)
    alpha -= 150.*grip*cross(transformVecByQuat(n,iquat)*Mass,normalize(vec3(0,.0,1)))/I;

    vec3 dacc=vec3(0);
    vec3 dalpha=vec3(0);

    dacc-=30.*grip*(max(KEY_W,KEY_UP)-max(KEY_S,KEY_DOWN))*by;
    acc+=dacc;

    // FIXME: get rid of tweak-constant 0.15
    alpha-=.2*grip*cross(transformVecByQuat(dacc,iquat)*Mass,.5*(WheelPosRL+WheelPosRR))/I;

    SteerAng*=.91;
    SteerAng-=(max(KEY_A,KEY_LEFT)-max(KEY_D,KEY_RIGHT))*.05;
    camDist*=(1.-.02*KEY_PLUS);
    camDist*=(1.+.02*KEY_MINUS);

    //quat=multQuat(quat,axAng2Quat(vec3(0,0,1),+.001*SteerAng*dot(vel,by)));

    vec3 accFrontAxe;
    vec3 accRearAxe;

    // friction on front/back tire (one virtual middle tire)
    for(int i=0;i<2;i++)
    {
        vec3 fricDir=vec3(cos(SteerAng),-sin(SteerAng),0);
        vec3 fricPos=.5*(WheelPosFL+WheelPosFR);
        if (i==1)
        {
            fricDir=vec3(1,0,0);
            fricPos=.5*(WheelPosRL+WheelPosRR);
        }
        // FIXME: this should be +omega ?!
        vec3 dv = cross(omega,fricPos) + transformVecByQuat(vel,iquat); // speed of wheelpos (by car rotation) + car speed
        dv = clamp(dot(dv,fricDir)*1.,-4.,4.)*fricDir;  // just component normal to wheel
        dacc   = -transformVecByQuat(dv,quat)*mu*GRAVITY*grip*.5;
        dalpha = CalcTorque(dacc,fricPos);
        if(i==0) accFrontAxe=dacc;
        if(i==1) accRearAxe=dacc;
        acc+=dacc;
        alpha+=dalpha;
    }

    // very unphysical mix - so careful with timedelta (or NumPasses)!
    //camQuat=normalize(mix(quat,camQuat,.95));
    camQuat=normalize(mix(quat,camQuat,mypow(.95,dt/.0166)));

    if(dTerr<0.) vel-=dot(vel,n)*n*.5;
    //pos.z=.5+hTerr(pos);
    //quat=multQuat(getTiltQuat(pos),quat);
    acc-=.03*vel*length(vel);
    alpha-=grip*2.*omega*length(omega);

    vel+=acc*dt*.5;
    pos+=vel*dt;
    vel+=acc*dt*.5;
#if 1
    omega+=alpha*dt*.5;
    //if (length(omega)>.1) omega=normalize(omega)*.1;
    if (length(omega)>.001)
        quat=multQuat(quat,angVec2Quat(omega*dt));
    omega+=alpha*dt*.5;
#endif

    //if(pos.z<hTerr(pos)) 
    vec3 omegaN=dot(omega,n)*n;
    omega=mix(omegaN,omega,mypow(mix(.98,.85,grip),dt/.0166));
    omega*=mypow(.99,dt/.0166);
    //omega=mix(omegaN,omega,mix(.98,.85,grip));
    //omega*=.99;
    //vel*=.99;
    if(length(vel)>.001) vel-=normalize(vel)*.005*dt/.0166;
    //if(length(vel)<.1) vel=vec3(0);
    //if(length(omega)<.01) omega=vec3(0);

    float wheelRadius=(WheelRadiusF+WheelRadiusR)*.5*.5;
    wheelRot-=-dot(vel,by)*dt/wheelRadius;

    if(iFrame==0)
    {
        pos=vec3(10,300,150);
        pos-=normalize(getGradTerr(pos,.5))*distTerr(pos);
        pos-=normalize(getGradTerr(pos,.5))*distTerr(pos);
        pos-=normalize(getGradTerr(pos,.5))*distTerr(pos);
        pos-=normalize(getGradTerr(pos,.5))*distTerr(pos);
        pos+=normalize(getGradTerr(pos,.5))*3.;
        //pos.z=hTerr(pos)+1.;
        vel=vec3(0,0,0);
        quat=normalize(vec4(0.03,0.02,0,1));
        camQuat=quat;
        omega=vec3(0);
        SteerAng=0.;
        wheelRot=vec4(0);
        camDist=1.;
    }

    if (ivec2(fragCoord)==ivec2(0,0)) fragColor = vec4(pos,1.0);
    if (ivec2(fragCoord)==ivec2(1,0)) fragColor = vec4(vel,1.0);
    if (ivec2(fragCoord)==ivec2(2,0)) fragColor = vec4(omega,1.0);
    if (ivec2(fragCoord)==ivec2(3,0)) fragColor = vec4(quat);
    if (ivec2(fragCoord)==ivec2(4,0)) fragColor = vec4(SteerAng,camDist,0,1);
    if (ivec2(fragCoord)==ivec2(5,0)) fragColor = vec4(wheelRot);
    if (ivec2(fragCoord)==ivec2(6,0)) fragColor = vec4(camQuat);
    if (ivec2(fragCoord)==ivec2(7,0)) fragColor = vec4(accFrontAxe,0);
    if (ivec2(fragCoord)==ivec2(8,0)) fragColor = vec4(accRearAxe,0);
}

